<head>
    <title>Gosper Tiling</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <style>
        #demo-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            display: flex;
            gap: 20px;
            margin: 20px;
            padding: 10px;
            background: #f0f0f0;
            border-radius: 5px;
        }
        #control-panel div {
            display: flex;
            align-items: center;
            gap: 5px;
        }
        #input-steps {
            width: 60px;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <div>
                <label for="input-steps">steps</label>
                <input type="number" id="input-steps" min="0" max="6" value="2" step="1">
            </div>
            <div>
                <label for="checkbox-zoom">zoom</label>
                <input type="checkbox" id="checkbox-zoom">
            </div>
            <div>
                <label for="select-color">color</label>
                <select id="select-color">
                    <option value="DarkRainbow">DarkRainbow</option>
                    <option value="SiennaTones">SiennaTones</option>
                    <option value="RustTones">RustTones</option>
                    <option value="FuchsiaTones">FuchsiaTones</option>
                    <option value="Grays">Grays</option>
                </select>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        const colorPalettes = {
            DarkRainbow: ['#1E4363', '#2E6341'],
            SiennaTones: ['#A0522D', '#CD853F', '#D2B48C'],
            RustTones: ['#B7410E', '#000033', '#FFFFFF'],
            FuchsiaTones: ['#C71585', '#000000', '#FFFFFF'],
            Grays: ['#404040', '#808080', '#C0C0C0']
        };

        let currentPalette;
        const SQRT7 = Math.sqrt(7);
        const SUBDIVISION_ANGLE = Math.atan(Math.sqrt(3)/5);

        function setup() {
            const canvas = createCanvas(800, 800);
            canvas.parent('canvas-container');
            angleMode(DEGREES);
            noLoop();

            document.getElementById('input-steps').addEventListener('change', redraw);
            document.getElementById('checkbox-zoom').addEventListener('change', redraw);
            document.getElementById('select-color').addEventListener('change', redraw);

            redraw();
        }

        function drawRhombus(x, y, size, angle, colorIndex) {
            const halfSize = size / 2;
            const shortDiag = size * Math.sin(60);

            push();
            translate(x, y);
            rotate(angle);

            fill(currentPalette[colorIndex % currentPalette.length]);
            noStroke();

            beginShape();
            vertex(-halfSize, 0);
            vertex(0, -shortDiag/2);
            vertex(halfSize, 0);
            vertex(0, shortDiag/2);
            endShape(CLOSE);

            // Inner diagonal line
            stroke(255, 50);
            line(-halfSize/2, -shortDiag/4, halfSize/2, shortDiag/4);

            pop();
        }

        function drawTiling(level, x, y, size, angle, colorIndex) {
            if (level === 0) {
                drawRhombus(x, y, size, angle, colorIndex);
                return;
            }

            const newSize = size / SQRT7;
            const positions = [
                {x: 0, y: 0, angle: angle},
                {x: size * Math.cos(angle + SUBDIVISION_ANGLE), 
                 y: size * Math.sin(angle + SUBDIVISION_ANGLE), 
                 angle: angle + 120},
                // ... add other 5 positions
            ];

            positions.forEach((pos, i) => {
                drawTiling(level - 1, 
                          x + pos.x, 
                          y + pos.y, 
                          newSize, 
                          pos.angle, 
                          colorIndex + i);
            });
        }

        function draw() {
            background(255);
            currentPalette = colorPalettes[document.getElementById('select-color').value];
            const steps = parseInt(document.getElementById('input-steps').value);
            const isZoomed = document.getElementById('checkbox-zoom').checked;

            translate(width/2, height/2);

            if (isZoomed) {
                scale(Math.pow(SQRT7, steps) / 4);
            } else {
                scale(200 / Math.pow(SQRT7, steps));
            }

            // Draw initial hexagon configuration
            for (let i = 0; i < 6; i++) {
                const angle = i * 60;
                drawTiling(steps, 
                          100 * Math.cos(radians(angle)), 
                          100 * Math.sin(radians(angle)), 
                          100, 
                          angle, 
                          i);
            }

            // Special central hexagon for certain color schemes
            if (steps > 0 && 
                (currentPalette === colorPalettes.RustTones || 
                 currentPalette === colorPalettes.FuchsiaTones)) {
                fill(currentPalette[2]);
                beginShape();
                for (let i = 0; i < 6; i++) {
                    const angle = i * 60;
                    vertex(50 * Math.cos(radians(angle)), 
                          50 * Math.sin(radians(angle)));
                }
                endShape(CLOSE);
            }
        }
    </script>
</body>
</html>
