<!DOCTYPE html>
<html>
<head>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <style>
        #main-container {
            display: flex;
            border: 1px solid #333;
            padding: 20px;
            max-width: 900px;
            margin: 20px auto;
        }
        #control-panel {
            width: 300px;
            padding-right: 20px;
        }
        #control-panel div {
            margin-bottom: 10px;
        }
        #visualization-area {
            flex: 1;
        }
        label {
            display: inline-block;
            margin-right: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-initialization">initialization</label>
                <input type="range" id="slider-initialization" min="0" max="65535" step="1" value="462">
                <span id="value-initialization">+ 462</span>
            </div>

            <div>
                <label for="slider-max-steps">max steps</label>
                <input type="range" id="slider-max-steps" min="1" max="50" step="1" value="9">
                <span id="value-max-steps">+ 9</span>
            </div>

            <div>
                <input type="checkbox" id="checkbox-cycle4">
                <label for="checkbox-cycle4">length 4 cycle</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-cycle2">
                <label for="checkbox-cycle2">length 2 cycles</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-cycle1">
                <label for="checkbox-cycle1">length 1 cycle points</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-all-points">
                <label for="checkbox-all-points">all map points</label>
            </div>
            <div>
                <input type="checkbox" id="checkbox-modify">
                <label for="checkbox-modify">modify initialization</label>
            </div>
        </div>

        <div id="visualization-area">
            <p id="info-text">result of initial condition 462 does not repeat in 10 steps</p>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        let currentState = {
            initialization: 462,
            maxSteps: 9,
            showCycle4: false,
            showCycle2: false,
            showCycle1: false,
            showAllPoints: false,
            modifyInit: false
        };

        function bitReverse15(n) {
            let rev = 0;
            for(let i = 0; i < 15; i++) {
                rev = (rev << 1) | (n & 1);
                n >>= 1;
            }
            return rev;
        }

        function f(n) {
            return (n + bitReverse15(Math.floor(n / 2))) % 65536;
        }

        function setup() {
            const canvas = createCanvas(512, 512);
            canvas.parent('canvas-container');
            noLoop();
            redraw();
        }

        function generateSequence(start, steps) {
            let seq = [start];
            let current = start;
            for(let i = 0; i <= steps; i++) {
                current = f(current);
                seq.push(current);
            }
            return seq;
        }

        function draw() {
            background('#e0f0e0');

            if(currentState.showAllPoints) {
                stroke(128);
                strokeWeight(1);
                for(let n = 0; n < 65536; n++) {
                    let next = f(n);
                    let x = (next & 255) * width/256;
                    let y = ((next >> 8) & 255) * height/256;
                    point(x, y);
                }
            }

            if(currentState.showCycle4) {
                const cycle4 = [43690, 54612, 32768, 49152];
                stroke('#008000');
                noFill();
                beginShape();
                for(let n of cycle4) {
                    let x = (n & 255) * width/256;
                    let y = ((n >> 8) & 255) * height/256;
                    vertex(x, y);
                }
                endShape(CLOSE);

                stroke('#FFA500');
                noFill();
                // Draw bounding box and perpendicular bisectors here
            }

            if(currentState.showCycle2) {
                const cycle2Pairs = [[10922, 21845], [13107, 26214], [13653, 27306], 
                    [15339, 30678], [21844, 43691], [26213, 52427], [27305, 54611], 
                    [30677, 61355], [43690, 54613]];

                stroke('#008000');
                strokeWeight(1);
                for(let pair of cycle2Pairs) {
                    let x1 = (pair[0] & 255) * width/256;
                    let y1 = ((pair[0] >> 8) & 255) * height/256;
                    let x2 = (pair[1] & 255) * width/256;
                    let y2 = ((pair[1] >> 8) & 255) * height/256;
                    line(x1, y1, x2, y2);
                }
            }

            // Draw current sequence
            let seq = generateSequence(currentState.initialization, currentState.maxSteps);
            stroke('#800080');
            strokeWeight(2);
            noFill();
            beginShape();
            for(let n of seq) {
                let x = (n & 255) * width/256;
                let y = ((n >> 8) & 255) * height/256;
                vertex(x, y);
            }
            endShape();

            // Start and end points
            fill('#FF0000');
            noStroke();
            let x0 = (seq[0] & 255) * width/256;
            let y0 = ((seq[0] >> 8) & 255) * height/256;
            circle(x0, y0, 6);

            fill('#0000FF');
            let xn = (seq[seq.length-1] & 255) * width/256;
            let yn = ((seq[seq.length-1] >> 8) & 255) * height/256;
            circle(xn, yn, 6);
        }

        // Event Listeners
        document.getElementById('slider-initialization').addEventListener('input', function(e) {
            currentState.initialization = parseInt(e.target.value);
            document.getElementById('value-initialization').textContent = '+ ' + e.target.value;
            redraw();
        });

        document.getElementById('slider-max-steps').addEventListener('input', function(e) {
            currentState.maxSteps = parseInt(e.target.value);
            document.getElementById('value-max-steps').textContent = '+ ' + e.target.value;
            redraw();
        });

        document.getElementById('checkbox-cycle4').addEventListener('change', function(e) {
            currentState.showCycle4 = e.target.checked;
            redraw();
        });

        document.getElementById('checkbox-cycle2').addEventListener('change', function(e) {
            currentState.showCycle2 = e.target.checked;
            redraw();
        });

        document.getElementById('checkbox-cycle1').addEventListener('change', function(e) {
            currentState.showCycle1 = e.target.checked;
            redraw();
        });

        document.getElementById('checkbox-all-points').addEventListener('change', function(e) {
            currentState.showAllPoints = e.target.checked;
            redraw();
        });

        document.getElementById('checkbox-modify').addEventListener('change', function(e) {
            currentState.modifyInit = e.target.checked;
        });
    </script>
</body>
</html>
