            width: 80px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label>step</label>
                <div>
                    <input type="radio" name="step-type" id="step-reflux" value="reflux_ratio" checked>
                    <label for="step-reflux">reflux ratio</label>
                </div>
                <div>
                    <input type="radio" name="step-type" id="step-reboil" value="reboil_ratio">
                    <label for="step-reboil">reboil ratio</label>
                </div>
            </div>

            <div class="control-group">
                <label for="step-percent">% step</label>
                <input type="number" id="step-percent" value="-0.3" min="-1.0" max="1.0" step="0.1">
            </div>

            <div class="control-group">
                <label>stage</label>
                <div>
                    <input type="radio" name="stage" id="stage-cond" value="Cond">
                    <label for="stage-cond">Cond</label>

                    <input type="radio" name="stage" id="stage-1" value="1">
                    <label for="stage-1">1</label>

                    <input type="radio" name="stage" id="stage-5" value="5" checked>
                    <label for="stage-5">5</label>

                    <input type="radio" name="stage" id="stage-10" value="10">
                    <label for="stage-10">10</label>

                    <input type="radio" name="stage" id="stage-reb" value="Reb">
                    <label for="stage-reb">Reb</label>
                </div>
            </div>

            <div class="control-group">
                <div>
                    <button id="btn-composition" class="plot-type-btn active">composition</button>
                    <button id="btn-temperature" class="plot-type-btn">temperature</button>
                    <button id="btn-molar-hold-up" class="plot-type-btn">molar hold-up</button>
                </div>
            </div>

            <div id="component-selector-container" class="control-group">
                <label>composition</label>
                <div>
                    <input type="radio" name="component" id="comp-benzene" value="benzene" checked>
                    <label for="comp-benzene">benzene</label>

                    <input type="radio" name="component" id="comp-toluene" value="toluene">
                    <label for="comp-toluene">toluene</label>

                    <input type="radio" name="component" id="comp-pxylene" value="pxylene">
                    <label for="comp-pxylene">p-xylene</label>
                </div>
            </div>
        </div>

        <div id="plot-container"></div>
    </div>

    <script>
        const simulationParams = {
            reflux_ratio: {
                composition: {
                    benzene: {
                        'Cond': { initial: 0.98, Kp: -0.1, tau: 1.2 },
                        '5': { initial: 0.8872, Kp: -0.02, tau: 0.5 },
                        'Reb': { initial: 0.02, Kp: 0.05, tau: 1.8 }
                    },
                    toluene: {
                        'Cond': { initial: 0.01, Kp: 0.08, tau: 1.1 },
                        '5': { initial: 0.0928, Kp: 0.015, tau: 0.6 },
                        'Reb': { initial: 0.35, Kp: -0.06, tau: 1.7 }
                    },
                    pxylene: {
                        'Cond': { initial: 0.01, Kp: 0.05, tau: 1.3 },
                        '5': { initial: 0.02, Kp: 0.01, tau: 0.7 },
                        'Reb': { initial: 0.63, Kp: -0.08, tau: 1.9 }
                    }
                },
                temperature: {
                    'Cond': { initial: 80, Kp: 2, tau: 1.0 },
                    '5': { initial: 95, Kp: 3, tau: 0.8 },
                    'Reb': { initial: 110, Kp: 4, tau: 1.5 }
                },
                molar_hold_up: {
                    'Cond': { initial: 100, Kp: -5, tau: 1.1 },
                    '5': { initial: 80, Kp: -3, tau: 0.9 },
                    'Reb': { initial: 120, Kp: -6, tau: 1.6 }
                }
            },
            reboil_ratio: {
                // Similar structure with different parameters
                composition: {
                    benzene: {
                        'Cond': { initial: 0.98, Kp: 0.08, tau: 1.4 },
                        '5': { initial: 0.8872, Kp: 0.03, tau: 0.6 },
                        'Reb': { initial: 0.02, Kp: -0.04, tau: 2.0 }
                    },
                    // ... other components
                },
                temperature: {
                    'Cond': { initial: 80, Kp: -2.5, tau: 1.2 },
                    '5': { initial: 95, Kp: -3.5, tau: 0.9 },
                    'Reb': { initial: 110, Kp: -4.5, tau: 1.7 }
                },
                molar_hold_up: {
                    'Cond': { initial: 100, Kp: 4, tau: 1.3 },
                    '5': { initial: 80, Kp: 2.5, tau: 1.0 },
                    'Reb': { initial: 120, Kp: 5, tau: 1.8 }
                }
            }
        };

        function calculateResponse(time, params, stepSize) {
            if (time < 10) {
                return params.initial;
            }
            return params.initial + params.Kp * stepSize * (1 - Math.exp(-(time - 10) / params.tau));
        }

        function updatePlot() {
            const stepType = document.querySelector('input[name="step-type"]:checked').value;
            const stepPercent = parseFloat(document.getElementById('step-percent').value);
            const stage = document.querySelector('input[name="stage"]:checked').value;
            const plotType = document.querySelector('.plot-type-btn.active').id.replace('btn-', '');
            const component = document.querySelector('input[name="component"]:checked')?.value;

            // Generate time points
            const timePoints = Array.from({length: 201}, (_, i) => i * 0.1);

            // Get simulation parameters
            let params;
            if (plotType === 'composition') {
                params = simulationParams[stepType][plotType][component][stage];
            } else {
                params = simulationParams[stepType][plotType][stage];
            }

            // Calculate response
            const yPoints = timePoints.map(t => calculateResponse(t, params, stepPercent));

            // Set up plot
            const trace = {
                x: timePoints,
                y: yPoints,
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: plotType === 'composition' ? 
                        (component === 'benzene' ? '#FF0000' : 
                         component === 'toluene' ? '#0000FF' : '#008B8B') : 
                        '#000000'
                }
            };

            const layout = {
                xaxis: {
                    title: 'time in minutes',
                    gridcolor: '#CCCCCC',
                    range: [0, 20]
                },
                yaxis: {
                    title: plotType === 'composition' ? 
                        `${component} mole fraction` :
                        plotType === 'temperature' ? 
                        'temperature (C)' : 'molar hold-up (kmol)',
                    gridcolor: '#CCCCCC'
                },
                margin: { l: 80, r: 20, t: 20, b: 50 },
                paper_bgcolor: 'rgba(0,0,0,0)',
                plot_bgcolor: 'rgba(0,0,0,0)'
            };

            Plotly.react('plot-container', [trace], layout);
        }

        // Event listeners
        document.querySelectorAll('input[type="radio"], input[type="number"]')
            .forEach(el => el.addEventListener('change', updatePlot));

        document.querySelectorAll('.plot-type-btn').forEach(btn => {
            btn.addEventListener('click', (e) => {
                document.querySelectorAll('.plot-type-btn')
                    .forEach(b => b.classList.remove('active'));
                e.target.classList.add('active');

                document.getElementById('component-selector-container').style.display = 
                    e.target.id === 'btn-composition' ? 'block' : 'none';

                updatePlot();
            });
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
