    <script>
        // Initialize Three.js scene, camera, and renderer
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        const container = document.getElementById('visualization-container');
        renderer.setSize(container.clientWidth, container.clientHeight);
        container.appendChild(renderer.domElement);

        // Initialize CSS2D renderer for labels
        const labelRenderer = new THREE.CSS2DRenderer();
        labelRenderer.setSize(container.clientWidth, container.clientHeight);
        labelRenderer.domElement.style.position = 'absolute';
        labelRenderer.domElement.style.top = '0';
        container.appendChild(labelRenderer.domElement);

        // Set up orbit controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        camera.position.z = 5;
        controls.update();

        // Create celestial sphere
        const sphereGeometry = new THREE.SphereGeometry(2, 32, 32);
        const sphereMaterial = new THREE.MeshBasicMaterial({
            color: 0xffffff,
            transparent: true,
            opacity: 0.4,
            side: THREE.DoubleSide,
            vertexColors: true
        });
        const celestialSphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(celestialSphere);

        // Event listeners for controls
        document.getElementById('select-family').addEventListener('change', updateConstellation);
        document.getElementById('select-constellation').addEventListener('change', updateConstellation);
        document.getElementById('check-sphere').addEventListener('change', e => celestialSphere.visible = e.target.checked);
        document.getElementById('slider-ra').addEventListener('input', updateZenith);
        document.getElementById('slider-dec').addEventListener('input', updateZenith);

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
            labelRenderer.render(scene, camera);
        }
        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            const width = container.clientWidth;
            const height = container.clientHeight;
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
            labelRenderer.setSize(width, height);
