            width: 24px;
            height: 24px;
            border-radius: 50%;
            border: 1px solid #666;
            background: white;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        #plot-signal, #plot-spectrum {
            height: 300px;
            margin: 10px 0;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <h3>coefficients</h3>
            <div class="control-group">
                <label for="slider-c1">c₁</label>
                <input type="range" id="slider-c1" min="-2" max="2" step="0.1" value="0">
                <span id="c1-value-display">0</span>
                <button id="reset-c1" class="reset-btn">+</button>
            </div>
            <div class="control-group">
                <label for="slider-c2">c₂</label>
                <input type="range" id="slider-c2" min="-2" max="2" step="0.1" value="0">
                <span id="c2-value-display">0</span>
                <button id="reset-c2" class="reset-btn">+</button>
            </div>

            <h3>clipping</h3>
            <div class="control-group">
                <input type="checkbox" id="checkbox-clipping">
                <label for="checkbox-clipping">enable</label>
            </div>
            <div id="level-control-group" class="control-group" style="display: none;">
                <label for="slider-level">level</label>
                <input type="range" id="slider-level" min="0" max="1.5" step="0.05" value="0.7">
                <span id="level-value-display">0.7</span>
            </div>

            <h3>DFT</h3>
            <div class="control-group">
                <label>scale</label>
                <button id="btn-linear" class="active">linear</button>
                <button id="btn-decibel">decibel</button>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-signal"></div>
            <div id="plot-spectrum"></div>
        </div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // FFT Implementation
        function fft(real, imag) {
            const n = real.length;
            if (n <= 1) return;

            const realEven = new Float32Array(n/2);
            const imagEven = new Float32Array(n/2);
            const realOdd = new Float32Array(n/2);
            const imagOdd = new Float32Array(n/2);

            for (let i = 0; i < n/2; i++) {
                realEven[i] = real[2*i];
                imagEven[i] = imag[2*i];
                realOdd[i] = real[2*i+1];
                imagOdd[i] = imag[2*i+1];
            }

            fft(realEven, imagEven);
            fft(realOdd, imagOdd);

            for (let k = 0; k < n/2; k++) {
                const theta = -2 * Math.PI * k / n;
                const c = Math.cos(theta);
                const s = Math.sin(theta);
                const tr = realOdd[k] * c - imagOdd[k] * s;
                const ti = realOdd[k] * s + imagOdd[k] * c;
                real[k] = realEven[k] + tr;
                imag[k] = imagEven[k] + ti;
                real[k + n/2] = realEven[k] - tr;
                imag[k + n/2] = imagEven[k] - ti;
            }
        }

        let normalizedMag;
        const N = 2048;
        const fs = 32000;
        const f = 1000;

        function updateSimulation() {
            const c1 = parseFloat(document.getElementById('slider-c1').value);
            const c2 = parseFloat(document.getElementById('slider-c2').value);
            const isClipping = document.getElementById('checkbox-clipping').checked;
            const level = parseFloat(document.getElementById('slider-level').value);

            // Generate signal
            const t = new Float32Array(N).map((_, i) => i/fs);
            const x = t.map(ti => Math.sin(2 * Math.PI * f * ti));
            let y = x.map(xi => xi + c1 * xi * xi + c2 * xi * xi * xi);

            if (isClipping) {
                y = y.map(yi => Math.max(-level, Math.min(level, yi)));
            }

            // Update signal plot
            Plotly.react('plot-signal', [{
                x: t.slice(0, Math.floor(0.005 * fs)),
                y: y.slice(0, Math.floor(0.005 * fs)),
                mode: 'lines',
                line: {color: '#1f77b4'}
            }], {
                title: 'signal',
                xaxis: {title: 'time[s]', range: [0, 0.005]},
                yaxis: {title: '', range: [-2, 2]},
                margin: {l: 30, r: 20, b: 40, t: 40}
            });

            // Compute FFT
            const real = new Float32Array(y);
            const imag = new Float32Array(N);
            fft(real, imag);

            // Calculate magnitude spectrum
            normalizedMag = new Float32Array(N/2);
            normalizedMag[0] = Math.sqrt(real[0]**2 + imag[0]**2) / N;
            for(let k = 1; k < N/2; k++) {
                normalizedMag[k] = 2 * Math.sqrt(real[k]**2 + imag[k]**2) / N;
            }

            updateSpectrumScale();

            // Update displays
            document.getElementById('c1-value-display').textContent = c1.toFixed(1);
            document.getElementById('c2-value-display').textContent = c2.toFixed(1);
            document.getElementById('level-value-display').textContent = level.toFixed(2);
        }

        function updateSpectrumScale() {
            const isLinear = document.getElementById('btn-linear').classList.contains('active');
            const freq = new Float32Array(N/2).map((_, k) => k * fs / N);

            let yData, yRange;
            if (isLinear) {
                yData = normalizedMag;
                yRange = [0, 1.1];
            } else {
                yData = normalizedMag.map(m => 20 * Math.log10(Math.max(m, 1e-10)));
                yRange = [-100, 0];
            }

            Plotly.react('plot-spectrum', [{
                x: freq,
                y: yData,
                type: 'bar',
                width: 5
            }], {
                title: 'magnitude spectrum (DFT)',
                xaxis: {title: 'frequency[Hz]', range: [0, 8000]},
                yaxis: {title: '', range: yRange},
                margin: {l: 30, r: 20, b: 40, t: 40}
            });
        }

        // Event listeners
        document.getElementById('slider-c1').oninput = updateSimulation;
        document.getElementById('slider-c2').oninput = updateSimulation;
        document.getElementById('slider-level').oninput = updateSimulation;

        document.getElementById('reset-c1').onclick = () => {
            document.getElementById('slider-c1').value = 0;
            updateSimulation();
        };

        document.getElementById('reset-c2').onclick = () => {
            document.getElementById('slider-c2').value = 0;
            updateSimulation();
        };

        document.getElementById('checkbox-clipping').onchange = function() {
            document.getElementById('level-control-group').style.display = 
                this.checked ? 'flex' : 'none';
            updateSimulation();
        };

        document.getElementById('btn-linear').onclick = function() {
            this.classList.add('active');
            document.getElementById('btn-decibel').classList.remove('active');
            updateSpectrumScale();
        };

        document.getElementById('btn-decibel').onclick = function() {
            this.classList.add('active');
            document.getElementById('btn-linear').classList.remove('active');
            updateSpectrumScale();
        };

        // Initialize plots
        updateSimulation();
    </script>
</body>
</html>
