        function setupControls() {
            // Get all control elements and store their values
            controls.rayCount = document.getElementById('slider-ray-count').value;
            controls.beamWidth = document.getElementById('slider-beam-width').value;
            controls.incidentAngle = document.getElementById('slider-incident-angle').value;
            controls.refractionRatio = document.getElementById('slider-refraction-ratio').value;

            controls.showInjected = document.getElementById('checkbox-injected').checked;
            controls.showRefracted = document.getElementById('checkbox-refracted').checked;
            controls.showReflected = document.getElementById('checkbox-reflected').checked;
            controls.showEjected = document.getElementById('checkbox-ejected').checked;
            controls.showNormals = document.getElementById('checkbox-normals').checked;
            controls.showIntersections = document.getElementById('checkbox-intersections').checked;
            controls.showBeamTop = document.getElementById('checkbox-beam-top').checked;
            controls.showBeamBottom = document.getElementById('checkbox-beam-bottom').checked;
        }

        function updateControls() {
            setupControls();

            // Update display values
            document.getElementById('ray-count-value').textContent = controls.rayCount;
            document.getElementById('beam-width-value').textContent = controls.beamWidth;
            document.getElementById('incident-angle-value').textContent = controls.incidentAngle;
            document.getElementById('refraction-ratio-value').textContent = controls.refractionRatio;
        }

        function draw() {
            background(255);

            // Set up coordinate system
            translate(width/2, height/2);
            scale(width/5);

            // Draw axes
            drawAxes();

            // Draw sphere
            noFill();
            stroke(0);
            circle(0, 0, 2);

            // Calculate and draw rays
            drawRays();
        }

        function drawAxes() {
            stroke(200);
            strokeWeight(0.01);
            line(-2.5, 0, 2.5, 0);
            line(0, -2.5, 0, 2.5);

            // Draw tick marks
            for(let i = -2; i <= 2; i++) {
                if(i === 0) continue;
                line(i, -0.1, i, 0.1);
                line(-0.1, i, 0.1, i);
            }
        }

        function drawRays() {
            const angle = radians(controls.incidentAngle);
            const rayCount = parseInt(controls.rayCount);
            const beamWidth = parseFloat(controls.beamWidth);
            const n = parseFloat(controls.refractionRatio);

            for(let i = 0; i < rayCount; i++) {
                const y = map(i, 0, rayCount-1, -beamWidth/2, beamWidth/2);

                if((y > 0 && !controls.showBeamTop) || (y < 0 && !controls.showBeamBottom)) {
                    continue;
                }

                // Calculate ray paths here
                const startX = -2.5;
                const startY = y;
                const dirX = cos(angle);
                const dirY = sin(angle);

                // Draw ray segments based on checkbox states
                if(controls.showInjected) {
                    stroke(255, 0, 0);
                    line(startX, startY, startX + 2*dirX, startY + 2*dirY);
                }

                if(controls.showIntersections) {
                    fill(255, 0, 0);
                    noStroke();
                    circle(startX + 1.5*dirX, startY + 1.5*dirY, 0.05);
                }
            }
        }
    </script>
