            </div>
        </div>

        <div id="plot-container"></div>
    </div>

    <script>
        function factorial(n) {
            if (n <= 1) return 1;
            return n * factorial(n - 1);
        }

        function generateData(n) {
            const x = Array.from({length: n}, (_, i) => i + 1);
            return {
                bubble: x.map(v => v * v),
                selection: x.map(v => 0.25 * v * v),
                insertion: x.map(v => v * factorial(v)),
                merge: x.map(v => v * Math.log2(v)),
                quick: x.map(v => 1.2 * v * Math.log2(v)),
                heap: x.map(v => 1.5 * v * Math.log2(v)),
                bogo: x.map(v => v * factorial(v))
            };
        }

        function updatePlot() {
            const n = parseInt(document.getElementById('slider-elements').value);
            const data = generateData(n);
            const x = Array.from({length: n}, (_, i) => i + 1);

            const traces = [
                {name: 'bubble', x, y: data.bubble, visible: document.getElementById('check-bubble').checked,
                 line: {color: 'green', width: 5}},
                {name: 'selection', x, y: data.selection, visible: document.getElementById('check-selection').checked,
                 line: {color: 'orange', dash: 'dot'}},
                {name: 'insertion', x, y: data.insertion, 
                 visible: document.getElementById('check-insertion').checked || document.getElementById('check-bogo').checked,
                 line: {color: 'red', width: 2}},
                {name: 'merge', x, y: data.merge, visible: document.getElementById('check-merge').checked,
                 line: {color: 'blue', width: 2}},
                {name: 'quick', x, y: data.quick, visible: document.getElementById('check-quick').checked,
                 line: {color: 'purple', width: 2}},
                {name: 'heap', x, y: data.heap, visible: document.getElementById('check-heap').checked,
                 line: {color: 'cyan', width: 2}}
            ];

            const layout = {
                title: 'time complexity',
                xaxis: {
                    title: 'number of elements',
                    range: document.getElementById('radio-range-fixed').checked ? [0, 20] : undefined,
                    autorange: document.getElementById('radio-range-automatic').checked
                },
                yaxis: {
                    title: 'operations',
                    type: document.getElementById('radio-scale-logarithmic').checked ? 'log' : 'linear',
                    range: document.getElementById('radio-range-fixed').checked ? [0, 500] : undefined,
                    autorange: document.getElementById('radio-range-automatic').checked
                },
                legend: {
                    orientation: 'h',
                    y: -0.2
                }
            };

            Plotly.react('plot-container', traces, layout);
        }

        // Event listeners
        document.getElementById('slider-elements').addEventListener('input', function(e) {
            document.getElementById('display-elements').textContent = '+ ' + e.target.value;
            updatePlot();
        });

        document.querySelectorAll('input[type="radio"], input[type="checkbox"]').forEach(input => {
            input.addEventListener('change', updatePlot);
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
