    <script>
        let sceneObjects = [];
        let selectedTool = null;
        let activeObject = null;
        let interactionMode = null;
        let dragOffset = {x: 0, y: 0};
        let initialAngle = 0;

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('drawing-canvas');
            textAlign(CENTER, CENTER);
            textSize(16);
        }

        function draw() {
            background(255);

            // Draw all objects
            for (let obj of sceneObjects) {
                push();
                translate(obj.x, obj.y);
                rotate(obj.rotation);

                if (obj.type === 'atom' || obj.type === 'charge') {
                    text(obj.value, 0, 0);
                } else if (obj.type === 'structure') {
                    drawStructure(obj);
                }
                pop();
            }

            // Draw preview if tool selected
            if (selectedTool) {
                push();
                translate(mouseX, mouseY);
                fill(128, 128, 128, 128);
                text(selectedTool.value, 0, 0);
                pop();
            }
        }

        // Setup toolbar buttons
        document.querySelectorAll('#toolbar button').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('#toolbar button').forEach(b => b.classList.remove('selected'));
                btn.classList.add('selected');

                if (btn.id.includes('charge')) {
                    selectedTool = {
                        type: 'charge',
                        value: btn.textContent
                    };
                } else {
                    selectedTool = {
                        type: 'atom',
                        value: btn.textContent
                    };
                }
            });
        });

        // Add button handler
        document.getElementById('btn-add').addEventListener('click', () => {
            const structure = document.getElementById('select-structure').value;
            sceneObjects.push({
                type: 'structure',
                value: structure,
                x: width/2,
                y: height/2,
                rotation: 0
            });
        });

        // Undo button handler
        document.getElementById('btn-undo').addEventListener('click', () => {
            sceneObjects.pop();
        });

        function mousePressed() {
            if (selectedTool) {
                sceneObjects.push({
                    type: selectedTool.type,
                    value: selectedTool.value,
                    x: mouseX,
                    y: mouseY,
                    rotation: 0
                });
                selectedTool = null;
                document.querySelectorAll('#toolbar button').forEach(b => b.classList.remove('selected'));
                return;
            }

            // Check for object selection
            for (let i = sceneObjects.length - 1; i >= 0; i--) {
                const obj = sceneObjects[i];
                const d = dist(mouseX, mouseY, obj.x, obj.y);
                if (d < 40) {
                    activeObject = obj;
                    dragOffset = {
                        x: mouseX - obj.x,
                        y: mouseY - obj.y
                    };
                    if (d < 20) {
                        interactionMode = 'rotate';
                        initialAngle = atan2(mouseY - obj.y, mouseX - obj.x);
                    } else {
                        interactionMode = 'translate';
                    }
                    break;
                }
            }
        }

        function mouseDragged() {
            if (activeObject && interactionMode === 'translate') {
                activeObject.x = mouseX - dragOffset.x;
                activeObject.y = mouseY - dragOffset.y;
            } else if (activeObject && interactionMode === 'rotate') {
                const currentAngle = atan2(mouseY - activeObject.y, mouseX - activeObject.x);
                activeObject.rotation = currentAngle - initialAngle;
            }
        }

        function mouseReleased() {
            activeObject = null;
            interactionMode = null;
        }

        function drawStructure(obj) {
            switch(obj.value) {
                case 'water':
                    line(0, 0, -20, -20);
                    line(0, 0, 20, -20);
                    text('O', 0, 0);
                    text('H', -20, -20);
                    text('H', 20, -20);
                    break;
                // Add other structure drawing functions as needed
            }
        }
    </script>
</body>
</html>
