    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 20px;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #controls-container {
            width: 800px;
            padding: 20px;
            background: #f5f5f5;
            border-radius: 8px;
        }
        #controls-container > div {
