        }
        select {
            width: 100px;
        }
        #formula-display {
            margin: 20px 0;
            padding: 10px;
            font-size: 1.2em;
            text-align: center;
        }
        #canvas-container {
            margin-top: 20px;
        }
    </style>
</head>
<body>
    <div id="controls-container">
        <div>
            <label for="slider-amplitude">\(a\)</label>
            <input type="range" id="slider-amplitude" min="0.1" max="2.0" step="0.01" value="1.5">
            <span id="value-amplitude">1.50</span>
        </div>
        <div>
            <label for="slider-frequency">\(f\)</label>
            <input type="range" id="slider-frequency" min="0.1" max="1.0" step="0.01" value="0.3">
            <span id="value-frequency">0.30</span>
        </div>
        <div>
            <label for="slider-wavelength">\(\lambda\)</label>
            <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
            <span id="value-wavelength">4.50</span>
        </div>
        <div>
            <label for="slider-time">\(t\)</label>
            <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
            <span id="value-time">2.50</span>
        </div>
        <div>
            <label for="select-phase">initial phase \(\phi_0\)</label>
            <select id="select-phase">
                <option value="-3.141592653589793">-π</option>
                <option value="-2.0943951023931953">-2π/3</option>
                <option value="-1.5707963267948966" selected>-π/2</option>
                <option value="-1.0471975511965976">-π/3</option>
                <option value="0">0</option>
                <option value="1.0471975511965976">π/3</option>
                <option value="1.5707963267948966">π/2</option>
                <option value="2.0943951023931953">2π/3</option>
                <option value="3.141592653589793">π</option>
            </select>
        </div>
    </div>
    <div id="formula-display"></div>
    <div id="canvas-container"></div>

    <script>
        let params = {
            A: 1.5,
            f: 0.3,
            lambda: 4.5,
            t: 2.5,
            phi0: -Math.PI/2
        };

        function updateFormula() {
            let k = 2 * Math.PI / params.lambda;
            let omega = 2 * Math.PI * params.f;
            let formula = '';

            if (Math.abs(params.phi0 - Math.PI/2) < 0.0001) {
                formula = `${params.A.toFixed(2)}\\sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
            } else if (Math.abs(params.phi0 + Math.PI/2) < 0.0001) {
                formula = `-${params.A.toFixed(2)}\\sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
            } else if (Math.abs(params.phi0 - Math.PI) < 0.0001) {
                formula = `-${params.A.toFixed(2)}\\cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
            } else if (Math.abs(params.phi0) < 0.0001) {
                formula = `${params.A.toFixed(2)}\\cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
            } else {
                formula = `${params.A.toFixed(2)}\\cos(${omega.toFixed(2)}t - ${k.toFixed(2)}x + ${params.phi0.toFixed(2)})`;
            }

            document.getElementById('formula-display').innerHTML = `\\[y(x,t) = ${formula}\\]`;
            MathJax.typeset();
        }

        let sketch = function(p) {
            const SCALE = 30;
            const ORIGIN_X = 50;
            const ORIGIN_Y = 300;

            p.setup = function() {
                p.createCanvas(800, 600);
            };

            function drawArrow(baseX, baseY, vectorY, color, label) {
                p.push();
                p.stroke(color);
                p.fill(color);
                p.line(baseX, baseY, baseX, baseY + vectorY);
                p.triangle(baseX - 5, baseY + vectorY, baseX + 5, baseY + vectorY, baseX, baseY + vectorY + (vectorY > 0 ? 10 : -10));
                p.textAlign(p.LEFT);
                p.text(label, baseX + 5, baseY + vectorY);
                p.pop();
            }

            p.draw = function() {
                p.background(255);
                p.translate(ORIGIN_X, ORIGIN_Y);
                p.scale(1, -1);

                // Draw axes
                p.stroke(0);
                p.line(0, 0, p.width - ORIGIN_X, 0);
                p.line(0, -p.height/2, 0, p.height/2);

                // Draw grid
                p.stroke(200, 255, 200);
                p.strokeWeight(1);
                for(let x = SCALE; x < p.width - ORIGIN_X; x += SCALE) {
                    p.line(x, -p.height/2, x, p.height/2);
                }

                // Draw wave
                let k = 2 * Math.PI / params.lambda;
                let omega = 2 * Math.PI * params.f;

                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for(let x = 0; x < p.width - ORIGIN_X; x++) {
                    let worldX = x / SCALE;
                    let y = params.A * Math.cos(omega * params.t - k * worldX + params.phi0);
                    p.vertex(x, y * SCALE);
                }
                p.endShape();

                // Draw points and vectors
                for(let x = 1; x <= 16; x++) {
                    let worldX = x;
                    let y = params.A * Math.cos(omega * params.t - k * worldX + params.phi0);
                    let v = -params.A * omega * Math.sin(omega * params.t - k * worldX + params.phi0);
                    let a = -params.A * omega * omega * Math.cos(omega * params.t - k * worldX + params.phi0);

                    p.fill(0);
                    p.noStroke();
                    p.circle(x * SCALE, y * SCALE, 5);

                    drawArrow(x * SCALE, y * SCALE, v * 0.5, 'red', 'v');
                    drawArrow(x * SCALE, y * SCALE, a * 0.1, 'black', 'a');
                }
            };
        };

        new p5(sketch, 'canvas-container');

        // Event listeners
        document.getElementById('slider-amplitude').addEventListener('input', function(e) {
            params.A = parseFloat(e.target.value);
            document.getElementById('value-amplitude').textContent = params.A.toFixed(2);
            updateFormula();
        });

        document.getElementById('slider-frequency').addEventListener('input', function(e) {
            params.f = parseFloat(e.target.value);
            document.getElementById('value-frequency').textContent = params.f.toFixed(2);
            updateFormula();
        });

        document.getElementById('slider-wavelength').addEventListener('input', function(e) {
            params.lambda = parseFloat(e.target.value);
            document.getElementById('value-wavelength').textContent = params.lambda.toFixed(2);
            updateFormula();
        });

        document.getElementById('slider-time').addEventListener('input', function(e) {
            params.t = parseFloat(e.target.value);
            document.getElementById('value-time').textContent = params.t.toFixed(2);
            updateFormula();
        });

        document.getElementById('select-phase').addEventListener('change', function(e) {
            params.phi0 = parseFloat(e.target.value);
            updateFormula();
        });

        // Initial formula update
        updateFormula();
    </script>
</body>
</html>
