            // Y axis
            line(0, -7, 0, 7);
            for(let y = -7; y <= 7; y++) {
                line(-0.1, y, 0.1, y);
            }

            // Draw central body
            fill(0);
            noStroke();
            circle(0, 0, 0.2);

            // Draw orbit
            stroke('#4a80b5');
            strokeWeight(2/30);
            noFill();

            beginShape();
            if(epsilon === 0) {
                // Circle case
                for(let theta = 0; theta <= TWO_PI; theta += 0.01) {
                    let x = 2 * cos(theta) - 2;
                    let y = 2 * sin(theta);
                    vertex(x, y);
                }
            } else if(epsilon < 1) {
                // Ellipse case
                for(let theta = 0; theta <= TWO_PI; theta += 0.01) {
                    let r = p/(1 + epsilon * cos(theta));
                    let x = r * cos(theta);
                    let y = r * sin(theta);
                    vertex(x, y);
                }
            } else if(epsilon === 1) {
                // Parabola case
                for(let theta = -2.2; theta <= 2.2; theta += 0.01) {
                    let r = p/(1 + epsilon * cos(theta));
                    let x = r * cos(theta);
                    let y = r * sin(theta);
                    vertex(x, y);
                }
            } else {
                // Hyperbola case
                let thetaMax = acos(-1/epsilon);
                for(let theta = -thetaMax; theta <= thetaMax; theta += 0.01) {
                    let r = p/(1 + epsilon * cos(theta));
                    let x = r * cos(theta);
                    let y = r * sin(theta);
                    vertex(x, y);
                }
            }
            endShape();
        }

        document.getElementById('slider-eccentricity').addEventListener('input', function(e) {
            epsilon = parseFloat(e.target.value);
            document.getElementById('value-eccentricity').textContent = epsilon.toFixed(1);

            // Update energy display
            let energy = -0.25 * (1 - epsilon * epsilon);
            if(epsilon === 0) {
                document.getElementById('energy-value').innerHTML = '\\(-\\frac{1}{4}\\)';
                MathJax.typeset();
            } else {
                document.getElementById('energy-value').textContent = energy.toFixed(2);
            }

            draw();
        });
    </script>
</body>
</html>
