        // Generate x-axis arrays
        const srb = Array.from({length: 100}, (_, i) => i + 1);
        const hgt = Array.from({length: 201}, (_, i) => i * 0.01);

        // Calculate MI values for given parameters
        function calculateMI(poc, eh, hgm, srb_val) {
            const C = 125 * (-eh + 200) * poc * hgm;
            return C / srb_val;
        }

        // Calculate MeHg values for Michaelis-Menten kinetics
        function calculateMeHg(k, a, hgt_val) {
            return (a * hgt_val) / (k + hgt_val);
        }

        // Update all plots
        function updatePlots() {
            const poc = parseFloat(document.getElementById('slider-poc').value);
            const eh = parseFloat(document.getElementById('slider-eh').value);
