                {name: 'River', poc: 56, color: '#1f77b4'},
                {name: 'Estuarine', poc: 6, color: '#aec7e8'},
                {name: 'Seawater', poc: 0.3, color: '#d1e0f0'}
            ];

            const traces = environments.map(env => ({
                name: env.name,
                x: srb,
                y: srb.map(x => calculateMI(env.poc, eh, hgm, x)),
                mode: 'lines',
                line: {color: env.color}
            }));

            Plotly.newPlot('plot2', traces, {
                title: 'MI at river, estuarine and marine POC',
                xaxis: {title: 'SRB (dSrB)', range: [0, 100]},
                yaxis: {title: 'methyl mercury index (MI)', range: [0, 1.0]},
                plot_bgcolor: 'white',
                showlegend: true
            });

            // Plot 3: Michaelis-Menten kinetics
            const mehg_values = hgt.map(x => calculateMeHg(k, a, x));
            Plotly.newPlot('plot3', [{
                x: hgt,
                y: mehg_values,
                mode: 'lines',
                line: {color: '#1f77b4'},
                fill: 'tozeroy',
                fillcolor: 'rgba(31, 119, 180, 0.2)'
            }], {
                title: 'methyl mercury availability',
                xaxis: {title: '(µM/L) HgT', range: [0, 2.0]},
                yaxis: {title: '[MeHg] (µM/L)', range: [0, 0.20]},
                plot_bgcolor: 'white',
                annotations: [{
                    x: 1.0,
                    y: 0.05,
                    text: `[MeHg] = ${a.toFixed(3)}`,
                    showarrow: false,
                    font: {size: 14},
                    bgcolor: 'rgba(255, 255, 255, 0.7)'
                }]
            });
        }

        // Update value displays and plots when sliders change
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', function() {
                const valueSpan = document.getElementById(`value-${this.id.split('-')[1]}`);
                if (this.id === 'slider-eh') {
                    valueSpan.textContent = this.value;
                } else if (this.id === 'slider-k' || this.id === 'slider-a') {
                    valueSpan.textContent = parseFloat(this.value).toFixed(3);
                } else {
                    valueSpan.textContent = parseFloat(this.value).toFixed(1);
                }
                updatePlots();
            });
        });

        // Initial plot render
        updatePlots();
    </script>
</body>
</html>
