            display: flex;
            border: 1px solid #ccc;
            padding: 10px;
        }
        .plot {
            flex: 1;
            margin: 0 10px;
        }
        .slider-container {
            margin: 10px 0;
        }
        .slider-container input {
            width: 300px;
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>Quantum Computer Search Algorithms</h1>

        <div class="control-panel">
            <div class="control-group">
                <span>algorithm: </span>
                <div class="button-group">
                    <button id="btn-unstructured">unstructured</button>
                    <button id="btn-adiabatic">adiabatic</button>
                    <button id="btn-discrete-adiabatic">discrete adiabatic</button>
                    <button id="btn-heuristic" class="active">heuristic</button>
                </div>
            </div>

            <div class="slider-container">
                <label for="slider-total-steps">number of steps to complete the search: </label>
                <input type="range" id="slider-total-steps" min="1" max="200" value="10" step="1">
                <span id="display-total-steps">10</span>
            </div>

            <div class="slider-container">
                <label for="slider-current-step">current step (0 to 10): </label>
                <input type="range" id="slider-current-step" min="0" max="10" value="0" step="1">
                <span id="display-current-step">0</span>
            </div>
        </div>

        <div class="visualization">
            <div class="plot">
                <h4>amplitudes</h4>
                <div id="plot-amplitudes"></div>
            </div>
            <div class="plot">
                <div class="button-group">
                    <button id="btn-conflicts" class="active">conflicts</button>
                    <button id="btn-eigenvalues">eigenvalues</button>
                </div>
                <div id="plot-probability"></div>
            </div>
        </div>
    </div>

    <script>
        // Global state
        let currentAlgorithm = 'heuristic';
        let totalSteps = 10;
        let currentStep = 0;
        let probabilityMode = 'conflicts';
        let N = 64;
        let solutionStates = [Math.floor(Math.random() * N), Math.floor(Math.random() * N)];
        let conflictNumbers = new Array(N).fill(0).map((_, i) => 
            Math.min(...solutionStates.map(s => hammingDistance(i, s)))
        );

        function hammingDistance(a, b) {
            let count = 0;
            let xor = a ^ b;
            while(xor) {
                count += xor & 1;
                xor >>= 1;
            }
            return count;
        }

        function calculateState() {
            let state = new Array(N).fill(1/Math.sqrt(N));

            // This is a simplified placeholder implementation
            // Real quantum state calculations would go here
            if (currentStep > 0) {
                state = state.map((amp, i) => {
                    let phase = conflictNumbers[i] * currentStep/totalSteps;
                    return math.complex(
                        amp * Math.cos(phase),
                        amp * Math.sin(phase)
                    );
                });
            }

            return state;
        }

        function update() {
            const state = calculateState();

            // Update amplitudes plot
            const amplitudesData = [{
                x: state.map(c => math.re(c)),
                y: state.map(c => math.im(c)),
                mode: 'markers',
                type: 'scattergl',
                marker: {
                    size: Array(N).fill(6).map((s, i) => 
                        solutionStates.includes(i) ? 12 : s
                    ),
                    color: conflictNumbers,
                    colorscale: 'Viridis'
                }
            }];

            const amplitudesLayout = {
                title: 'State Amplitudes',
                xaxis: { title: 'Re' },
                yaxis: { title: 'Im' },
                showlegend: false
            };

            Plotly.newPlot('plot-amplitudes', amplitudesData, amplitudesLayout);

            // Update probability plot
            const probabilities = state.map(c => math.abs(c) * math.abs(c));
            const probabilityData = [{
                x: Array(N).fill(0).map((_, i) => conflictNumbers[i]),
                y: probabilities,
                type: 'bar',
                width: 0.1
            }];

            const probabilityLayout = {
                title: probabilityMode === 'conflicts' ? 'Probability by Conflicts' : 'Probability by Eigenvalue',
                xaxis: { title: probabilityMode === 'conflicts' ? 'number of conflicts' : 'eigenvalue' },
                yaxis: { title: 'probability', range: [0, 1] },
                showlegend: false
            };

            Plotly.newPlot('plot-probability', probabilityData, probabilityLayout);
        }

        // Event listeners
        document.querySelectorAll('.button-group button').forEach(button => {
            button.addEventListener('click', (e) => {
                if (e.target.id.startsWith('btn-')) {
                    const group = e.target.parentElement;
                    group.querySelectorAll('button').forEach(b => b.classList.remove('active'));
                    e.target.classList.add('active');

                    if (e.target.id.includes('conflicts') || e.target.id.includes('eigenvalues')) {
                        probabilityMode = e.target.id.replace('btn-', '');
                    } else {
                        currentAlgorithm = e.target.id.replace('btn-', '');
                        // Set default steps for algorithm
                        totalSteps = {
                            'unstructured': 5,
                            'adiabatic': 200,
                            'discrete-adiabatic': 200,
                            'heuristic': 10
                        }[currentAlgorithm];
                        document.getElementById('slider-total-steps').value = totalSteps;
                        document.getElementById('display-total-steps').textContent = totalSteps;
                        document.getElementById('slider-current-step').max = totalSteps;
                    }
                    update();
                }
            });
        });

        document.getElementById('slider-total-steps').addEventListener('input', (e) => {
            totalSteps = parseInt(e.target.value);
            document.getElementById('display-total-steps').textContent = totalSteps;
            const currentStepSlider = document.getElementById('slider-current-step');
            currentStepSlider.max = totalSteps;
            if (currentStep > totalSteps) {
                currentStep = totalSteps;
                currentStepSlider.value = currentStep;
                document.getElementById('display-current-step').textContent = currentStep;
            }
            update();
        });

        document.getElementById('slider-current-step').addEventListener('input', (e) => {
            currentStep = parseInt(e.target.value);
            document.getElementById('display-current-step').textContent = currentStep;
            update();
        });

        // Initialize visualization
        update();
    </script>
</body>
</html>
