<body>
    <div id="main-container">
        <div id="controls-container">
            <div id="control-group-pressure">
                <label for="slider-pressure">pressure (bar)</label>
                <input type="range" id="slider-pressure" min="0.01" max="0.20" step="0.005" value="0.08">
                <span id="value-pressure">0.08</span>
            </div>

            <div id="control-group-temperature" style="display: none;">
                <label for="slider-temperature">temperature (K)</label>
                <input type="range" id="slider-temperature" min="450" max="490" step="1" value="475">
                <span id="value-temperature">475</span>
            </div>

            <select id="select-plot">
                <option value="vs_temp">fugacity versus temperature</option>
                <option value="vs_press">fugacity versus pressure</option>
            </select>

            <div id="checkbox-container" style="display: none;">
                <input type="checkbox" id="checkbox-high-pressure" checked>
                <label for="checkbox-high-pressure">high pressure</label>
            </div>
        </div>

        <div id="plot-container"></div>
    </div>

    <script>
        // Get DOM elements
        const selectPlot = document.getElementById('select-plot');
        const sliderPressure = document.getElementById('slider-pressure');
        const sliderTemperature = document.getElementById('slider-temperature');
        const checkboxHighPressure = document.getElementById('checkbox-high-pressure');
        const pressureGroup = document.getElementById('control-group-pressure');
        const temperatureGroup = document.getElementById('control-group-temperature');
        const checkboxContainer = document.getElementById('checkbox-container');

        // Event listeners
        selectPlot.addEventListener('change', handlePlotTypeChange);
        sliderPressure.addEventListener('input', handlePressureChange);
        sliderTemperature.addEventListener('input', handleTemperatureChange);
        checkboxHighPressure.addEventListener('change', updatePlot);

        function handlePlotTypeChange() {
            if(selectPlot.value === 'vs_temp') {
                pressureGroup.style.display = 'block';
                temperatureGroup.style.display = 'none';
                checkboxContainer.style.display = 'none';
            } else {
                pressureGroup.style.display = 'none';
                temperatureGroup.style.display = 'block';
                checkboxContainer.style.display = 'block';
            }
            updatePlot();
        }

        function handlePressureChange() {
            document.getElementById('value-pressure').textContent = 
                Number(sliderPressure.value).toFixed(2);
            updatePlot();
        }

        function handleTemperatureChange() {
            document.getElementById('value-temperature').textContent = 
                Math.round(sliderTemperature.value);
            updatePlot();
        }

        function calculateFugacityVsTemp(T) {
            return Math.pow(10, 8.65 - 5818 / (230 + T));
        }

        function calculateSatTemp(P) {
            return 5818 / (8.65 - Math.log10(P)) - 230;
        }

        function calculateFugacityVsPress(P, T, isReal) {
            if(isReal) {
                return P * Math.exp(-25 * P / T);
            }
            return P;
        }

        function calculateSatPress(T) {
            return Math.pow(10, 10.83 - 5047 / T);
        }

        function updatePlot() {
            if(selectPlot.value === 'vs_temp') {
                plotFugacityVsTemp();
            } else {
                plotFugacityVsPress();
            }
        }

        function plotFugacityVsTemp() {
            const P = Number(sliderPressure.value);
            const Tsat = calculateSatTemp(P);

            // Generate temperature arrays
            const T = Array.from({length: 121}, (_, i) => 280 + i);

            // Calculate fugacities
            const fsat = T.map(t => calculateFugacityVsTemp(t));

            const layout = {
                paper_bgcolor: 'white',
                plot_bgcolor: 'white',
                xaxis: {
                    title: 'temperature (°C)',
                    range: [280, 400],
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: 'fugacity (bar)',
                    range: [0, 0.22],
                    gridcolor: '#e0e0e0'
                },
                font: {
                    family: 'sans-serif'
                },
                annotations: [
                    {text: 'liquid', x: 310, y: 0.01},
                    {text: 'vapor', x: 390, y: P},
                    {text: 'T<sup>sat</sup>', x: Tsat, y: P, yanchor: 'bottom'}
                ]
            };

            const data = [
                // Stable liquid
                {
                    x: T.filter(t => t <= Tsat),
                    y: fsat.filter((_, i) => T[i] <= Tsat),
                    mode: 'lines',
                    line: {color: '#0000FF'}
                },
                // Stable vapor
                {
                    x: T.filter(t => t >= Tsat),
                    y: Array(T.filter(t => t >= Tsat).length).fill(P),
                    mode: 'lines',
                    line: {color: '#0000FF'}
                },
                // Metastable liquid
                {
                    x: T.filter(t => t >= Tsat),
                    y: fsat.filter((_, i) => T[i] >= Tsat),
                    mode: 'lines',
                    line: {color: '#A0A0FF', dash: 'dash'}
                },
                // Metastable vapor
                {
                    x: T.filter(t => t <= Tsat),
                    y: Array(T.filter(t => t <= Tsat).length).fill(P),
                    mode: 'lines',
                    line: {color: '#A0A0FF', dash: 'dash'}
                },
                // Saturation point
                {
                    x: [Tsat],
                    y: [P],
                    mode: 'markers',
                    marker: {color: '#000000', size: 8}
                },
                // Indicator line
                {
                    x: [Tsat, Tsat],
                    y: [0, P],
                    mode: 'lines',
                    line: {color: '#000000', dash: 'dash'}
                }
            ];

            Plotly.react('plot-container', data, layout);
        }

        function plotFugacityVsPress() {
            const T = Number(sliderTemperature.value);
            const isReal = checkboxHighPressure.checked;
            const Psat = calculateSatPress(T);
            const fsat = calculateFugacityVsPress(Psat, T, isReal);

            // Generate pressure arrays
            const P = Array.from({length: 301}, (_, i) => i * 0.01);

            // Calculate fugacities
            const fvap = P.map(p => calculateFugacityVsPress(p, T, isReal));

            const layout = {
                paper_bgcolor: 'white',
                plot_bgcolor: 'white',
                xaxis: {
                    title: 'pressure (MPa)',
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: 'fugacity (MPa)',
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0'
                },
                font: {
                    family: 'sans-serif'
                },
                annotations: [
                    {text: 'vapor', x: 0.5, y: 0.25},
                    {text: 'liquid', x: 2.7, y: fsat},
                    {text: 'P<sup>sat</sup>', x: Psat, y: fsat, yanchor: 'bottom'}
                ]
            };

            const data = [
                // Stable vapor
                {
                    x: P.filter(p => p <= Psat),
                    y: fvap.filter((_, i) => P[i] <= Psat),
                    mode: 'lines',
                    line: {color: '#0000FF'}
                },
                // Stable liquid
                {
                    x: P.filter(p => p >= Psat),
                    y: Array(P.filter(p => p >= Psat).length).fill(fsat),
                    mode: 'lines',
                    line: {color: '#0000FF'}
                },
                // Metastable vapor
                {
                    x: P.filter(p => p >= Psat),
                    y: fvap.filter((_, i) => P[i] >= Psat),
                    mode: 'lines',
                    line: {color: '#A0A0FF', dash: 'dash'}
                },
                // Metastable liquid
                {
                    x: P.filter(p => p <= Psat),
                    y: Array(P.filter(p => p <= Psat).length).fill(fsat),
                    mode: 'lines',
                    line: {color: '#A0A0FF', dash: 'dash'}
                },
                // Saturation point
                {
                    x: [Psat],
                    y: [fsat],
                    mode: 'markers',
                    marker: {color: '#000000', size: 8}
                },
                // Indicator line
                {
                    x: [Psat, Psat],
                    y: [0, fsat],
                    mode: 'lines',
                    line: {color: '#000000', dash: 'dash'}
                }
            ];

            Plotly.react('plot-container', data, layout);
        }

