                let xi = (i/100) * sin2theta;
                x.push(xi);
                y.push(Math.pow(Math.sin(2*theta) - Math.sqrt(xi), 2));
            }
            return {x, y};
        }

        function calculateErrorCA() {
            const ax = R*Math.sin(theta);
            const ay = R*Math.cos(theta);
            return 0.5 * ((c.x-ax)*(c.x-ax) + (c.y-ay)*(c.y-ay)) / (R*R);
        }

        function calculateErrorCB() {
            const bx = -R*Math.sin(theta);
            const by = R*Math.cos(theta);
            // Simplified calculation for demo
            return 0.5 * ((c.x-bx)*(c.x-bx) + (c.y-by)*(c.y-by)) / (R*R);
        }

        function updateErrorPlot() {
            Plotly.react('plot-error', [{
                x: generateTradeoffCurve().x,
                y: generateTradeoffCurve().y,
