<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-points">collocation points</label>
                <input type="range" id="slider-points" min="5" max="30" step="1" value="20">
                <span id="label-points">20</span>
            </div>
            <div class="control-row">
                <label for="slider-time">t</label>
                <input type="range" id="slider-time" min="0.001" max="0.2" step="0.001" value="0.05">
                <span id="label-time">0.050</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        function analyticalSolution(x, t) {
            let T = 0;
            for (let n = 1; n <= 100; n++) {
                T += (1 / (n * n)) * Math.sin(n * Math.PI / 2) * 
                     Math.sin(n * Math.PI * x) * Math.exp(-2 * n * n * Math.PI * Math.PI * t);
            }
            return (8 / (Math.PI * Math.PI)) * T;
        }

        function generateAnalyticalCurve(t) {
            const x = Array.from({length: 201}, (_, i) => i / 200);
            const y = x.map(xi => analyticalSolution(xi, t));
            return {x, y};
        }

        function generateCollocationPoints(numPoints, t) {
            const N = numPoints - 1;
            const x = Array.from({length: numPoints}, (_, j) => 
                (Math.cos(j * Math.PI / N) + 1) / 2
            ).reverse();
            const y = x.map(xi => analyticalSolution(xi, t));
            return {x, y};
        }

        function updatePlot() {
            const numPoints = parseInt(document.getElementById('slider-points').value);
            const t = parseFloat(document.getElementById('slider-time').value);

            const analyticalData = generateAnalyticalCurve(t);
            const collocationData = generateCollocationPoints(numPoints, t);

            const traces = [
                {
                    x: analyticalData.x,
                    y: analyticalData.y,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 3 }
                },
                {
                    x: collocationData.x,
                    y: collocationData.y,
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: 'red', size: 8 }
                }
            ];

            const layout = {
                xaxis: {
                    title: 'x',
                    range: [0, 1],
                    tickmode: 'linear',
                    dtick: 0.2,
                    gridcolor: '#eee'
                },
                yaxis: {
                    title: 'T',
                    range: [-0.1, 1.1],
                    tickmode: 'linear',
                    dtick: 0.2,
                    gridcolor: '#eee'
                },
                showlegend: false,
                margin: {t: 20, r: 20, b: 40, l: 40}
            };

            Plotly.react('plot-container', traces, layout);
        }

        // Event listeners
        document.getElementById('slider-points').addEventListener('input', (e) => {
            document.getElementById('label-points').textContent = e.target.value;
            updatePlot();
        });

        document.getElementById('slider-time').addEventListener('input', (e) => {
            document.getElementById('label-time').textContent = 
                parseFloat(e.target.value).toFixed(3);
            updatePlot();
        });

        // Initial plot
        window.addEventListener('load', updatePlot);
    </script>
</body>
</html>
