            <h4>Now choose 'Steiner points' and how many you want to use.</h4>
            <div class="radio-group">
                <input type="radio" name="point-mode" id="radio-regular">
                <label for="radio-regular">regular points</label>
                <input type="radio" name="point-mode" id="radio-steiner" checked>
                <label for="radio-steiner">Steiner points</label>
            </div>
            <div id="steiner-points-selector" class="point-selector">
                <button id="btn-steiner-0">0</button>
                <button id="btn-steiner-1">1</button>
                <button id="btn-steiner-2" class="active">2</button>
                <button id="btn-steiner-3">3</button>
            </div>
            <p>You can drag them anywhere.</p>
            <p>Can you find an optimal position for the Steiner points that minimizes the total network length?</p>

            <hr>
            <div>
                <label for="checkbox-show-angles">show the angles</label>
                <input type="checkbox" id="checkbox-show-angles" checked>
            </div>
            <hr>
            <div>
                <p>The total length of the network is:</p>
                <h3 id="text-total-length">0.00000</h3>
            </div>
        </div>
        <div id="canvas-container">
            <div id="main-canvas"></div>
        </div>
    </div>

    <script>
        let numRegularPoints = 4;
        let numSteinerPoints = 2;
        let pointMode = 'steiner';
        let showAngles = true;
        let regularPoints = [];
        let steinerPoints = [];
        let selectedPoint = null;
        let mstEdges = [];
        let totalLength = 0;

        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('main-canvas');
            initializePoints();
            setupEventListeners();
        }

        function initializePoints() {
            regularPoints = [];
            steinerPoints = [];
            const centerX = width/2;
            const centerY = height/2;
            const radius = min(width, height) * 0.35;

            // Initialize regular points
            for(let i = 0; i < numRegularPoints; i++) {
                const angle = (TWO_PI * i) / numRegularPoints;
                regularPoints.push({
                    x: centerX + radius * cos(angle),
                    y: centerY + radius * sin(angle)
                });
            }

            // Initialize Steiner points
            for(let i = 0; i < numSteinerPoints; i++) {
                steinerPoints.push({
                    x: centerX,
                    y: centerY
                });
            }

            calculateMST();
        }

        function draw() {
            background('#EEEEEE');

            // Draw complete graph
            stroke('rgba(190, 160, 130, 0.5)');
            strokeWeight(1);
            drawCompleteGraph();

            // Draw MST
            stroke('#333333');
            strokeWeight(3);
            drawMST();

            // Draw points
            drawPoints();

            // Draw angles if enabled
            if(showAngles) {
                drawAngles();
            }
        }

        function drawCompleteGraph() {
            const allPoints = [...regularPoints, ...steinerPoints];
            for(let i = 0; i < allPoints.length; i++) {
                for(let j = i+1; j < allPoints.length; j++) {
                    line(allPoints[i].x, allPoints[i].y, allPoints[j].x, allPoints[j].y);
                }
            }
        }

        function drawMST() {
            for(const edge of mstEdges) {
                line(edge.p1.x, edge.p1.y, edge.p2.x, edge.p2.y);
            }
        }

        function drawPoints() {
            // Draw regular points
            stroke('#0000FF');
            fill('#FFFFFF');
            strokeWeight(2);
            for(const point of regularPoints) {
                circle(point.x, point.y, 16);
            }

            // Draw Steiner points
            stroke('#FF0000');
            for(const point of steinerPoints) {
                circle(point.x, point.y, 16);
                circle(point.x, point.y, 8);
            }
        }

        function mousePressed() {
            const allPoints = pointMode === 'regular' ? regularPoints : steinerPoints;
            for(let i = 0; i < allPoints.length; i++) {
                const point = allPoints[i];
                if(dist(mouseX, mouseY, point.x, point.y) < 8) {
                    selectedPoint = {index: i, point: point};
                    return;
                }
            }
        }

        function mouseDragged() {
            if(selectedPoint) {
                selectedPoint.point.x = mouseX;
                selectedPoint.point.y = mouseY;
                calculateMST();
            }
        }

        function mouseReleased() {
            selectedPoint = null;
        }

        function calculateMST() {
            // Kruskal's algorithm implementation would go here
            // For demo purposes, just connecting all points sequentially
            mstEdges = [];
            const allPoints = [...regularPoints, ...steinerPoints];
            totalLength = 0;

            for(let i = 0; i < allPoints.length-1; i++) {
                mstEdges.push({
                    p1: allPoints[i],
                    p2: allPoints[i+1]
                });
                totalLength += dist(allPoints[i].x, allPoints[i].y, 
                                 allPoints[i+1].x, allPoints[i+1].y);
            }

            document.getElementById('text-total-length').textContent = 
                totalLength.toFixed(5);
        }

        function setupEventListeners() {
            // Regular points buttons
            document.querySelectorAll('#regular-points-selector button').forEach(btn => {
                btn.addEventListener('click', (e) => {
                    numRegularPoints = parseInt(e.target.textContent);
                    document.querySelectorAll('#regular-points-selector button')
                        .forEach(b => b.classList.remove('active'));
                    e.target.classList.add('active');
                    initializePoints();
                });
            });

            // Steiner points buttons
            document.querySelectorAll('#steiner-points-selector button').forEach(btn => {
                btn.addEventListener('click', (e) => {
                    numSteinerPoints = parseInt(e.target.textContent);
                    document.querySelectorAll('#steiner-points-selector button')
                        .forEach(b => b.classList.remove('active'));
                    e.target.classList.add('active');
                    initializePoints();
                });
            });

            // Point mode radio buttons
            document.querySelectorAll('input[name="point-mode"]').forEach(radio => {
                radio.addEventListener('change', (e) => {
                    pointMode = e.target.id.split('-')[1];
                });
            });

            // Show angles checkbox
            document.getElementById('checkbox-show-angles').addEventListener('change', (e) => {
                showAngles = e.target.checked;
            });
        }

        function drawAngles() {
            textSize(12);
            textAlign(CENTER, CENTER);
            fill('#000000');
            noStroke();

            for(const point of steinerPoints) {
                // Calculate and display angles for each Steiner point
                // This is a simplified version - actual angle calculations would go here
                text("120°", point.x + 20, point.y - 20);
            }
        }
    </script>
