            <div class="slider-container">
                <label>Y</label>
                <input type="range" id="slider-mean-y" min="-10" max="10" step="0.1" value="0">
                <span id="value-mean-y">0.0</span>
            </div>
            <div class="slider-container">
                <label>Z</label>
                <input type="range" id="slider-mean-z" min="-10" max="10" step="0.1" value="0">
                <span id="value-mean-z">0.0</span>
            </div>

            <h3>Standard Deviations</h3>
            <div class="slider-container">
                <label>W</label>
                <input type="range" id="slider-sd-w" min="1" max="10" step="0.1" value="1">
                <span id="value-sd-w">1.0</span>
            </div>
            <div class="slider-container">
                <label>X</label>
                <input type="range" id="slider-sd-x" min="1" max="10" step="0.1" value="1">
                <span id="value-sd-x">1.0</span>
            </div>
            <div class="slider-container">
                <label>Y</label>
                <input type="range" id="slider-sd-y" min="1" max="10" step="0.1" value="1">
                <span id="value-sd-y">1.0</span>
            </div>
            <div class="slider-container">
                <label>Z</label>
                <input type="range" id="slider-sd-z" min="1" max="10" step="0.1" value="1">
                <span id="value-sd-z">1.0</span>
            </div>
        </div>
        <div id="visualization-panel"></div>
    </div>

    <script>
        let means = [0, 0, 0, 0];
        let sds = [1, 1, 1, 1];
        let canvas;
        const N = 10; // subjects per group
        const K = 4;  // number of groups

        function setup() {
            const visPanel = document.getElementById('visualization-panel');
            canvas = createCanvas(visPanel.clientWidth - 40, visPanel.clientHeight - 40);
            canvas.parent('visualization-panel');
            noLoop();

            // Add event listeners to all sliders
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', updateValues);
            });

            updateValues(); // Initial render
        }

        function updateValues() {
            means = [
                parseFloat(document.getElementById('slider-mean-w').value),
                parseFloat(document.getElementById('slider-mean-x').value),
                parseFloat(document.getElementById('slider-mean-y').value),
                parseFloat(document.getElementById('slider-mean-z').value)
            ];
            sds = [
                parseFloat(document.getElementById('slider-sd-w').value),
                parseFloat(document.getElementById('slider-sd-x').value),
                parseFloat(document.getElementById('slider-sd-y').value),
                parseFloat(document.getElementById('slider-sd-z').value)
            ];

            // Update display values
            means.forEach((mean, i) => {
                const group = ['w', 'x', 'y', 'z'][i];
                document.getElementById(`value-mean-${group}`).textContent = mean.toFixed(1);
            });
            sds.forEach((sd, i) => {
                const group = ['w', 'x', 'y', 'z'][i];
                document.getElementById(`value-sd-${group}`).textContent = sd.toFixed(1);
            });

            redraw();
        }

        function draw() {
            background(255);

            // Calculate ANOVA statistics
            const grandMean = means.reduce((a, b) => a + b) / K;
            const SS_between = N * means.reduce((sum, mean) => sum + Math.pow(mean - grandMean, 2), 0);
            const MS_between = SS_between / (K - 1);
            const SS_within = sds.reduce((sum, sd) => sum + (Math.pow(sd, 2) * (N - 1)), 0);
            const MS_within = SS_within / (N * K - K);
            const F_ratio = MS_between / MS_within;

            // Draw Group Data Plot
            const topMargin = 60;
            const plotHeight = (height - topMargin) / 2;

            // Draw axes
            stroke(0);
            line(50, topMargin, 50, topMargin + plotHeight); // y-axis
            line(50, topMargin + plotHeight/2, width-50, topMargin + plotHeight/2); // x-axis

            // Draw grand mean line
            stroke(0);
            setLineDash([5, 5]);
            const grandMeanY = map(grandMean, 10, -10, topMargin, topMargin + plotHeight);
            line(50, grandMeanY, width-50, grandMeanY);
            setLineDash([]);

            // Draw group bars
            const barWidth = (width - 100) / 4;
            means.forEach((mean, i) => {
                const x = 50 + i * barWidth + barWidth/4;
                const meanY = map(mean, 10, -10, topMargin, topMargin + plotHeight);
                const topY = map(mean + sds[i], 10, -10, topMargin, topMargin + plotHeight);
                const bottomY = map(mean - sds[i], 10, -10, topMargin, topMargin + plotHeight);

                fill('#F57F29');
                noStroke();
                rect(x, topY, barWidth/2, bottomY - topY);

                stroke(0);
                line(x, meanY, x + barWidth/2, meanY);

                // Group labels
                textAlign(CENTER);
                text(['W', 'X', 'Y', 'Z'][i], x + barWidth/4, topMargin + plotHeight + 20);
            });

            // Draw F-ratio text
            textAlign(CENTER);
            textSize(16);
            text(`F ratio = ${F_ratio.toFixed(4)}`, width/2, 30);

            // Draw ANOVA Results Plot
            const bottomPlotY = topMargin + plotHeight + 40;

            textAlign(LEFT);
            text("Analysis of the F-Ratio", 50, bottomPlotY);

            // Draw bars
            const maxWidth = width - 100;
            const barHeight = 30;
            const values = [MS_between, MS_within, F_ratio];
            const labels = ["MS Between", "MS Within", "F-Ratio"];
            const colors = ['#4691A5', '#D49D3C', '#8C3A8D'];

            values.forEach((value, i) => {
                const y = bottomPlotY + 30 + i * (barHeight + 10);
                const barW = min(map(value, 0, 200, 0, maxWidth), maxWidth);

                fill(colors[i]);
                noStroke();
                rect(50, y, barW, barHeight);

                fill(0);
                text(labels[i], 50, y - 5);
            });
        }

        function setLineDash(list) {
            drawingContext.setLineDash(list);
        }
    </script>
</body>
</html>
