    <script>
        // GEV PDF calculation function
        function gevPDF(x, mu, sigma, xi) {
            if (Math.abs(xi) < 1e-10) {
                // Gumbel case
                const z = (x - mu) / sigma;
                const t = Math.exp(-z);
                return (1/sigma) * t * Math.exp(-t);
            } else {
                const z = (x - mu) / sigma;
                const t = Math.pow(1 + xi * z, -1/xi);
                if (1 + xi * z <= 0) return 0;
                return (1/sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
            }
        }

        // Return level calculation function
        function returnLevel(p, mu, sigma, xi) {
            if (Math.abs(xi) < 1e-10) {
                return mu - sigma * Math.log(-Math.log(1 - 1/p));
            } else {
                return mu - (sigma/xi) * (1 - Math.pow(-Math.log(1 - 1/p), -xi));
            }
        }

        // Update all plots
        function updatePlots() {
            // Get current parameter values
            const mu1 = parseFloat(document.getElementById('slider-mu1').value);
            const sigma1 = parseFloat(document.getElementById('slider-sigma1').value);
            const xi1 = parseFloat(document.getElementById('slider-xi1').value);
            const mu2 = parseFloat(document.getElementById('slider-mu2').value);
            const sigma2 = parseFloat(document.getElementById('slider-sigma2').value);
            const xi2 = parseFloat(document.getElementById('slider-xi2').value);
            const pmax = parseFloat(document.getElementById('slider-pmax').value);

            // Update displays
            document.getElementById('display-mu1').value = mu1.toFixed(3);
            document.getElementById('display-sigma1').value = sigma1.toFixed(3);
            document.getElementById('display-xi1').value = xi1.toFixed(3);
            document.getElementById('display-mu2').value = mu2.toFixed(3);
            document.getElementById('display-sigma2').value = sigma2.toFixed(3);
            document.getElementById('display-xi2').value = xi2.toFixed(3);
            document.getElementById('display-pmax').value = pmax;

            // Generate PDF data
            const x = Array.from({length: 451}, (_, i) => 2.5 + i * 0.01);
            const y1 = x.map(x => gevPDF(x, mu1, sigma1, xi1));
            const y2 = x.map(x => gevPDF(x, mu2, sigma2, xi2));

            // Generate return level data
            const p = Array.from({length: 100}, (_, i) => 1 + i * (pmax-1)/99);
            const rl1 = p.map(p => returnLevel(p, mu1, sigma1, xi1));
            const rl2 = p.map(p => returnLevel(p, mu2, sigma2, xi2));

            // Update PDF plot
            Plotly.newPlot('plot-pdf', [
                {
                    x: x,
                    y: y1,
                    name: 'GEV 1',
                    line: {color: 'blue'},
                    fill: 'tozeroy',
                    fillcolor: 'rgba(0,0,255,0.2)'
                },
                {
                    x: x,
                    y: y2,
                    name: 'GEV 2',
                    line: {color: 'red'},
                    fill: 'tozeroy',
                    fillcolor: 'rgba(255,0,0,0.2)'
                }
            ], {
                title: 'PDFs of GEV Distributions 1 and 2',
                xaxis: {range: [2.5, 7]},
                showlegend: true
            });

            // Update return level plot
            Plotly.newPlot('plot-return-level', [
                {
                    x: p,
                    y: rl1,
                    name: 'GEV 1',
                    line: {color: 'blue'}
                },
                {
                    x: p,
                    y: rl2,
                    name: 'GEV 2',
                    line: {color: 'red'}
                }
            ], {
                xaxis: {
                    title: 'Return Period (years)',
                    range: [0, pmax]
                },
                yaxis: {
                    title: 'Maximum Sea Level (meters)'
                },
                showlegend: true
            });
        }

        // Add event listeners to all sliders
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', updatePlots);
        });

        // Initial plot
        updatePlots();
    </script>
</body>
