    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        #main-container {
            max-width: 1000px;
            margin: 0 auto;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            display: flex;
            flex-direction: column;
