            height: 400px;
            margin: 20px 0;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label>signal:</label>
                <button id="btn-rampdown">RampDown</button>
                <button id="btn-tophat">TopHat</button>
                <button id="btn-stairstep">StairStep</button>
                <button id="btn-triangle" class="active">Triangle</button>
                <button id="btn-uh">UH</button>
            </div>
            <div>
                <div>
                    <label for="slider-a">a:</label>
                    <input type="range" id="slider-a" min="0.1" max="4" step="0.1" value="2">
                    <span id="slider-a-value">2.0</span>
                </div>
                <div>
                    <label for="slider-b">b:</label>
                    <input type="range" id="slider-b" min="-10" max="10" step="0.1" value="4">
                    <span id="slider-b-value">4.0</span>
                </div>
                <div>
                    <label for="slider-t">T = \(\frac{b}{a}\):</label>
                    <input type="range" id="slider-t" min="-25" max="25" step="0.1" value="2">
                    <span id="slider-t-value">2.0</span>
                </div>
            </div>
            <div>
                <label>display:</label>
                <input type="checkbox" id="check-x" checked>
                <label for="check-x">\(x(t)\)</label>
                <input type="checkbox" id="check-y" checked>
                <label for="check-y">\(y(t) = x(at - b) = x(a(t-T))\)</label>
                <input type="checkbox" id="check-z" checked>
                <label for="check-z">\(z(t) = x(t-b)\)</label>
                <input type="checkbox" id="check-w">
                <label for="check-w">\(w(t) = x(at)\)</label>
            </div>
        </div>
        <div id="plot-top"></div>
        <div id="plot-bottom"></div>
    </div>

    <script>
        let currentSignal = 'triangle';
        let a = 2.0, b = 4.0, T = b/a;

        function generateSignalPoints(signal, tRange) {
            const points = [];
            for(let t = tRange[0]; t <= tRange[1]; t += 0.01) {
                let y = 0;
                switch(signal) {
                    case 'triangle':
                        if(t >= 0 && t < 3) y = (4/3)*t;
                        else if(t >= 3 && t <= 6) y = 4 - (4/3)*(t-3);
                        break;
                    case 'tophat':
                        if(t >= 1 && t <= 5) y = 4;
                        break;
                    case 'rampdown':
                        if(t >= 0 && t <= 6) y = 4 - (2/3)*t;
                        break;
                    case 'stairstep':
                        if(t >= 1 && t < 2) y = 1;
                        else if(t >= 2 && t <= 4) y = 4;
                        else if(t > 4 && t <= 5) y = 1;
                        break;
                }
                points.push([t, y]);
            }
            return points;
        }

        function transformSignal(points, transform) {
            return points.map(([t, y]) => {
                const newT = transform(t);
                return [t, y];
            });
        }

        function updatePlots() {
            const tRange = [-10, 20];
            const basePoints = generateSignalPoints(currentSignal, tRange);

            const traces = {
                x: {data: basePoints, color: 'rgb(70, 130, 180)', name: '\\(x(t)\\)'},
                y: {data: transformSignal(basePoints, t => a*t - b), color: 'red', name: '\\(y(t)\\)'},
                z: {data: transformSignal(basePoints, t => t - b), color: 'olivedrab', name: '\\(z(t)\\)'},
                w: {data: transformSignal(basePoints, t => a*t), color: 'saddlebrown', name: '\\(w(t)\\)'}
            };

            const layout = {
                xaxis: {title: 'time (s)', range: tRange, zeroline: true},
                yaxis: {range: [-0.5, 4.5], zeroline: true},
                showlegend: true,
                shapes: [
                    {type: 'circle', x0: b-0.1, x1: b+0.1, y0: -0.1, y1: 0.1, fillcolor: 'green'},
                    {type: 'circle', x0: T-0.1, x1: T+0.1, y0: -0.1, y1: 0.1, fillcolor: 'red'}
                ],
                annotations: [
                    {x: b, y: 0.2, text: 'b', showarrow: false, font: {color: 'green'}},
                    {x: T, y: 0.2, text: 'T', showarrow: false, font: {color: 'red'}}
                ]
            };

            // Top plot
            Plotly.newPlot('plot-top', [traces.x], {...layout, title: 'signal', legend: {x: 0.01, y: 0.75}});

            // Bottom plot
            const visibleTraces = [
                {...traces.x, visible: document.getElementById('check-x').checked},
                {...traces.y, visible: document.getElementById('check-y').checked},
                {...traces.z, visible: document.getElementById('check-z').checked},
                {...traces.w, visible: document.getElementById('check-w').checked}
            ];

            Plotly.newPlot('plot-bottom', visibleTraces, {...layout, legend: {x: 0.01, y: 0.98}});
        }

        // Event Listeners
        document.querySelectorAll('button').forEach(btn => {
            btn.addEventListener('click', e => {
                document.querySelector('button.active').classList.remove('active');
                e.target.classList.add('active');
                currentSignal = e.target.id.replace('btn-', '').toLowerCase();
                updatePlots();
            });
        });

        document.getElementById('slider-a').addEventListener('input', e => {
            a = parseFloat(e.target.value);
            T = b/a;
            document.getElementById('slider-a-value').textContent = a.toFixed(1);
            document.getElementById('slider-t').value = T;
            document.getElementById('slider-t-value').textContent = T.toFixed(1);
            updatePlots();
        });

        document.getElementById('slider-b').addEventListener('input', e => {
            b = parseFloat(e.target.value);
            T = b/a;
            document.getElementById('slider-b-value').textContent = b.toFixed(1);
            document.getElementById('slider-t').value = T;
            document.getElementById('slider-t-value').textContent = T.toFixed(1);
            updatePlots();
        });

        document.getElementById('slider-t').addEventListener('input', e => {
            T = parseFloat(e.target.value);
            b = a*T;
            document.getElementById('slider-t-value').textContent = T.toFixed(1);
            document.getElementById('slider-b').value = b;
            document.getElementById('slider-b-value').textContent = b.toFixed(1);
            updatePlots();
        });

        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', updatePlots);
        });

        // Initial plot
        window.addEventListener('load', () => {
            updatePlots();
            MathJax.typeset();
        });
    </script>
</body>
</html>
