    <title>Signal Encoding Demo</title>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <style>
        body { margin: 0; padding: 20px; font-family: Arial, sans-serif; }
        #main-container {
            background-color: #f0f0f0;
            padding: 20px;
            position: relative;
            max-width: 1200px;
            margin: 0 auto;
        }
        #info-button {
            position: absolute;
            top: 10px;
            right: 10px;
            width: 30px;
            height: 30px;
            border-radius: 50%;
            border: 2px solid #666;
            background: white;
            cursor: pointer;
        }
        #description-panel {
            background: white;
            padding: 15px;
            margin: 10px 0;
            border: 1px solid #ccc;
            border-radius: 5px;
        }
        #control-panel {
            display: flex;
            flex-direction: column;
            gap: 15px;
            margin-bottom: 20px;
        }
        .control-row {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        .control-row label {
            min-width: 100px;
        }
        input[type="range"] {
            width: 300px;
        }
        .button-group {
            display: flex;
            gap: 5px;
        }
        .button-group button {
            padding: 5px 15px;
            cursor: pointer;
        }
        .button-group button.active {
            background-color: #ccc;
            border-style: inset;
        }
        #canvas-container {
            background: white;
            border: 1px solid #ccc;
            padding: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <button id="info-button">+</button>
        <div id="description-panel" style="display: none;">
            <p>This demo shows the visualization of analog signals and their time-encoded representations using ASDM and IAF methods.</p>
        </div>
        <div id="control-panel">
            <div class="control-row">
                <label for="slider-start-time">start time</label>
                <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
                <span id="start-time-value">-1.0</span>
            </div>
            <div class="control-row">
                <label for="slider-total-time">total time</label>
                <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
                <span id="total-time-value">2.2</span>
            </div>
            <div class="control-row">
                <label>function</label>
                <div id="function-buttons" class="button-group">
                    <button id="btn-f">f</button>
                    <button id="btn-g" class="active">g</button>
                    <button id="btn-sin">sin</button>
                    <button id="btn-cos">cos</button>
                    <button id="btn-tan">tan</button>
                </div>
            </div>
            <div class="control-row">
                <label>encoding method</label>
                <div id="encoding-buttons" class="button-group">
                    <button id="btn-asdm" class="active">ASDM</button>
                    <button id="btn-iaf">IAF</button>
                </div>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let sketch = function(p) {
            let startTime = -1.0;
            let totalTime = 2.2;
            let currentFunction = 'g';
            let encodingMethod = 'ASDM';

            const margin = 50;
            let width = 800;
            let height = 400;

            p.setup = function() {
                p.createCanvas(width, height);
                p.noLoop();
            };

            function evaluateFunction(t) {
                switch(currentFunction) {
                    case 'f': return 0.5 * t;
                    case 'g': return p.sin(2 * p.PI * t) + 0.3 * p.cos(3 * 2 * p.PI * t);
                    case 'sin': return p.sin(2 * p.PI * t);
                    case 'cos': return p.cos(2 * p.PI * t);
                    case 'tan': return p.tan(p.PI * t);
                }
            }

            function drawAxes() {
                p.stroke(0);
                p.line(margin, height/2, width-margin, height/2); // x-axis
                p.line(margin, margin, margin, height-margin); // y-axis

                // Draw ticks and labels
                for(let x = Math.ceil(startTime); x <= startTime + totalTime; x += 0.5) {
                    let screenX = p.map(x, startTime, startTime + totalTime, margin, width-margin);
                    p.line(screenX, height/2-5, screenX, height/2+5);
                    p.text(x.toFixed(1), screenX-10, height/2+20);
                }

                for(let y = -1; y <= 1; y += 0.5) {
                    let screenY = p.map(y, -1.2, 1.2, height-margin, margin);
                    p.line(margin-5, screenY, margin+5, screenY);
                    p.text(y.toFixed(1), margin-30, screenY+5);
                }
            }

            function drawAnalogSignal() {
                p.stroke(0, 0, 255);
                p.noFill();
                p.beginShape();
                for(let x = 0; x <= width-2*margin; x++) {
                    let t = p.map(x, 0, width-2*margin, startTime, startTime + totalTime);
                    let y = evaluateFunction(t);
                    if(Math.abs(y) < 100) { // Skip drawing near asymptotes
                        let screenY = p.map(y, -1.2, 1.2, height-margin, margin);
                        p.vertex(x + margin, screenY);
                    }
                }
                p.endShape();
            }

            function simulateASDM() {
                const B = 0.25;
                const delta = 0.05;
                const dt = 0.001;
                let outputLevel = B;
                let integral = 0;
                let flipTimes = [];

                for(let t = startTime; t <= startTime + totalTime; t += dt) {
                    integral += (evaluateFunction(t) - outputLevel) * dt;
                    if((outputLevel === B && integral < -delta) || 
                       (outputLevel === -B && integral > delta)) {
                        flipTimes.push(t);
                        outputLevel = -outputLevel;
                        integral = 0;
                    }
                }
                return flipTimes;
            }

            function simulateIAF() {
                const theta = 0.05;
                const dt = 0.001;
                let integral = 0;
                let spikeTimes = [];

                for(let t = startTime; t <= startTime + totalTime; t += dt) {
                    integral += evaluateFunction(t) * dt;
                    if(integral >= theta) {
                        spikeTimes.push(t);
                        integral = 0;
                    }
                }
                return spikeTimes;
            }

            function drawEncodedSignal() {
                p.stroke(255, 0, 0);
                if(encodingMethod === 'ASDM') {
                    let flipTimes = simulateASDM();
                    let outputLevel = 0.25;
                    let lastX = p.map(startTime, startTime, startTime + totalTime, margin, width-margin);
                    let lastY = p.map(outputLevel, -1.2, 1.2, height-margin, margin);

                    flipTimes.forEach(t => {
                        let x = p.map(t, startTime, startTime + totalTime, margin, width-margin);
                        p.line(lastX, lastY, x, lastY);
                        outputLevel = -outputLevel;
                        let y = p.map(outputLevel, -1.2, 1.2, height-margin, margin);
                        p.line(x, lastY, x, y);
                        lastX = x;
                        lastY = y;
                    });

                    p.line(lastX, lastY, width-margin, lastY);
                } else {
                    let spikeTimes = simulateIAF();
                    const spikeHeight = 0.3;
                    spikeTimes.forEach(t => {
                        let x = p.map(t, startTime, startTime + totalTime, margin, width-margin);
                        let y = p.map(spikeHeight, -1.2, 1.2, height-margin, margin);
                        p.line(x, height/2, x, y);
                        p.fill(255, 0, 0);
                        p.ellipse(x, y, 5, 5);
                    });
                }
            }

            p.draw = function() {
                p.background(255);
                drawAxes();
                drawAnalogSignal();
                drawEncodedSignal();
            };

            // Expose update methods
            window.updateSketch = function(newStartTime, newTotalTime, newFunction, newEncoding) {
                startTime = newStartTime;
                totalTime = newTotalTime;
                currentFunction = newFunction;
                encodingMethod = newEncoding;
                p.redraw();
            };
        };

        new p5(sketch, 'canvas-container');

        // Event listeners
        document.getElementById('info-button').onclick = function() {
            const panel = document.getElementById('description-panel');
            panel.style.display = panel.style.display === 'none' ? 'block' : 'none';
        };

        document.getElementById('slider-start-time').oninput = function() {
            document.getElementById('start-time-value').textContent = this.value;
            updateVisualization();
        };

        document.getElementById('slider-total-time').oninput = function() {
            document.getElementById('total-time-value').textContent = this.value;
            updateVisualization();
        };

        function updateVisualization() {
            const startTime = parseFloat(document.getElementById('slider-start-time').value);
            const totalTime = parseFloat(document.getElementById('slider-total-time').value);
            const functionButtons = document.getElementById('function-buttons').getElementsByTagName('button');
            const encodingButtons = document.getElementById('encoding-buttons').getElementsByTagName('button');

            let currentFunction = Array.from(functionButtons).find(b => b.classList.contains('active')).id.slice(4);
            let encodingMethod = Array.from(encodingButtons).find(b => b.classList.contains('active')).id.slice(4).toUpperCase();

            window.updateSketch(startTime, totalTime, currentFunction, encodingMethod);
        }

        // Button click handlers
        document.querySelectorAll('#function-buttons button').forEach(button => {
            button.onclick = function() {
                document.querySelectorAll('#function-buttons button').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                updateVisualization();
            };
        });

        document.querySelectorAll('#encoding-buttons button').forEach(button => {
            button.onclick = function() {
                document.querySelectorAll('#encoding-buttons button').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                updateVisualization();
            };
        });
    </script>
</body>
</html>
```

