    <script>
        // Initialize Three.js scene, renderer, cameras
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xE0E0E0);

        const container = document.getElementById('visualization-container');
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(container.clientWidth, container.clientHeight);
        container.appendChild(renderer.domElement);

        // Setup cameras
        const perspectiveCamera = new THREE.PerspectiveCamera(75, container.clientWidth / container.clientHeight, 0.1, 1000);
        perspectiveCamera.position.set(10, 10, 10);
        perspectiveCamera.lookAt(0, 0, 0);

        const orthoSize = 12;
        const aspect = container.clientWidth / container.clientHeight;
        const orthographicCamera = new THREE.OrthographicCamera(
            -orthoSize * aspect, orthoSize * aspect,
            orthoSize, -orthoSize,
            0.1, 1000
        );
        orthographicCamera.position.set(0, 0, 10);
        orthographicCamera.lookAt(0, 0, 0);

        let activeCamera = orthographicCamera;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1);
        scene.add(directionalLight);

        // Animation state
        let isPlaying = false;
        let time = 0;

        // Wave parameters
        const state = {
            pWaveType: 'sine',
            sWaveType: 'sine',
            sWaveDirection: 'xy'
        };

        // Create grid mesh
        function createGrid(segments) {
            const geometry = new THREE.PlaneGeometry(10, 10, segments, segments);
            const material = new THREE.MeshLambertMaterial({ 
                vertexColors: true,
                side: THREE.DoubleSide
            });
            const mesh = new THREE.Mesh(geometry, material);

            const wireframe = new THREE.LineSegments(
                new THREE.WireframeGeometry(geometry),
                new THREE.LineBasicMaterial({ color: 0x556B2F })
            );

            return { mesh, wireframe };
        }

        let { mesh, wireframe } = createGrid(40);
        scene.add(mesh);
        scene.add(wireframe);

        // Wave functions
        function sineWave(u) {
            return Math.sin(2 * Math.PI * u);
        }

        function gaussianPulse(u) {
            return Math.exp(-u * u * 4);
        }

        // Update vertex positions and colors
        function updateGeometry() {
            const positions = mesh.geometry.attributes.position;
            const colors = mesh.geometry.attributes.color;

            for(let i = 0; i < positions.count; i++) {
                const x = positions.getX(i);
                const y = positions.getY(i);
                const r = Math.sqrt(x*x + y*y);

                // Calculate wave displacements
                const pAmp = document.getElementById('p-wave-amplitude-slider').value;
                const pWL = document.getElementById('p-wave-wavelength-slider').value;
                const sAmp = document.getElementById('s-wave-amplitude-slider').value;
                const sWL = document.getElementById('s-wave-wavelength-slider').value;

                const pWaveFn = state.pWaveType === 'sine' ? sineWave : gaussianPulse;
                const sWaveFn = state.sWaveType === 'sine' ? sineWave : gaussianPulse;

                const pPhase = (r / pWL) - time;
                const sPhase = (r / sWL) - time;

                let displacement = new THREE.Vector3(0, 0, 0);

                // P-wave
                if(r > 0) {
                    const pDisp = pAmp * pWaveFn(pPhase);
                    displacement.x += (x/r) * pDisp;
                    displacement.y += (y/r) * pDisp;
                }

                // S-wave
                if(r > 0) {
                    const sDisp = sAmp * sWaveFn(sPhase);
                    if(state.sWaveDirection === 'xy') {
                        displacement.x += (-y/r) * sDisp;
                        displacement.y += (x/r) * sDisp;
                    } else {
                        displacement.z += sDisp;
                    }
                }

                positions.setXYZ(i,
                    x + displacement.x,
                    y + displacement.y,
                    displacement.z
                );

                // Color based on z displacement
                const z = displacement.z;
                if(z > 0) {
                    colors.setXYZ(i, 1, 1-z*2, 1-z*2); // Red
                } else {
                    colors.setXYZ(i, 1+z*2, 1+z*2, 1); // Blue
                }
            }

            positions.needsUpdate = true;
            colors.needsUpdate = true;

            wireframe.geometry = new THREE.WireframeGeometry(mesh.geometry);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);

            if(isPlaying) {
                time += 0.1;
                document.getElementById('time-slider').value = time;
            }

            updateGeometry();
            renderer.render(scene, activeCamera);
        }

        // Event listeners
        document.getElementById('play-pause-button').addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '❚❚' : '►';
        });

        document.getElementById('reset-button').addEventListener('click', function() {
            time = 0;
            isPlaying = false;
            document.getElementById('time-slider').value = 0;
            document.getElementById('play-pause-button').textContent = '►';
        });

        document.getElementById('step-button').addEventListener('click', function() {
            time += 0.1;
            document.getElementById('time-slider').value = time;
        });

        document.getElementById('time-slider').addEventListener('input', function() {
            time = parseFloat(this.value);
        });

        document.getElementById('plot-points-slider').addEventListener('input', function() {
            scene.remove(mesh);
            scene.remove(wireframe);
            ({ mesh, wireframe } = createGrid(parseInt(this.value)));
            scene.add(mesh);
            scene.add(wireframe);
        });

        // Wave type buttons
        document.getElementById('p-wave-sine-button').addEventListener('click', function() {
            state.pWaveType = 'sine';
            this.classList.add('active');
            document.getElementById('p-wave-pulse-button').classList.remove('active');
        });

        document.getElementById('p-wave-pulse-button').addEventListener('click', function() {
            state.pWaveType = 'pulse';
            this.classList.add('active');
            document.getElementById('p-wave-sine-button').classList.remove('active');
        });

        document.getElementById('s-wave-sine-button').addEventListener('click', function() {
            state.sWaveType = 'sine';
            this.classList.add('active');
            document.getElementById('s-wave-pulse-button').classList.remove('active');
        });

        document.getElementById('s-wave-pulse-button').addEventListener('click', function() {
            state.sWaveType = 'pulse';
            this.classList.add('active');
            document.getElementById('s-wave-sine-button').classList.remove('active');
        });

        // S-wave direction buttons
        document.getElementById('s-wave-dir-xy-button').addEventListener('click', function() {
            state.sWaveDirection = 'xy';
            this.classList.add('active');
            document.getElementById('s-wave-dir-z-button').classList.remove('active');
        });

        document.getElementById('s-wave-dir-z-button').addEventListener('click', function() {
            state.sWaveDirection = 'z';
            this.classList.add('active');
            document.getElementById('s-wave-dir-xy-button').classList.remove('active');
        });

        // Viewpoint buttons
        document.getElementById('viewpoint-top-button').addEventListener('click', function() {
            activeCamera = orthographicCamera;
            this.classList.add('active');
            document.getElementById('viewpoint-default-button').classList.remove('active');
        });

        document.getElementById('viewpoint-default-button').addEventListener('click', function() {
            activeCamera = perspectiveCamera;
            this.classList.add('active');
            document.getElementById('viewpoint-top-button').classList.remove('active');
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            const width = container.clientWidth;
            const height = container.clientHeight;

            renderer.setSize(width, height);

            perspectiveCamera.aspect = width / height;
            perspectiveCamera.updateProjectionMatrix();

            const aspect = width / height;
            orthographicCamera.left = -orthoSize * aspect;
            orthographicCamera.right = orthoSize * aspect;
            orthographicCamera.updateProjectionMatrix();
        });

        // Start animation
        animate();
    </script>
</body>
</html>
