                <label for="check-show-circle">show circle</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-axes">
                <label for="check-show-axes">show axes</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-grid-lines">
                <label for="check-show-grid-lines">show grid lines</label>
            </div>
            <button id="btn-new-example">new example</button>
            <button id="btn-golden-ratio">golden ratio</button>
        </div>

        <div id="visualization-area">
            <div id="canvas-container"></div>
            <div id="formula-display">P(z) = <span id="formula-text">z^2 - z - 1</span></div>
            <table id="results-table">
                <thead>
                    <tr>
                        <th>P(z)</th>
                        <th>|L<sub>3</sub> L'<sub>2</sub>|</th>
                        <th>z</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td id="value-pz">-0.25</td>
                        <td id="value-l3l2">0.25</td>
                        <td id="value-z">1.5</td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>

    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        let a = 1, b = -1, c = -1;  // Initial coefficients
        let z = 1.5;                 // Initial z value
        let plotRange = 4;           // Initial plot range
        let showLabels = false;
        let showCircle = false;
        let showAxes = false;
        let showGridLines = false;

        // P5.js sketch
        let sketch = function(p) {
            let canvas;

            p.setup = function() {
                canvas = p.createCanvas(
                    document.getElementById('canvas-container').offsetWidth,
                    document.getElementById('canvas-container').offsetHeight
                );
                canvas.parent('canvas-container');
                p.noLoop();
                update();
            };

            p.draw = function() {
                p.background(255);
                p.push();

                // Transform coordinate system
                let scale = Math.min(p.width, p.height) / (2 * plotRange);
                p.translate(p.width/2, p.height/2);
                p.scale(scale, -scale);

                if (showGridLines) drawGrid();
                if (showAxes) drawAxes();

                // Draw primary path
                drawPrimaryPath();

                // Draw test path
                drawTestPath();

                if (showCircle) drawSolutionCircle();
                if (showLabels) drawLabels();

                p.pop();
            };

            function drawGrid() {
                p.stroke(200);
                p.strokeWeight(1/scale);
                for(let i = -plotRange; i <= plotRange; i++) {
                    p.line(i, -plotRange, i, plotRange);
                    p.line(-plotRange, i, plotRange, i);
                }
            }

            function drawAxes() {
                p.stroke(0);
                p.strokeWeight(2/scale);
                p.line(-plotRange, 0, plotRange, 0);
                p.line(0, -plotRange, 0, plotRange);
            }

            function drawPrimaryPath() {
                let O = p.createVector(0, 0);
                let L1 = p.createVector(a, 0);
                let L2 = p.createVector(a, b);
                let L3 = p.createVector(a-c, b);

                p.stroke(0);
                p.strokeWeight(2/scale);
                p.line(O.x, O.y, L1.x, L1.y);
                p.line(L1.x, L1.y, L2.x, L2.y);
                p.line(L2.x, L2.y, L3.x, L3.y);

                drawPoint(O);
                drawPoint(L1);
                drawPoint(L2);
                drawPoint(L3);
            }

            function drawTestPath() {
                let O = p.createVector(0, 0);
                let L1p = p.createVector(a, -z*a);
                let L2p = p.createVector(a + b*z + a*z*z, b);
                let L3 = p.createVector(a-c, b);

                p.stroke(0);
                p.line(O.x, O.y, L1p.x, L1p.y);

                p.stroke(255, 0, 255);
                p.line(L1p.x, L1p.y, L2p.x, L2p.y);

                p.stroke(255, 0, 0);
                p.line(L2p.x, L2p.y, L3.x, L3.y);

                drawPoint(L1p);
                drawPoint(L2p);
            }

            function drawPoint(v) {
                p.fill(0);
                p.noStroke();
                p.circle(v.x, v.y, 0.1);
            }

            function drawSolutionCircle() {
                let center = p.createVector((a-c)/2, b/2);
                let diameter = p.sqrt((a-c)*(a-c) + b*b);
                p.noFill();
                p.stroke(0);
                p.circle(center.x, center.y, diameter);
            }

            function drawLabels() {
                p.push();
                p.scale(1/scale, -1/scale);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(12);
                p.fill(0);
                p.noStroke();

                let points = {
                    'O': [0, 0],
                    'L1': [a, 0],
                    'L2': [a, b],
                    'L3': [a-c, b],
                    "L1'": [a, -z*a],
                    "L2'": [a + b*z + a*z*z, b]
                };

                for(let [label, pos] of Object.entries(points)) {
                    p.text(label, pos[0]*scale, -pos[1]*scale - 15);
                }
                p.pop();
            }
        };

        function update() {
            // Update display values
            document.getElementById('label-z').textContent = z.toFixed(2);
            document.getElementById('label-plot-range').textContent = plotRange.toFixed(1);

            // Calculate P(z)
            let pz = a*z*z + b*z + c;
            let l3l2 = Math.abs(pz);

            // Update results table
            document.getElementById('value-pz').textContent = pz.toFixed(4);
            document.getElementById('value-l3l2').textContent = l3l2.toFixed(4);
            document.getElementById('value-z').textContent = z.toFixed(4);

            // Redraw canvas
            if(window.myp5) window.myp5.redraw();
        }

        function setGoldenRatio() {
            a = 1;
            b = -1;
            c = -1;
            document.getElementById('formula-text').textContent = 'z^2 - z - 1';
            MathJax.typesetPromise();
            update();
        }

        function setNewExample() {
            let possible_a = [-3,-2,-1,1,2,3];
            a = possible_a[Math.floor(Math.random() * possible_a.length)];
            b = Math.floor(Math.random() * 11) - 5;
            c = Math.floor(Math.random() * 11) - 5;

            document.getElementById('formula-text').textContent = 
                `${a}z^2 ${b>=0?'+':''}${b}z ${c>=0?'+':''}${c}`;
            MathJax.typesetPromise();
            update();
        }

        // Event listeners
        document.getElementById('slider-z').addEventListener('input', function(e) {
            z = parseFloat(e.target.value);
            update();
        });

        document.getElementById('slider-plot-range').addEventListener('input', function(e) {
            plotRange = parseFloat(e.target.value);
            update();
        });

        document.getElementById('check-show-labels').addEventListener('change', function(e) {
            showLabels = e.target.checked;
            update();
        });

        document.getElementById('check-show-circle').addEventListener('change', function(e) {
            showCircle = e.target.checked;
            update();
        });

        document.getElementById('check-show-axes').addEventListener('change', function(e) {
            showAxes = e.target.checked;
            update();
        });

        document.getElementById('check-show-grid-lines').addEventListener('change', function(e) {
            showGridLines = e.target.checked;
            update();
        });

        document.getElementById('btn-new-example').addEventListener('click', setNewExample);
        document.getElementById('btn-golden-ratio').addEventListener('click', setGoldenRatio);

        // Initialize p5.js sketch
        window.myp5 = new p5(sketch);
    </script>
</body>
</html>
