    <script>
        let state = {
            spiralType: 'double',
            itemType: 'point',
            spiralGrowth: 0.15,
            z0_loc: {x: 0, y: 0},
            z_plus_loc: {x: -100, y: 0},
            z_minus_loc: {x: 100, y: 0},
            dragging: null
        };

        const fox_vertices = [
            {x:-15, y:0}, {x:-10, y:-5}, {x:-5, y:-4}, {x:0, y:-5}, 
            {x:5, y:-5}, {x:8, y:-2}, {x:10, y:-6}, {x:12, y:-6},
            {x:15, y:0}, {x:12, y:6}, {x:10, y:6}, {x:8, y:2},
            {x:5, y:5}, {x:0, y:5}, {x:-5, y:4}, {x:-10, y:5}
        ];

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('p5-canvas');

            setupEventListeners();
        }

        function draw() {
            background(255);
            translate(width/2, height/2);

            if(state.spiralType === 'single') {
                drawSingleSpiral();
            } else {
                drawDoubleSpiral();
            }

            drawLocators();
        }

        function drawSingleSpiral() {
            beginShape();
            noFill();
            stroke('#4A6DA8');
            strokeWeight(2);

            for(let t = -8*PI; t <= 8*PI; t += 0.1) {
                const r = exp(state.spiralGrowth * t);
                const x = state.z0_loc.x + r * cos(t);
                const y = state.z0_loc.y + r * sin(t);
                curveVertex(x, y);

                if(state.itemType !== 'none' && t % 1 < 0.1) {
                    drawItem(x, y, t);
                }
            }
            endShape();
        }

        function drawDoubleSpiral() {
            // Similar implementation for double spiral with Möbius transformation
            // Omitted for brevity
        }

        function drawItem(x, y, t) {
            push();
            if(state.itemType === 'point') {
                fill('#CB3335');
                noStroke();
                circle(x, y, 5);
            } else if(state.itemType.includes('fox')) {
                translate(x, y);
                rotate(t + PI/2);
                if(state.itemType === 'fox') {
                    noFill();
                    stroke('#D6292D');
                    strokeWeight(1);
                } else {
                    fill('#D6292D');
                    noStroke();
                }
                drawFox();
            }
            pop();
        }

        function drawFox() {
            beginShape();
            for(const v of fox_vertices) {
                vertex(v.x, v.y);
            }
            endShape(CLOSE);
        }

        function drawLocators() {
            // Draw draggable locators with crosshairs
            function drawLocator(x, y, color) {
                push();
                fill(color);
                stroke(0);
                circle(x, y, 20);
                line(x-10, y, x+10, y);
                line(x, y-10, x, y+10);
                pop();
            }

            if(state.spiralType === 'single') {
                drawLocator(state.z0_loc.x, state.z0_loc.y, 'rgba(255,255,0,0.8)');
            } else {
                drawLocator(state.z_plus_loc.x, state.z_plus_loc.y, 'rgba(255,255,0,0.8)');
                drawLocator(state.z_minus_loc.x, state.z_minus_loc.y, 'rgba(0,255,0,0.8)');
            }
        }

        function setupEventListeners() {
            // Setup button click handlers
            document.querySelectorAll('.button-group button').forEach(btn => {
                btn.addEventListener('click', function() {
                    const group = this.parentElement;
                    group.querySelectorAll('button').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');

                    if(this.id.includes('type-')) {
                        state.spiralType = this.id.replace('btn-type-', '');
                    } else if(this.id.includes('item-')) {
                        state.itemType = this.id.replace('btn-item-', '');
                    }
                });
            });

            // Setup slider
            const slider = document.getElementById('slider-growth');
            const display = document.getElementById('display-growth');

            slider.addEventListener('input', function() {
                state.spiralGrowth = parseFloat(this.value);
                display.textContent = state.spiralGrowth.toFixed(4);
            });

            document.getElementById('btn-reset-growth').addEventListener('click', function() {
                state.spiralGrowth = 0.25;
                slider.value = 0.25;
                display.textContent = '0.2500';
            });
        }

    </script>
</body>
