<!DOCTYPE html>
<html>
<head>
    <title>Polyhedron Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #app-container {
            display: flex;
            height: 100vh;
        }
        #controls-panel {
            width: 320px;
            padding: 20px;
            background: #f5f5f5;
            border-right: 1px solid #ddd;
            overflow-y: auto;
        }
        #canvas-container {
            flex-grow: 1;
        }
        label {
            display: block;
            margin: 15px 0 5px;
            font-weight: bold;
        }
        input[type="range"] {
            width: 100%;
            margin: 5px 0 15px;
        }
        #polyhedron-buttons {
            display: grid;
            gap: 8px;
            margin: 10px 0;
        }
        #polyhedron-buttons button {
            padding: 8px;
            border: 1px solid #ccc;
            background: #fff;
            cursor: pointer;
            border-radius: 4px;
        }
        #polyhedron-buttons button.active {
            background: #4CAF50;
            color: white;
            border-color: #45a049;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="controls-panel">
            <label for="slider-sphere-diameter">diameter of sphere</label>
            <input type="range" id="slider-sphere-diameter" min="0.5" max="3.0" step="0.01" value="1.5">

            <label>polyhedron vertices</label>
            <div id="polyhedron-buttons">
                <button id="btn-cube">Cube</button>
                <button id="btn-dodecahedron">Dodecahedron</button>
                <button id="btn-icosahedron">Icosahedron</button>
                <button id="btn-octahedron" class="active">Octahedron</button>
                <button id="btn-tetrahedron">Tetrahedron</button>
            </div>

            <label for="slider-cone-height">height</label>
            <input type="range" id="slider-cone-height" min="0.1" max="4.0" step="0.01" value="1.5">

            <label for="slider-cone-base">base</label>
            <input type="range" id="slider-cone-base" min="0.05" max="1.0" step="0.01" value="0.4">
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        let scene, camera, renderer, controls;
        let sphere, cones = [];
        let currentPolyhedron = 'octahedron';

        // Initial parameters
        const params = {
            sphereDiameter: 1.5,
            coneHeight: 1.5,
            coneBase: 0.4
        };

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(0, 0, 8);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(document.getElementById('canvas-container').clientWidth, 
                           document.getElementById('canvas-container').clientHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const light1 = new THREE.DirectionalLight(0xffffff, 0.8);
            light1.position.set(1, 1, 1);
            scene.add(light1);

            const light2 = new THREE.DirectionalLight(0xffffff, 0.4);
            light2.position.set(-1, -1, -1);
            scene.add(light2);

            // Create sphere
            createSphere();

            // Create initial polyhedron cones
            updatePolyhedron('octahedron');

            // Animation loop
            animate();

            // Event listeners
            window.addEventListener('resize', onWindowResize);
            setupEventListeners();
        }

        function createSphere() {
            const geometry = new THREE.SphereGeometry(params.sphereDiameter/2, 32, 32);
            const material = new THREE.MeshPhongMaterial({
                color: 0xdddddd,
                shininess: 50
            });
            sphere = new THREE.Mesh(geometry, material);
            scene.add(sphere);
        }

        function getVertices(type) {
            let geometry;
            switch(type) {
                case 'tetrahedron':
                    geometry = new THREE.TetrahedronGeometry(1);
                    break;
                case 'cube':
                    geometry = new THREE.BoxGeometry(1, 1, 1);
                    break;
                case 'octahedron':
                    geometry = new THREE.OctahedronGeometry(1);
                    break;
                case 'dodecahedron':
                    geometry = new THREE.DodecahedronGeometry(1);
                    break;
                case 'icosahedron':
                    geometry = new THREE.IcosahedronGeometry(1);
                    break;
            }
            return geometry.attributes.position;
        }

        function createCones(vertices) {
            // Remove existing cones
            cones.forEach(cone => scene.remove(cone));
            cones = [];

            const coneGeometry = new THREE.ConeGeometry(params.coneBase, params.coneHeight, 32);
            const coneMaterial = new THREE.MeshPhongMaterial({
                color: 0xffa500,
                shininess: 30
            });

            for(let i = 0; i < vertices.count; i++) {
                const direction = new THREE.Vector3(
                    vertices.array[i * 3],
                    vertices.array[i * 3 + 1],
                    vertices.array[i * 3 + 2]
                ).normalize();

                const cone = new THREE.Mesh(coneGeometry, coneMaterial);
                const position = direction.clone().multiplyScalar(params.sphereDiameter/2 + params.coneHeight/2);
                cone.position.copy(position);

                cone.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction);
                scene.add(cone);
                cones.push(cone);
            }
        }

        function updatePolyhedron(type) {
            currentPolyhedron = type;
            const vertices = getVertices(type);
            createCones(vertices);
        }

        function setupEventListeners() {
            document.getElementById('slider-sphere-diameter').addEventListener('input', (e) => {
                params.sphereDiameter = parseFloat(e.target.value);
                sphere.scale.setScalar(params.sphereDiameter/1.5);
                updatePolyhedron(currentPolyhedron);
            });

            document.getElementById('slider-cone-height').addEventListener('input', (e) => {
                params.coneHeight = parseFloat(e.target.value);
                updatePolyhedron(currentPolyhedron);
            });

            document.getElementById('slider-cone-base').addEventListener('input', (e) => {
                params.coneBase = parseFloat(e.target.value);
                updatePolyhedron(currentPolyhedron);
            });

            const buttons = document.querySelectorAll('#polyhedron-buttons button');
            buttons.forEach(button => {
                button.addEventListener('click', (e) => {
                    buttons.forEach(b => b.classList.remove('active'));
                    e.target.classList.add('active');
                    const type = e.target.id.replace('btn-', '');
                    updatePolyhedron(type);
                });
            });
        }

        function onWindowResize() {
            camera.aspect = document.getElementById('canvas-container').clientWidth / 
                           document.getElementById('canvas-container').clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(document.getElementById('canvas-container').clientWidth,
                           document.getElementById('canvas-container').clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        init();
    </script>
</body>
</html>
