<head>
    <title>Analemma Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        #main-container {
            display: flex;
            height: 100vh;
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            width: 300px;
            padding: 20px;
            background-color: #f0f0f0;
            display: flex;
            flex-direction: column;
            gap: 15px;
        }
        #visualization-area {
            flex-grow: 1;
            padding: 20px;
        }
        .slider-container {
            display: flex;
            flex-direction: column;
            gap: 5px;
        }
        input[type="range"] {
            width: 100%;
        }
        input[type="range"]:disabled {
            opacity: 0.5;
        }
        label {
            font-size: 14px;
        }
        #select-planet {
            padding: 5px;
            margin-bottom: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="select-planet">Planet</label>
            <select id="select-planet">
                <option value="manual" selected>-- choose orbit manually --</option>
                <option value="Earth">Earth</option>
                <option value="Mars">Mars</option>
            </select>

            <div id="manual-controls">
                <div>
                    <input type="checkbox" id="checkbox-manual" checked>
                    <label for="checkbox-manual">choose orbit manually</label>
                </div>

                <div class="slider-container">
                    <label for="slider-tilt">axis angle relative to ecliptic</label>
                    <input type="range" id="slider-tilt" min="0" max="90" value="23.5" step="0.1">
                </div>

                <div class="slider-container">
                    <label for="slider-equinox">spring equinox point</label>
                    <input type="range" id="slider-equinox" min="-180" max="180" value="0" step="1">
                </div>

                <div class="slider-container">
                    <label for="slider-eccentricity">orbit eccentricity</label>
                    <input type="range" id="slider-eccentricity" min="0" max="0.5" value="0.1" step="0.001">
                </div>
            </div>

            <div id="display-controls">
                <div>
                    <input type="checkbox" id="checkbox-snapshots" checked>
                    <label for="checkbox-snapshots">show sun snapshots</label>
                </div>
                <div>
                    <input type="checkbox" id="checkbox-scales" checked>
                    <label for="checkbox-scales">show scales</label>
                </div>
            </div>
        </div>
        <div id="visualization-area"></div>
    </div>

    <script>
        const planetData = {
            "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
            "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
        };

        let canvas;
        let analemmaPoints = [];

        function setup() {
            const visArea = document.getElementById('visualization-area');
            canvas = createCanvas(visArea.clientWidth - 40, visArea.clientHeight - 40);
            canvas.parent('visualization-area');

            setupEventListeners();
            calculateAnalemma();
        }

        function setupEventListeners() {
            document.getElementById('select-planet').addEventListener('change', handlePlanetSelection);
            document.getElementById('checkbox-manual').addEventListener('change', handleManualToggle);
            document.getElementById('slider-tilt').addEventListener('input', calculateAnalemma);
            document.getElementById('slider-equinox').addEventListener('input', calculateAnalemma);
            document.getElementById('slider-eccentricity').addEventListener('input', calculateAnalemma);
            document.getElementById('checkbox-snapshots').addEventListener('change', redraw);
            document.getElementById('checkbox-scales').addEventListener('change', redraw);
        }

        function handlePlanetSelection(e) {
            const planet = e.target.value;
            const manualCheckbox = document.getElementById('checkbox-manual');

            if (planet === 'manual') {
                manualCheckbox.checked = true;
                enableSliders(true);
            } else {
                manualCheckbox.checked = false;
                enableSliders(false);
                const data = planetData[planet];
                document.getElementById('slider-tilt').value = data.tilt;
                document.getElementById('slider-eccentricity').value = data.eccentricity;
                document.getElementById('slider-equinox').value = data.equinox;
            }
            calculateAnalemma();
        }

        function handleManualToggle(e) {
            enableSliders(e.target.checked);
            if (e.target.checked) {
                document.getElementById('select-planet').value = 'manual';
            }
            calculateAnalemma();
        }

        function enableSliders(enabled) {
            document.getElementById('slider-tilt').disabled = !enabled;
            document.getElementById('slider-equinox').disabled = !enabled;
            document.getElementById('slider-eccentricity').disabled = !enabled;
        }

        function solveKepler(M, ecc) {
            let E = M;
            for (let i = 0; i < 10; i++) {
                E = M + ecc * sin(E);
            }
            return E;
        }

        function calculateAnalemma() {
            const tilt = radians(parseFloat(document.getElementById('slider-tilt').value));
            const ecc = parseFloat(document.getElementById('slider-eccentricity').value);
            const equinoxAngle = radians(parseFloat(document.getElementById('slider-equinox').value));

            analemmaPoints = [];
            for (let d = 0; d < 365; d++) {
                const M = (TWO_PI * d) / 365.25;
                const E = solveKepler(M, ecc);
                const v = 2 * atan2(sqrt(1 + ecc) * sin(E/2), sqrt(1 - ecc) * cos(E/2));
                const lambda = v + equinoxAngle;

                const y = asin(sin(tilt) * sin(lambda));
                let alpha = atan2(cos(tilt) * sin(lambda), cos(lambda));
                if (alpha < 0) alpha += TWO_PI;
                let EoT = M - alpha;
                if (EoT > PI) EoT -= TWO_PI;
                if (EoT < -PI) EoT += TWO_PI;

                analemmaPoints.push({x: EoT * 200, y: y * 200});
            }
            redraw();
        }

        function draw() {
            background(255);
            translate(width/2, height/2);

            // Draw orbit ellipse
            const ecc = parseFloat(document.getElementById('slider-eccentricity').value);
            stroke(139, 69, 19);
            noFill();
            ellipse(0, -height/3, 100 * (1 + ecc), 50);

            // Draw scales
            if (document.getElementById('checkbox-scales').checked) {
                drawScales();
            }

            // Draw analemma curve
            stroke(255, 165, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let pt of analemmaPoints) {
                vertex(pt.x, pt.y);
            }
            endShape();

            // Draw sun snapshots
            if (document.getElementById('checkbox-snapshots').checked) {
                noStroke();
                fill(255, 165, 0);
                for (let i = 0; i < analemmaPoints.length; i += 20) {
                    circle(analemmaPoints[i].x, analemmaPoints[i].y, 8);
                }
            }
        }

        function drawScales() {
            stroke(0);
            strokeWeight(1);

            // Draw axes
            line(-width/2, 0, width/2, 0);
            line(0, -height/2, 0, height/2);

            // Draw ticks and labels
            textAlign(CENTER, CENTER);
            textSize(10);
            for (let x = -200; x <= 200; x += 40) {
                line(x, -5, x, 5);
                text(x/5, x, 20);
            }
            for (let y = -200; y <= 200; y += 40) {
                line(-5, y, 5, y);
                text(-y/5, -20, y);
            }
        }
    </script>
</body>
</html>
