            </div>
            <div class="slider-group">
                <label for="slider-bv">B.v</label>
                <input type="range" id="slider-bv" min="-2" max="2" step="0.01" value="1.0">
                <span id="value-bv">1.0</span>
            </div>
            <div class="slider-group">
                <label for="slider-aw">A.w</label>
                <input type="range" id="slider-aw" min="-2" max="2" step="0.01" value="1.1">
                <span id="value-aw">1.1</span>
            </div>
            <div class="slider-group">
                <label for="slider-bw">B.w</label>
                <input type="range" id="slider-bw" min="-2" max="2" step="0.01" value="-0.2">
                <span id="value-bw">-0.2</span>
            </div>
            <div class="slider-group">
                <label for="slider-x-scale">x scale</label>
                <input type="range" id="slider-x-scale" min="20" max="200" step="1" value="80">
                <span id="value-x-scale">80</span>
            </div>
            <div class="slider-group">
                <label for="slider-y-scale">y scale</label>
                <input type="range" id="slider-y-scale" min="20" max="200" step="1" value="80">
                <span id="value-y-scale">80</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let u, v, w, A, B;
        let xScale = 80, yScale = 80;

        function setup() {
            const canvas = createCanvas(
                document.getElementById('canvas-container').offsetWidth,
                document.getElementById('canvas-container').offsetHeight
            );
            canvas.parent('canvas-container');
            noLoop();

            // Attach listeners to all sliders
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', function() {
                    document.getElementById(`value-${this.id.split('-')[1]}`).textContent = this.value;
                    if (this.id.includes('scale')) {
                        if (this.id.includes('x')) xScale = parseFloat(this.value);
                        else yScale = parseFloat(this.value);
                    }
                    calculateVectors();
                    redraw();
                });
            });

            calculateVectors();
            redraw();
        }

        function calculateVectors() {
            // Get values from sliders
            const au = parseFloat(document.getElementById('slider-au').value);
            const bu = parseFloat(document.getElementById('slider-bu').value);
            const av = parseFloat(document.getElementById('slider-av').value);
            const bv = parseFloat(document.getElementById('slider-bv').value);
            const aw = parseFloat(document.getElementById('slider-aw').value);
            const bw = parseFloat(document.getElementById('slider-bw').value);

            // Construct measurement matrix
            const M = math.matrix([[au, bu], [av, bv], [aw, bw]]);

            // Perform SVD
            const svd = math.svd(M);
            const U = svd.U;
            const S = svd.S;
            const V = svd.V;

            // Extract vectors
            u = [U._data[0][0], U._data[0][1]];
            v = [U._data[1][0], U._data[1][1]];
            w = [U._data[2][0], U._data[2][1]];
            A = [V._data[0][0] * S[0], V._data[0][1] * S[1]];
            B = [V._data[1][0] * S[0], V._data[1][1] * S[1]];
        }

        function draw() {
            background(240);
            translate(width/2, height/2);
            scale(xScale, -yScale);

            // Draw axes
            stroke(0, 0, 205); // Dark blue
            strokeWeight(3/xScale);
            line(-2*u[0], -2*u[1], 2*u[0], 2*u[1]); // u axis
            line(-2*w[0], -2*w[1], 2*w[0], 2*w[1]); // w axis

            stroke(100, 149, 237); // Light blue
            line(-2*v[0], -2*v[1], 2*v[0], 2*v[1]); // v axis

            // Draw points
            noStroke();
            fill(255, 0, 0); // Red
            circle(0, 0, 0.1); // Origin
            circle(A[0], A[1], 0.1); // Point A

            fill(255, 165, 0); // Orange
            circle(B[0], B[1], 0.1); // Point B

            // Draw projections
            stroke(128, 128, 128); // Gray
            strokeWeight(1/xScale);

            // Project A onto axes
            const Au = math.multiply(math.dot(A, u), u);
            const Av = math.multiply(math.dot(A, v), v);
            const Aw = math.multiply(math.dot(A, w), w);

            line(A[0], A[1], Au[0], Au[1]);
            line(A[0], A[1], Av[0], Av[1]);
            line(A[0], A[1], Aw[0], Aw[1]);

            // Project B onto axes
            const Bu = math.multiply(math.dot(B, u), u);
            const Bv = math.multiply(math.dot(B, v), v);
            const Bw = math.multiply(math.dot(B, w), w);

            line(B[0], B[1], Bu[0], Bu[1]);
            line(B[0], B[1], Bv[0], Bv[1]);
            line(B[0], B[1], Bw[0], Bw[1]);

            // Draw projection points
            fill(0, 255, 0); // Green
            noStroke();
            circle(Au[0], Au[1], 0.05);
            circle(Av[0], Av[1], 0.05);
            circle(Aw[0], Aw[1], 0.05);
            circle(Bu[0], Bu[1], 0.05);
            circle(Bv[0], Bv[1], 0.05);
            circle(Bw[0], Bw[1], 0.05);
        }

        // Handle window resizing
        window.addEventListener('resize', function() {
            resizeCanvas(
                document.getElementById('canvas-container').offsetWidth,
                document.getElementById('canvas-container').offsetHeight
            );
            redraw();
        });
    </script>
</body>
</html>
