        function init() {
            scene = new THREE.Scene();
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth - 240, window.innerHeight);
            document.getElementById('visualization-container').appendChild(renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 5, 5);
            scene.add(ambientLight, directionalLight);

            setupCamera('2D');
            updateVisualization();
            animate();
        }

        function setupCamera(dimension) {
            if (dimension === '2D') {
                camera = new THREE.OrthographicCamera(-5, 5, 5, -5, 0.1, 1000);
                camera.position.z = 5;
            } else {
                camera = new THREE.PerspectiveCamera(75, (window.innerWidth - 240) / window.innerHeight, 0.1, 1000);
                camera.position.set(5, 5, 5);
                controls = new THREE.OrbitControls(camera, renderer.domElement);
            }
        }

        function updateVisualization() {
            while(scene.children.length > 0){ 
                scene.remove(scene.children[0]); 
            }

            if (currentDimension === '2D') {
                create2DVisualization();
            } else {
                create3DVisualization();
            }
        }

        // Function implementations for parametric equations would go here
        // Example for 2A2:
        function create2DVisualization() {
            const points = [];
            const steps = 200;
            for(let i = 0; i <= steps; i++) {
                const u = -1.5 + (3 * i / steps);
                const x = 2 * Math.pow(u, 3) + t1;
                const y = 3 * Math.pow(u, 2) + t2;
                points.push(new THREE.Vector3(x, y, 0));
            }

            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: 0x4a69bd });
            const line = new THREE.Line(geometry, material);
            scene.add(line);
        }

        function animate() {
            requestAnimationFrame(animate);
            if (controls) controls.update();
            renderer.render(scene, camera);
        }

        // Time pad interaction
        const timePad = document.getElementById('time-pad');
        const timeHandle = document.getElementById('time-handle');
        let isDragging = false;

        function updateTimeParams(event) {
            const rect = timePad.getBoundingClientRect();
            const x = (event.clientX - rect.left) / rect.width;
            const y = (event.clientY - rect.top) / rect.height;

            t1 = (x * 2 - 1);
            t2 = -(y * 2 - 1);

            timeHandle.style.left = `${x * 100}%`;
            timeHandle.style.top = `${y * 100}%`;

            updateVisualization();
        }

        timeHandle.addEventListener('mousedown', () => isDragging = true);
        document.addEventListener('mouseup', () => isDragging = false);
        document.addEventListener('mousemove', (e) => {
            if (isDragging) updateTimeParams(e);
        });
        timePad.addEventListener('click', updateTimeParams);

        // Button interaction
        document.querySelectorAll('button').forEach(button => {
            button.addEventListener('click', () => {
                document.querySelectorAll('button').forEach(b => b.classList.remove('active'));
                button.classList.add('active');

                currentType = button.id.substring(4);
                currentDimension = button.id.startsWith('btn-2') ? '2D' : '3D';

                if (camera.type !== (currentDimension === '2D' ? 'OrthographicCamera' : 'PerspectiveCamera')) {
                    setupCamera(currentDimension);
                }

                updateVisualization();
            });
        });

        window.addEventListener('resize', () => {
            renderer.setSize(window.innerWidth - 240, window.innerHeight);
            if (currentDimension === '3D') {
                camera.aspect = (window.innerWidth - 240) / window.innerHeight;
                camera.updateProjectionMatrix();
            }
        });

        init();
    </script>
