            color: white;
            border: none;
        }
        input[type="range"] {
            width: 100%;
        }
        select {
            width: 100%;
            padding: 5px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>Tree type</h4>
            <div id="tree-type-buttons" class="button-group">
                <button id="btn-rt" class="active">Random Tree</button>
                <button id="btn-rrt">RRT</button>
                <button id="btn-rrt-star">RRT*</button>
            </div>

            <h4>Obstacle type</h4>
            <select id="select-obstacle">
                <option value="narrow-passage" selected>narrow passage</option>
                <option value="empty">empty</option>
                <option value="random-rects">random rects</option>
            </select>

            <h4>Number of nodes to add</h4>
            <div id="add-nodes-buttons" class="button-group">
                <button id="btn-add-1">1</button>
                <button id="btn-add-10">10</button>
                <button id="btn-add-100">100</button>
                <button id="btn-add-200">200</button>
                <button id="btn-add-500">500</button>
            </div>

            <h4>Exploration bias</h4>
            <input type="range" id="slider-bias" min="0" max="1" step="0.05" value="0">
            <span id="bias-value-label">0</span>

            <h4>Goal radius</h4>
            <input type="range" id="slider-radius" min="1" max="10" step="0.1" value="1">
            <span id="radius-value-label">1</span>
        </div>

        <div id="canvas-container">
            <p id="status-text">1 node, goal not yet reached</p>
            <button id="btn-reset">+</button>
        </div>
    </div>

    <script>
        let sketch = function(p) {
            let state = {
                nodes: [],
                startPos: {x: 50, y: 250},
                goalPos: {x: 450, y: 450},
                nodeCount: 1,
                goalReached: false,
                draggingGoal: false,
                treeType: 'rt',
                obstacleType: 'narrow-passage',
                explorationBias: 0,
                goalRadius: 1
            };

            p.setup = function() {
                let canvas = p.createCanvas(500, 500);
                canvas.parent('canvas-container');
                resetSimulation();
            };

            p.draw = function() {
                p.background(255);
                drawObstacles();
                drawNodes();
                drawGoal();
                drawStart();
            };

            function drawObstacles() {
                p.stroke('blue');
                p.strokeWeight(8);
                p.noFill();

                if(state.obstacleType === 'narrow-passage') {
                    // Draw border
                    p.rect(0, 0, p.width, p.height);
                    // Draw walls with passages
                    p.line(p.width/3, 0, p.width/3, p.height/3);
                    p.line(p.width/3, p.height/2, p.width/3, p.height);
                    p.line(2*p.width/3, p.height/3, 2*p.width/3, 2*p.height/3);
                }
            }

            function drawNodes() {
                p.noStroke();
                p.fill('red');
                state.nodes.forEach(node => {
                    p.circle(node.x, node.y, 4);
                });
            }

            function drawGoal() {
                p.fill(255, 255, 0, 100);
                p.noStroke();
                p.circle(state.goalPos.x, state.goalPos.y, state.goalRadius * 20);

                p.stroke(0);
                p.strokeWeight(1);
                let size = 10;
                p.line(state.goalPos.x - size, state.goalPos.y, state.goalPos.x + size, state.goalPos.y);
                p.line(state.goalPos.x, state.goalPos.y - size, state.goalPos.x, state.goalPos.y + size);
            }

            function drawStart() {
                p.noStroke();
                p.fill(state.nodes.length > 1 ? 'green' : 'red');
                p.circle(state.startPos.x, state.startPos.y, 8);
            }

            function resetSimulation() {
                state.nodes = [{x: state.startPos.x, y: state.startPos.y}];
                state.nodeCount = 1;
                state.goalReached = false;
                updateStatusText();
            }

            function updateStatusText() {
                document.getElementById('status-text').innerText = 
                    `${state.nodeCount} node${state.nodeCount > 1 ? 's' : ''}, goal not yet reached`;
            }

            // Mouse interaction handlers
            p.mousePressed = function() {
                let d = p.dist(p.mouseX, p.mouseY, state.goalPos.x, state.goalPos.y);
                if(d < state.goalRadius * 10) {
                    state.draggingGoal = true;
                }
            };

            p.mouseDragged = function() {
                if(state.draggingGoal) {
                    state.goalPos.x = p.constrain(p.mouseX, 0, p.width);
                    state.goalPos.y = p.constrain(p.mouseY, 0, p.height);
                    resetSimulation();
                }
            };

            p.mouseReleased = function() {
                state.draggingGoal = false;
            };
        };

        new p5(sketch);

        // UI Event Listeners
        document.getElementById('btn-reset').onclick = () => {
            // Reset simulation logic here
        };

        document.getElementById('slider-bias').oninput = function() {
            document.getElementById('bias-value-label').innerText = this.value;
        };

        document.getElementById('slider-radius').oninput = function() {
            document.getElementById('radius-value-label').innerText = this.value;
        };

        // Tree type button handlers
        document.querySelectorAll('#tree-type-buttons button').forEach(btn => {
            btn.onclick = function() {
                document.querySelectorAll('#tree-type-buttons button').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
            };
        });
    </script>
</body>
</html>
