<!DOCTYPE html>
<html>
<head>
    <title>Penning Trap Visualization</title>
    <style>
        body {
            margin: 0;
            display: flex;
            height: 100vh;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            width: 280px;
            padding: 20px;
            background: #f8f8f8;
            border-right: 1px solid #ddd;
            overflow-y: auto;
        }
        .control-group {
            margin-bottom: 20px;
        }
        .control-group label {
            display: block;
            margin-bottom: 5px;
        }
        .control-group input[type="range"] {
            width: 100%;
            margin-bottom: 5px;
        }
        .control-group input[type="number"] {
            width: 80px;
        }
        #visualization-container {
            flex: 1;
            position: relative;
        }
        #three-canvas {
            width: 100%;
            height: 100%;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <div class="control-group">
            <label>t</label>
            <input type="range" id="slider-t" min="1" max="100" step="1" value="49">
            <input type="number" id="input-t" min="1" max="100" step="1" value="49">
        </div>
        <div class="control-group">
            <label>\(\rho_0\)</label>
            <input type="range" id="slider-rho0" min="0.5" max="3.0" step="0.01" value="1.9">
            <input type="number" id="input-rho0" min="0.5" max="3.0" step="0.01" value="1.9">
        </div>
        <div class="control-group">
            <label>\(\zeta_0\)</label>
            <input type="range" id="slider-zeta0" min="0" max="1.6" step="0.005" value="0.455">
            <input type="number" id="input-zeta0" min="0" max="1.6" step="0.005" value="0.455">
        </div>
        <div class="control-group">
            <label>\(e_0\)</label>
            <input type="range" id="slider-e0" min="2.01" max="5.0" step="0.01" value="2.93">
            <input type="number" id="input-e0" min="2.01" max="5.0" step="0.01" value="2.93">
        </div>
        <div class="control-group">
            <label>v</label>
            <input type="range" id="slider-v" min="0" max="2.0" step="0.01" value="0">
            <input type="number" id="input-v" min="0" max="2.0" step="0.01" value="0">
        </div>
        <div class="control-group">
            <label>\(\lambda\)</label>
            <input type="range" id="slider-lambda" min="0" max="2.0" step="0.01" value="1.1">
            <input type="number" id="input-lambda" min="0" max="2.0" step="0.01" value="1.1">
        </div>
    </div>
    <div id="visualization-container">
        <canvas id="three-canvas"></canvas>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <script>
        let scene, camera, renderer, controls;
        let currentPath = null;

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            const container = document.getElementById('visualization-container');
            camera = new THREE.PerspectiveCamera(60, container.clientWidth/container.clientHeight, 0.1, 1000);
            camera.position.set(4, 4, 4);
            camera.lookAt(0, 1, 0);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({
                canvas: document.getElementById('three-canvas'),
                antialias: true
            });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(container.clientWidth, container.clientHeight);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.target.set(0, 1, 0);

            // Lighting
            const ambient = new THREE.AmbientLight(0x666666);
            scene.add(ambient);

            const directional = new THREE.DirectionalLight(0xffffff, 1.0);
            directional.position.set(5, 5, 5);
            scene.add(directional);

            // Axes
            const axes = new THREE.AxesHelper(4);
            scene.add(axes);

            // Add static geometry
            addStaticGeometry();

            // Setup controls
            setupControls();

            // Initial visualization
            updateVisualization();

            // Animation loop
            animate();

            // Window resize handler
            window.addEventListener('resize', onWindowResize, false);
        }

        function addStaticGeometry() {
            // Potential surface
            const surfaceGeometry = new THREE.ParametricBufferGeometry((u, v, target) => {
                const rho = 0.4 + u * 2.1;
                const theta = v * 2 * Math.PI;
                const x = rho * Math.cos(theta);
                const y_surf = rho*rho + 1/(rho*rho);
                const z_surf = -rho * Math.sin(theta);
                target.set(x, y_surf, z_surf);
            }, 50, 50);

            const surfaceMaterial = new THREE.MeshStandardMaterial({
                color: 0xE69943,
                metalness: 0.4,
                roughness: 0.5,
                side: THREE.DoubleSide
            });

            const surface = new THREE.Mesh(surfaceGeometry, surfaceMaterial);
            scene.add(surface);

            // Central cylinder
            const cylinderGeometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
            const cylinderMaterial = new THREE.MeshStandardMaterial({
                color: 0xcccccc,
                metalness: 0.1,
                roughness: 0.9
            });

            const cylinder = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
            cylinder.position.set(0, 1.25, 0);
            scene.add(cylinder);
        }

        function setupControls() {
            const controls = ['t', 'rho0', 'zeta0', 'e0', 'v', 'lambda'];
            controls.forEach(param => {
                const slider = document.getElementById(`slider-${param}`);
                const input = document.getElementById(`input-${param}`);

                slider.addEventListener('input', e => {
                    input.value = e.target.value;
                    updateVisualization();
                });

                input.addEventListener('input', e => {
                    slider.value = e.target.value;
                    updateVisualization();
                });
            });
        }

        function rk4Step(y, h, lambda, e0) {
            function deriv(y) {
                return [
                    y[1],
                    -(2*y[0] - 2/Math.pow(y[0],3)) - 4*lambda*lambda*y[0] + 4*e0/Math.pow(y[0],3),
                    2*(e0/Math.pow(y[0],2) - lambda)
                ];
            }

            const k1 = deriv(y);
            const k2 = deriv(y.map((yi, i) => yi + k1[i]*h/2));
            const k3 = deriv(y.map((yi, i) => yi + k2[i]*h/2));
            const k4 = deriv(y.map((yi, i) => yi + k3[i]*h));

            return y.map((yi, i) => yi + h/6*(k1[i] + 2*k2[i] + 2*k3[i] + k4[i]));
        }

        function updateVisualization() {
            // Get current parameters
            const t = parseFloat(document.getElementById('input-t').value);
            const rho0 = parseFloat(document.getElementById('input-rho0').value);
            const zeta0 = parseFloat(document.getElementById('input-zeta0').value);
            const e0 = parseFloat(document.getElementById('input-e0').value);
            const nu = parseFloat(document.getElementById('input-v').value);
            const lambda = parseFloat(document.getElementById('input-lambda').value);

            // Remove existing path
            if (currentPath) {
                scene.remove(currentPath);
                currentPath.geometry.dispose();
                currentPath.material.dispose();
            }

            // Generate points using RK4
            const points = [];
            const ds = 0.05;
            let y = [rho0, 0, 0]; // [ρ, dρ/ds, φ]

            for (let s = 0; s <= t; s += ds) {
                y = rk4Step(y, ds, lambda, e0);
                const rho = y[0];
                const phi = y[2];

                const u_rho = rho*rho + 1/(rho*rho);
                const sqrt_arg = Math.max(0, e0 - u_rho);
                const z = Math.sqrt(sqrt_arg) * Math.sin(Math.sqrt(2) * nu * s + zeta0);

                const x = rho * Math.cos(phi);
                const y_coord = rho * Math.sin(phi);

                points.push(new THREE.Vector3(x, z, -y_coord));
            }

            // Create new path
            const curve = new THREE.CatmullRomCurve3(points);
            const geometry = new THREE.TubeGeometry(curve, 512, 0.04, 8);
            const material = new THREE.MeshBasicMaterial({ color: 0x61AEEF });

            currentPath = new THREE.Mesh(geometry, material);
            currentPath.name = 'ionPath';
            scene.add(currentPath);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function onWindowResize() {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        // Initialize when page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
