<!DOCTYPE html>
<html>
<head>
    <title>H2+ Molecular Orbitals</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        #app-container {
            max-width: 1000px;
            margin: 0 auto;
            padding: 20px;
        }
        #control-panel {
            text-align: center;
            margin-bottom: 20px;
        }
        #potential-energy-plot-container {
            height: 400px;
            margin-bottom: 20px;
        }
        #density-plots-container {
            display: flex;
            justify-content: space-around;
            height: 400px;
        }
        #bonding-plot-container, #antibonding-plot-container {
            width: 48%;
        }
        input[type="range"] {
            width: 300px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="slider-r">R (Å): </label>
            <input type="range" id="slider-r" min="0.001" max="4.0" step="0.001" value="0.001">
            <output id="slider-r-value">0.001</output>
        </div>
        <div id="potential-energy-plot-container"></div>
        <div id="density-plots-container">
            <div id="bonding-plot-container"></div>
            <div id="antibonding-plot-container"></div>
        </div>
    </div>

    <script>
        // Utility functions
        function generateGrid(min, max, points) {
            const step = (max - min) / (points - 1);
            return Array.from({length: points}, (_, i) => min + i * step);
        }

        function calculateOverlap(R) {
            return (1 + R + R*R/3) * Math.exp(-R);
        }

        function calculatePotentialEnergy(R) {
            const S = calculateOverlap(R);
            const J = (1/R) - (1 + 1/R) * Math.exp(-2*R);
            const K = (1 + R) * Math.exp(-R);
            return {
                bonding: (J + K) / (1 + S),
                antibonding: (J - K) / (1 - S)
            };
        }

        // Initialize plots
        function initPotentialEnergyPlot() {
            const R_values = generateGrid(0.2, 15, 200);
            const E_b = [], E_a = [];

            R_values.forEach(R => {
                const PE = calculatePotentialEnergy(R);
                E_b.push(PE.bonding);
                E_a.push(PE.antibonding);
            });

            const R_angstrom = R_values.map(R => R * 0.529177);

            const data = [
                {
                    x: R_angstrom,
                    y: E_b,
                    name: 'Bonding PE',
                    line: {color: 'blue'}
                },
                {
                    x: R_angstrom,
                    y: E_a,
                    name: 'Antibonding PE',
                    line: {color: 'red'}
                }
            ];

            const layout = {
                title: 'Potential Energy',
                xaxis: {title: 'R (Å)', range: [0, 8]},
                yaxis: {title: 'Energy (Hartree)', range: [-0.6, 0.4]},
                showlegend: true
            };

            Plotly.newPlot('potential-energy-plot-container', data, layout);
        }

        function calculateDensity(R_au) {
            const x = generateGrid(-3, 3, 50);
            const y = generateGrid(-3, 3, 50);
            const z_bonding = [], z_antibonding = [];

            const S = calculateOverlap(R_au);
            const N_b = 1 / Math.sqrt(2 * (1 + S));
            const N_a = 1 / Math.sqrt(2 * (1 - S));

            for (let i = 0; i < y.length; i++) {
                const z_b_row = [], z_a_row = [];
                for (let j = 0; j < x.length; j++) {
                    const dist_A = Math.sqrt((x[j] + R_au/2)**2 + y[i]**2);
                    const dist_B = Math.sqrt((x[j] - R_au/2)**2 + y[i]**2);

                    const phi_A = Math.exp(-dist_A);
                    const phi_B = Math.exp(-dist_B);

                    const psi_b = N_b * (phi_A + phi_B);
                    const psi_a = N_a * (phi_A - phi_B);

                    z_b_row.push(psi_b * psi_b);
                    z_a_row.push(psi_a * psi_a);
                }
                z_bonding.push(z_b_row);
                z_antibonding.push(z_a_row);
            }

            return {x, y, z_bonding, z_antibonding};
        }

        function updateDensityPlots(R_angstrom) {
            const R_au = R_angstrom / 0.529177;
            const {x, y, z_bonding, z_antibonding} = calculateDensity(R_au);

            const surfaceLayout = {
                showlegend: false,
                autosize: true,
                scene: {
                    xaxis: {range: [-3, 3], showgrid: false, zeroline: false, showticklabels: false},
                    yaxis: {range: [-3, 3], showgrid: false, zeroline: false, showticklabels: false},
                    zaxis: {range: [0, 0.5], showgrid: false, zeroline: false, showticklabels: false},
                    camera: {
                        eye: {x: 1.5, y: 1.5, z: 1.5}
                    }
                }
            };

            const surfaceData = (z_data) => [{
                type: 'surface',
                x: x,
                y: y,
                z: z_data,
                colorscale: 'Viridis',
                showscale: false
            }];

            Plotly.react('bonding-plot-container', surfaceData(z_bonding), surfaceLayout);
            Plotly.react('antibonding-plot-container', surfaceData(z_antibonding), surfaceLayout);
        }

        // Initialize page
        document.addEventListener('DOMContentLoaded', () => {
            initPotentialEnergyPlot();
            updateDensityPlots(0.001);

            const slider = document.getElementById('slider-r');
            const output = document.getElementById('slider-r-value');

            slider.addEventListener('input', (e) => {
                const value = parseFloat(e.target.value);
                output.value = value.toFixed(3);
                updateDensityPlots(value);
            });
        });
    </script>
</body>
</html>
