    <script>
        let time = 0;
        let isPlaying = false;
        let animationId;
        let rotationMode = 'normal';

        // p5.js setup
        function setup() {
            const canvas = createCanvas(
                document.getElementById('visualization-area').offsetWidth,
                document.getElementById('visualization-area').offsetHeight
            );
            canvas.parent('canvas-container');
        }

        function draw() {
            background(255);
            translate(width/2, height/2);

            const earthRadius = 80;
            const moonRadius = 25;
            const orbitRadius = 250;

            const earthRotation = (time / 24) * TWO_PI;
            const moonOrbit = (time / 672) * TWO_PI;

            let moonRotation;
            switch(rotationMode) {
                case 'half': moonRotation = moonOrbit * 2; break;
                case 'normal': moonRotation = moonOrbit; break;
                case 'double': moonRotation = moonOrbit / 2; break;
                case 'none': moonRotation = 0; break;
            }

            if (!document.getElementById('check-moon-only').checked) {
                // Draw Earth
                fill('#3B82F6');
                circle(0, 0, earthRadius * 2);

                push();
                rotate(earthRotation);

                // Earth features
                fill(0, 255, 0);
                beginShape();
                vertex(-20, -30);
                vertex(20, -40);
                vertex(30, 0);
                vertex(-10, 20);
                endShape(CLOSE);

                // Red stick figure on Earth
                stroke(255, 0, 0);
                line(0, -earthRadius, 0, -earthRadius-15);
                circle(0, -earthRadius-20, 10);
                pop();

                if (document.getElementById('check-day-night').checked) {
                    fill(0, 128);
                    arc(0, 0, earthRadius*2, earthRadius*2, -HALF_PI, HALF_PI);
                }
            }

            // Moon position
            let mx = orbitRadius * cos(moonOrbit - HALF_PI);
            let my = orbitRadius * sin(moonOrbit - HALF_PI);

            if (document.getElementById('check-moon-only').checked) {
                mx = 0;
                my = 0;
            }

            if (document.getElementById('check-earth-moon-line').checked && 
                !document.getElementById('check-moon-only').checked) {
                stroke(100);
                setLineDash([5, 5]);
                line(0, 0, mx, my);
                setLineDash([]);
            }

            // Draw Moon
            push();
            translate(mx, my);
            fill(128);
            circle(0, 0, moonRadius * 2);

            if (document.getElementById('check-near-side').checked) {
                push();
                rotate(moonRotation);
                fill('#22C55E');
                arc(0, 0, moonRadius*2, moonRadius*2, -HALF_PI, HALF_PI);

                // Magenta stick figure
                stroke('#D946EF');
                line(0, -moonRadius, 0, -moonRadius-8);
                circle(0, -moonRadius-10, 5);
                pop();
            }

            if (document.getElementById('check-day-night').checked) {
                fill(0, 128);
                arc(0, 0, moonRadius*2, moonRadius*2, -HALF_PI, HALF_PI);
            }
            pop();
        }

        function setLineDash(list) {
            drawingContext.setLineDash(list);
        }

        // Event listeners
        document.getElementById('slider-time').addEventListener('input', function(e) {
            time = parseInt(e.target.value);
            updateTimeDisplay();
        });

        document.getElementById('btn-play-pause').addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '❚❚' : '▶';

            if (isPlaying) {
                animate();
            } else {
                cancelAnimationFrame(animationId);
            }
        });

        function animate() {
            time = (time + 1) % 673;
            document.getElementById('slider-time').value = time;
            updateTimeDisplay();
            animationId = requestAnimationFrame(animate);
        }

        function updateTimeDisplay() {
            const days = Math.floor(time / 24);
            const hours = time % 24;
            document.getElementById('display-time').textContent = 
                `${days} d ${hours} h`;
        }

        document.getElementById('btn-step-back').addEventListener('click', function() {
            time = (time - 1 + 673) % 673;
            document.getElementById('slider-time').value = time;
            updateTimeDisplay();
        });

        document.getElementById('btn-step-forward').addEventListener('click', function() {
            time = (time + 1) % 673;
            document.getElementById('slider-time').value = time;
            updateTimeDisplay();
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            time = 0;
            document.getElementById('slider-time').value = time;
            updateTimeDisplay();
        });

        // Rotation mode buttons
        document.querySelectorAll('#rotation-selector button').forEach(button => {
            button.addEventListener('click', function() {
                document.querySelectorAll('#rotation-selector button')
                    .forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                rotationMode = this.id.replace('btn-rot-', '');
            });
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            resizeCanvas(
                document.getElementById('visualization-area').offsetWidth,
                document.getElementById('visualization-area').offsetHeight
            );
        });
    </script>
</body>
</html>
