    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        let scene, camera, renderer, controls;
        let radiationPattern, boxHelper;
        let currentWaveType = 'P';

        // Initialize scene
        function init() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(3, 3, 3);

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(document.getElementById('canvas-container').clientWidth, 
                           document.getElementById('canvas-container').clientHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            controls = new OrbitControls(camera, renderer.domElement);
            controls.target.set(0, 0, 0);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight1.position.set(5, 5, 5);
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.3);
            directionalLight2.position.set(-5, -5, -5);
            scene.add(directionalLight2);

            createRadiationPattern();
            createCompass();
            createUpArrow();

            animate();
        }

        function createRadiationPattern() {
            // Implementation of parametric geometry based on wave type
            const geometry = new THREE.SphereGeometry(1, 32, 32); // Placeholder
            const material = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                side: THREE.DoubleSide
            });

            radiationPattern = new THREE.Mesh(geometry, material);
            scene.add(radiationPattern);

            boxHelper = new THREE.BoxHelper(radiationPattern);
            boxHelper.visible = false;
            scene.add(boxHelper);
        }

        function createCompass() {
            // Implementation of compass overlay
        }

        function createUpArrow() {
            const arrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 1, 0),
                new THREE.Vector3(0, 0, 0),
                1,
                0x000000
            );
            scene.add(arrow);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Event Listeners
        document.getElementById('slider-strike').addEventListener('input', function(e) {
            document.getElementById('strike-value').textContent = e.target.value + '°';
            updateRotation();
        });

        document.getElementById('slider-dip').addEventListener('input', function(e) {
            document.getElementById('dip-value').textContent = e.target.value + '°';
            updateRotation();
        });

        document.getElementById('slider-rake').addEventListener('input', function(e) {
            document.getElementById('rake-value').textContent = e.target.value + '°';
            updateRotation();
        });

        document.getElementById('checkbox-box').addEventListener('change', function(e) {
            boxHelper.visible = e.target.checked;
        });

        ['p', 'sv', 'sh'].forEach(type => {
            document.getElementById(`btn-${type}`).addEventListener('click', function() {
                document.querySelectorAll('.button-container button').forEach(btn => {
                    btn.classList.remove('active');
                });
                this.classList.add('active');
                currentWaveType = type.toUpperCase();
                createRadiationPattern();
            });
        });

        function updateRotation() {
            const strike = THREE.MathUtils.degToRad(parseFloat(document.getElementById('slider-strike').value));
            const dip = THREE.MathUtils.degToRad(parseFloat(document.getElementById('slider-dip').value));
            const rake = THREE.MathUtils.degToRad(parseFloat(document.getElementById('slider-rake').value));

            radiationPattern.rotation.set(0, 0, 0);
            radiationPattern.rotateZ(strike);
            radiationPattern.rotateX(dip);
            radiationPattern.rotateY(rake);
        }

        // Initialize MathJax
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']]
            }
        };

        // Start when page is loaded
        window.addEventListener('load', function() {
            init();
            MathJax.typeset();
        });
    </script>
</body>
