    <title>Circle Pattern Inversion Demo</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            display: flex;
            justify-content: center;
            padding: 20px;
        }
        #main-container {
            width: 550px;
        }
        #control-panel {
            display: flex;
            justify-content: space-between;
            margin-bottom: 20px;
        }
        .control-group {
            display: flex;
            flex-direction: column;
        }
        .radio-container {
            display: flex;
            gap: 10px;
            margin-top: 5px;
        }
        #canvas-container {
            border: 1px solid #999;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="rings-control-group" class="control-group">
                <label>rings</label>
                <div class="radio-container">
                    <input type="radio" name="rings" value="1" id="rings-1" checked>
                    <label for="rings-1">1</label>
                    <input type="radio" name="rings" value="2" id="rings-2">
                    <label for="rings-2">2</label>
                    <input type="radio" name="rings" value="3" id="rings-3">
                    <label for="rings-3">3</label>
                    <input type="radio" name="rings" value="4" id="rings-4">
                    <label for="rings-4">4</label>
                    <input type="radio" name="rings" value="5" id="rings-5">
                    <label for="rings-5">5</label>
                    <input type="radio" name="rings" value="6" id="rings-6">
                    <label for="rings-6">6</label>
                    <input type="radio" name="rings" value="7" id="rings-7">
                    <label for="rings-7">7</label>
                </div>
            </div>
            <div id="circles-per-ring-control-group" class="control-group">
                <label>circles per ring</label>
                <div class="radio-container">
                    <input type="radio" name="cpr" value="4" id="cpr-4">
                    <label for="cpr-4">4</label>
                    <input type="radio" name="cpr" value="6" id="cpr-6">
                    <label for="cpr-6">6</label>
                    <input type="radio" name="cpr" value="8" id="cpr-8" checked>
                    <label for="cpr-8">8</label>
                    <input type="radio" name="cpr" value="10" id="cpr-10">
                    <label for="cpr-10">10</label>
                    <input type="radio" name="cpr" value="12" id="cpr-12">
                    <label for="cpr-12">12</label>
                    <input type="radio" name="cpr" value="14" id="cpr-14">
                    <label for="cpr-14">14</label>
                </div>
            </div>
            <div id="inverse-control-group" class="control-group">
                <label for="checkbox-inverse">inverse</label>
                <input type="checkbox" id="checkbox-inverse">
            </div>
        </div>
        <div id="canvas-container">
        </div>
    </div>

    <script>
        let locators = [];
        let draggedLocator = null;

        function setup() {
            const canvas = createCanvas(550, 550);
            canvas.parent('canvas-container');
            angleMode(RADIANS);

            locators = [
                createVector(-50, 0),
                createVector(50, 0)
            ];
        }

        function draw() {
            background('#C1D4E6');
            translate(width/2, height/2);

            const rings = parseInt(document.querySelector('input[name="rings"]:checked').value);
            const circlesPerRing = parseInt(document.querySelector('input[name="cpr"]:checked').value);
            const isInverse = document.getElementById('checkbox-inverse').checked;

            if (isInverse) {
                drawInverseMode(rings, circlesPerRing);
            } else {
                drawNormalMode(rings, circlesPerRing);
            }
        }

        function drawNormalMode(rings, N) {
            const Rmax = width/2 * 0.9;
            const k = (1 - sin(PI/N)) / (1 + sin(PI/N));
            const Rc = Rmax / (1 + sin(PI/N));
            const r = Rc * sin(PI/N);

            for (let ring = 0; ring < rings; ring++) {
                const ringRadius = Rc * pow(k, ring);
                const circleRadius = r * pow(k, ring);

                for (let i = 0; i < N; i++) {
                    const angle = i * TWO_PI / N;
                    const x = ringRadius * cos(angle);
                    const y = ringRadius * sin(angle);

                    if (ring === 0) {
                        stroke('#A0A0A0');
                        line(0, 0, x, y);
                    }

                    fill('#8C929D');
                    stroke(0);
                    circle(x, y, circleRadius * 2);
                }
            }
        }

        function drawInverseMode(rings, N) {
            // Draw original pattern first
            drawNormalMode(rings, N);

            // Draw locators and inversion circle
            const center = p5.Vector.add(locators[0], locators[1]).mult(0.5);
            const radius = p5.Vector.dist(locators[0], locators[1]) / 2;

            stroke('#FF0000');
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);

            fill('rgba(255, 0, 0, 0.3)');
            stroke(0);
            circle(center.x, center.y, radius * 2);

            // Draw locator points
            fill('#FF0000');
            noStroke();
            circle(locators[0].x, locators[0].y, 8);
            circle(locators[1].x, locators[1].y, 8);

            // Calculate and draw inversions
            invertCircles(rings, N, center, radius);
        }

        function invertCircles(rings, N, invCenter, invRadius) {
            const Rmax = width/2 * 0.9;
            const k = (1 - sin(PI/N)) / (1 + sin(PI/N));
            const Rc = Rmax / (1 + sin(PI/N));
            const r = Rc * sin(PI/N);

            fill('rgba(220, 100, 220, 0.5)');
            stroke(0);

            for (let ring = 0; ring < rings; ring++) {
                const ringRadius = Rc * pow(k, ring);
                const circleRadius = r * pow(k, ring);

                for (let i = 0; i < N; i++) {
                    const angle = i * TWO_PI / N;
                    const x = ringRadius * cos(angle);
                    const y = ringRadius * sin(angle);

                    const origCenter = createVector(x, y);
                    const d = p5.Vector.dist(invCenter, origCenter);

                    if (abs(d - circleRadius) < 0.001) {
                        // Draw line for special case
                        const perpVector = p5.Vector.sub(origCenter, invCenter).rotate(HALF_PI).normalize();
                        const dist = sq(invRadius) / (2 * d);
                        const lineCenter = p5.Vector.add(invCenter, p5.Vector.mult(perpVector, dist));
                        line(lineCenter.x - width, lineCenter.y, lineCenter.x + width, lineCenter.y);
                    } else {
                        // Draw inverted circle
                        const scale = sq(invRadius) / (sq(d) - sq(circleRadius));
                        const newCenter = p5.Vector.add(
                            invCenter,
                            p5.Vector.mult(p5.Vector.sub(origCenter, invCenter), scale)
                        );
                        const newRadius = abs(scale) * circleRadius;
                        circle(newCenter.x, newCenter.y, newRadius * 2);
                    }
                }
            }
        }

        function mousePressed() {
            if (!document.getElementById('checkbox-inverse').checked) return;

            const mouseVec = createVector(mouseX - width/2, mouseY - height/2);
            for (let i = 0; i < locators.length; i++) {
                if (p5.Vector.dist(mouseVec, locators[i]) < 8) {
                    draggedLocator = i;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (draggedLocator !== null) {
                locators[draggedLocator].set(mouseX - width/2, mouseY - height/2);
            }
        }

        function mouseReleased() {
            draggedLocator = null;
        }

        // Add event listeners for controls
        document.querySelectorAll('input[type="radio"], input[type="checkbox"]').forEach(input => {
            input.addEventListener('change', () => {
                draw();
            });
        });
    </script>
</body>
</html>
