            width: 800px;
            height: 600px;
        }
        #formula-strong {
            position: absolute;
            top: 100px;
            left: 100px;
            color: maroon;
        }
        #formula-weak {
            position: absolute;
            top: 200px;
            right: 100px;
            color: green;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label>\(C_a\) concentration:</label>
                <input type="range" id="slider-logCa" min="-4" max="0" step="0.01" value="-1">
                <span>Value: <span id="logCa-value">-1</span></span>
            </div>
            <div class="slider-group">
                <label>\(pK_a\) value:</label>
                <input type="range" id="slider-pka" min="-4" max="7" step="0.1" value="1.2">
                <span>Value: <span id="pka-value">1.2</span></span>
            </div>
        </div>
        <div id="vis-container">
            <div id="formula-strong">\( \color{maroon}{pH = - \log C_a} \)</div>
            <div id="formula-weak">\( \color{green}{pH = \frac{1}{2} (pK_a - \log C_a)} \)</div>
        </div>
    </div>

    <script>
        let logCa = -1;
        let pKa = 1.2;
        let canvas;

        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('vis-container');

            // Add event listeners
            document.getElementById('slider-logCa').addEventListener('input', function(e) {
                logCa = parseFloat(e.target.value);
                document.getElementById('logCa-value').textContent = logCa.toFixed(2);
                draw();
            });

            document.getElementById('slider-pka').addEventListener('input', function(e) {
                pKa = parseFloat(e.target.value);
                document.getElementById('pka-value').textContent = pKa.toFixed(2);
                draw();
            });
        }

        function calculateExactPH(pKa, logCa) {
            const Ka = Math.pow(10, -pKa);
            const Ca = Math.pow(10, logCa);
            const b = Ka;
            const c = -Ka * Ca;
            const h = (-b + Math.sqrt(b*b - 4*c))/2;
            return -Math.log10(h);
        }

        function draw() {
            background(255);

            // Set up coordinate system
            const margin = 50;
            const xMin = -4, xMax = 7;
            const yMin = 0, yMax = 7;

            // Draw axes
            stroke(100);
            strokeWeight(1);
            line(margin, height-margin, width-margin, height-margin); // x-axis
            line(margin, margin, margin, height-margin); // y-axis

            // Draw axis labels
            noStroke();
            fill(0);
            textAlign(CENTER);
            text('pKa', width/2, height-10);
            push();
            translate(20, height/2);
            rotate(-PI/2);
            text('pH', 0, 0);
            pop();

            // Map functions
            const mapX = (x) => map(x, xMin, xMax, margin, width-margin);
            const mapY = (y) => map(y, yMin, yMax, height-margin, margin);

            // Draw strong acid line
            stroke(139,0,0);
            const strongPH = -logCa;
            line(margin, mapY(strongPH), width-margin, mapY(strongPH));

            // Draw weak acid line
            stroke(0,128,0);
            const weakPH1 = (xMin - logCa)/2;
            const weakPH2 = (xMax - logCa)/2;
            line(mapX(xMin), mapY(weakPH1), mapX(xMax), mapY(weakPH2));

            // Draw exact pH curve
            stroke(0,0,255);
            strokeWeight(2);
            noFill();
            beginShape();
            for(let x = xMin; x <= xMax; x += 0.1) {
                const pH = calculateExactPH(x, logCa);
                vertex(mapX(x), mapY(pH));
            }
            endShape();

            // Draw indicator point
            const currentPH = calculateExactPH(pKa, logCa);
            fill(255,165,0);
            stroke(0);
            strokeWeight(1);
            circle(mapX(pKa), mapY(currentPH), 10);
        }
    </script>
</body>
</html>
