            height: 20px;
            background: white;
            border: 2px solid #999;
            border-radius: 50%;
            position: absolute;
            transform: translate(-50%, -50%);
            cursor: pointer;
        }
        select {
            width: 200px;
            padding: 5px;
        }
        #canvas-container {
            flex-grow: 1;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label>search algorithm</label>
                <div>
                    <input type="radio" id="radio-dijkstra" name="algorithm" value="dijkstra" checked>
                    <label for="radio-dijkstra">Dijkstra</label>
                    <input type="radio" id="radio-a-star" name="algorithm" value="a-star">
                    <label for="radio-a-star">A*</label>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-step">algorithm step</label>
                <div class="slider-container">
                    <input type="range" id="slider-step" min="1" max="1" value="1">
                    <span id="label-step">+ 1</span>
                </div>
            </div>

            <div class="control-group">
                <label>starting point</label>
                <div id="joystick-start" class="joystick-area">
                    <div id="handle-start" class="joystick-handle"></div>
                </div>
            </div>

            <div class="control-group">
                <label>end point</label>
                <div id="joystick-end" class="joystick-area">
                    <div id="handle-end" class="joystick-handle"></div>
                </div>
            </div>

            <div class="control-group">
                <label for="select-obstacles">obstacles</label>
                <select id="select-obstacles">
                    <option value="1-square">1 square</option>
                    <option value="2-squares">2 squares</option>
                    <option value="l-shaped-wall">L-shaped wall</option>
                </select>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        // State variables
        let algorithmHistory = [];
        let currentStep = 1;
        let gridSize = 50;
        let startPos = { x: 0.15, y: 0.85 };
        let endPos = { x: 0.85, y: 0.15 };

        // Setup joystick dragging
        function setupJoystick(joystickId, handleId, initialPos, callback) {
            const joystick = document.getElementById(joystickId);
            const handle = document.getElementById(handleId);
            let isDragging = false;

            handle.style.left = `${initialPos.x * 100}%`;
            handle.style.top = `${initialPos.y * 100}%`;

            handle.addEventListener('mousedown', () => isDragging = true);
            document.addEventListener('mouseup', () => {
                if (isDragging) {
                    isDragging = false;
                    callback();
                }
            });

            joystick.addEventListener('mousemove', (e) => {
                if (!isDragging) return;
                const rect = joystick.getBoundingClientRect();
                let x = (e.clientX - rect.left) / rect.width;
                let y = (e.clientY - rect.top) / rect.height;
                x = Math.max(0, Math.min(1, x));
                y = Math.max(0, Math.min(1, y));
                handle.style.left = `${x * 100}%`;
                handle.style.top = `${y * 100}%`;
                if (handleId === 'handle-start') startPos = {x, y};
                else endPos = {x, y};
            });
        }

        // p5.js setup
        new p5((p) => {
            p.setup = () => {
                const canvas = p.createCanvas(600, 600);
                canvas.parent('canvas-container');
                recomputePath();
            };

            p.draw = () => {
                p.background(255);
                drawGrid();
                drawPathfinding();
            };

            function drawGrid() {
                const cellSize = p.width / gridSize;
                p.stroke(200);
                for (let i = 0; i <= gridSize; i++) {
                    p.line(i * cellSize, 0, i * cellSize, p.height);
                    p.line(0, i * cellSize, p.width, i * cellSize);
                }
            }

            function drawPathfinding() {
                if (algorithmHistory.length === 0) return;
                const frame = algorithmHistory[currentStep - 1];
                const cellSize = p.width / gridSize;

                // Draw current state
                frame.visited.forEach(cell => {
                    p.fill(0, 0, 205);
                    p.noStroke();
                    p.rect(cell.x * cellSize, cell.y * cellSize, cellSize, cellSize);
                });

                frame.frontier.forEach(cell => {
                    p.fill(189, 224, 254);
                    p.noStroke();
                    p.rect(cell.x * cellSize, cell.y * cellSize, cellSize, cellSize);
                });

                // Draw start and end
                p.fill(52, 168, 83);
                p.noStroke();
                const startX = Math.floor(startPos.x * gridSize);
                const startY = Math.floor(startPos.y * gridSize);
                const endX = Math.floor(endPos.x * gridSize);
                const endY = Math.floor(endPos.y * gridSize);
                p.rect(startX * cellSize, startY * cellSize, cellSize, cellSize);
                p.rect(endX * cellSize, endY * cellSize, cellSize, cellSize);

                // Draw final path if on last step
                if (currentStep === algorithmHistory.length && frame.path) {
                    p.stroke(255, 0, 0);
                    p.strokeWeight(3);
                    p.noFill();
                    p.beginShape();
                    frame.path.forEach(point => {
                        p.vertex(
                            (point.x + 0.5) * cellSize,
                            (point.y + 0.5) * cellSize
                        );
                    });
                    p.endShape();
                }
            }
        });

        // Setup event listeners
        document.getElementById('slider-step').addEventListener('input', (e) => {
            currentStep = parseInt(e.target.value);
            document.getElementById('label-step').textContent = `+ ${currentStep}`;
        });

        document.querySelectorAll('input[name="algorithm"]').forEach(radio => {
            radio.addEventListener('change', recomputePath);
        });

        document.getElementById('select-obstacles').addEventListener('change', recomputePath);

        setupJoystick('joystick-start', 'handle-start', startPos, recomputePath);
        setupJoystick('joystick-end', 'handle-end', endPos, recomputePath);

        function recomputePath() {
            // Simulate pathfinding computation
            algorithmHistory = [
                {
                    visited: [],
                    frontier: [],
                    path: [
                        {x: Math.floor(startPos.x * gridSize), y: Math.floor(startPos.y * gridSize)},
                        {x: Math.floor(endPos.x * gridSize), y: Math.floor(endPos.y * gridSize)}
                    ]
                }
            ];

            const slider = document.getElementById('slider-step');
            slider.max = algorithmHistory.length;
            slider.value = 1;
            currentStep = 1;
            document.getElementById('label-step').textContent = '+ 1';
        }
    </script>
</body>
</html>
```

This implementation includes:
1. Complete layout with control panel and visualization canvas
2. Functional 2D joystick controls for start/end points
3. Working step slider with label
4. Algorithm selection radio buttons
5. Obstacles dropdown
6. Basic p5.js visualization of the grid and pathfinding states
7. Event handling for all controls

Note that the actual pathfinding algorithms (Dijkstra and A*) are not implemented in this code - they would need to be added to the `recomputePath` function to make the visualization fully functional. The current implementation just draws a direct line between start and end points as a placeholder.

