<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="slider-group">
                <label for="slider-source-charge">source charge</label>
                <input type="range" id="slider-source-charge" min="-5" max="5" step="0.1" value="2.0">
                <span id="label-source-charge">2.0</span>
            </div>
            <div class="slider-group">
                <label for="slider-test-charge">charge</label>
                <input type="range" id="slider-test-charge" min="-0.2" max="0.2" step="0.0005" value="-0.0845">
                <span id="label-test-charge">-0.0845</span>
            </div>
        </div>
        <div id="main-content">
            <div id="canvas-container"></div>
            <div id="info-panel">
                <p id="display-E-mag">|E| = 0.153 N/C</p>
                <p id="display-Ex">E<sub>x</sub> component = 0.085 N/C</p>
                <p id="display-Ey">E<sub>y</sub> component = 0.127 N/C</p>
                <p id="display-x-pos">x position = 2.00 m</p>
                <p id="display-y-pos">y position = 3.00 m</p>
                <p id="display-force">Electric force = 0.013 N</p>
            </div>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.js"></script>
    <script>
        let Q = 2.0; // source charge
        let q = -0.0845; // test charge
        let x = 2; // test charge x position
        let y = 3; // test charge y position
        let isDragging = false;
        const k = 1; // Coulomb constant (simplified)
        const scale = 40; // pixels per meter

        function setup() {
            const canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
        }

        function drawArrow(base, vec, color) {
            push();
            stroke(color);
            strokeWeight(2);
            const arrowSize = 10;

            translate(base.x * scale, base.y * scale);
            line(0, 0, vec.x * scale, vec.y * scale);

            const angle = atan2(vec.y, vec.x);
            translate(vec.x * scale, vec.y * scale);
            rotate(angle);
            triangle(-arrowSize, -arrowSize/2, -arrowSize, arrowSize/2, 0, 0);
            pop();
        }

        function calculateField() {
            const r = sqrt(x*x + y*y);
            const r_hat = createVector(x/r, y/r);
            const E_mag = k * Q / (r*r);
            const E_vec = p5.Vector.mult(r_hat, E_mag);
            const F_vec = p5.Vector.mult(E_vec, q);
            return {E_vec, F_vec, E_mag};
        }

        function draw() {
            background(255);
            translate(width/2, height/2);
            scale(1, -1);

            // Draw grid
            stroke(200);
            strokeWeight(1);
            for(let i=-7; i<=7; i+=2) {
                line(i*scale, -height/2, i*scale, height/2);
                line(-width/2, i*scale, width/2, i*scale);
            }

            // Draw axes
            stroke(0);
            line(-width/2, 0, width/2, 0);
            line(0, -height/2, 0, height/2);

            // Source charge
            fill(0, 0, 255);
            noStroke();
            circle(0, 0, 10);

            // Test charge
            fill(0);
            circle(x*scale, y*scale, 8);

            // Calculate and draw vectors
            const {E_vec, F_vec, E_mag} = calculateField();
            drawArrow(createVector(x, y), E_vec, color(255, 0, 0));
            if(q !== 0) {
                drawArrow(createVector(x, y), F_vec, color(0, 0, 255));
            }

            // Update info panel
            document.getElementById('display-E-mag').innerText = `|E| = ${E_mag.toFixed(3)} N/C`;
            document.getElementById('display-Ex').innerHTML = `E<sub>x</sub> component = ${E_vec.x.toFixed(3)} N/C`;
            document.getElementById('display-Ey').innerHTML = `E<sub>y</sub> component = ${E_vec.y.toFixed(3)} N/C`;
            document.getElementById('display-x-pos').innerText = `x position = ${x.toFixed(2)} m`;
            document.getElementById('display-y-pos').innerText = `y position = ${y.toFixed(2)} m`;
            document.getElementById('display-force').innerText = `Electric force = ${F_vec.mag().toFixed(3)} N`;
        }

        function mousePressed() {
            const dx = (mouseX - width/2)/scale - x;
            const dy = -(mouseY - height/2)/scale - y;
            if(dx*dx + dy*dy < 0.25) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if(isDragging) {
                x = (mouseX - width/2)/scale;
                y = -(mouseY - height/2)/scale;
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        // Slider event listeners
        document.getElementById('slider-source-charge').addEventListener('input', function(e) {
            Q = parseFloat(e.target.value);
            document.getElementById('label-source-charge').innerText = Q.toFixed(1);
        });

        document.getElementById('slider-test-charge').addEventListener('input', function(e) {
            q = parseFloat(e.target.value);
            document.getElementById('label-test-charge').innerText = q.toFixed(4);
        });
    </script>
</body>
</html>
