<!DOCTYPE html>
<html>
<head>
    <title>Fourier Transform of Radially Symmetric Potential Functions</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 20px;
        }
        #controls-container {
            display: flex;
            flex-direction: column;
            gap: 15px;
            margin-bottom: 20px;
            padding: 10px;
            background: #f5f5f5;
        }
        #function-selector, #scale-selector, #sliders-container {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        #plots-container {
            display: flex;
            border: 1px solid black;
            height: 500px;
        }
        #plot-left, #plot-right {
            flex: 1;
            height: 100%;
        }
        button {
            padding: 5px 10px;
            cursor: pointer;
        }
        button.active {
            background: #666;
            color: white;
        }
        #hover-info {
            color: #666;
            font-style: italic;
        }
        input[type="range"] {
            width: 200px;
        }
    </style>
</head>
<body>
    <h1>Fourier Transform of Radially Symmetric Potential Functions</h1>

    <div id="controls-container">
        <div id="function-selector">
            <input type="checkbox" id="checkbox-hertz" checked>
            <label for="checkbox-hertz">Hertz</label>
            <input type="checkbox" id="checkbox-overlap" checked>
            <label for="checkbox-overlap">overlap</label>
            <input type="checkbox" id="checkbox-gaussian" checked>
            <label for="checkbox-gaussian">Gaussian</label>
        </div>

        <div id="scale-selector">
            <label>axes</label>
            <button id="btn-linear-linear" class="active">linear-linear</button>
            <button id="btn-log-linear">log-linear</button>
            <button id="btn-log-log">log-log</button>
        </div>

        <div id="sliders-container">
            <div id="slider-r-control">
                <label for="slider-r">radius r</label>
                <input type="range" id="slider-r" min="0.1" max="5" step="0.1" value="2.5">
                <span id="span-r-value">2.5</span>
            </div>
            <div id="slider-k-control">
                <label for="slider-k">wave number k</label>
                <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50">
                <span id="span-k-value">50</span>
            </div>
        </div>
    </div>

    <p id="hover-info">(Hover over the curves to see math labels.)</p>

    <div id="plots-container">
        <div id="plot-left"></div>
        <div id="plot-right"></div>
    </div>

    <script>
        function calculateHertz(r) {
            return r <= 1 ? Math.pow(1 - r*r, 2.5) : 0;
        }

        function calculateOverlap(r) {
            return r <= 1 ? Math.pow(1 - r, 2) : 0;
        }

        function calculateGaussian(r) {
            return Math.exp(-Math.PI * r*r);
        }

        function calculateHertzFT(k) {
            if (k === 0) return 15 * Math.PI / 15;
            return 15 * Math.PI * ((3 - k*k) * Math.sin(k) - 3 * k * Math.cos(k)) / Math.pow(k, 5);
        }

        function calculateOverlapFT(k) {
            if (k === 0) return 8 * Math.PI / 3;
            return 8 * Math.PI * (2 - k*k - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / Math.pow(k, 4);
        }

        function calculateGaussianFT(k) {
            return (1/Math.sqrt(Math.PI)) * Math.exp(-k*k / (4*Math.PI));
        }

        function updatePlots() {
            const rMax = parseFloat(document.getElementById('slider-r').value);
            const kMax = parseFloat(document.getElementById('slider-k').value);

            document.getElementById('span-r-value').textContent = rMax.toFixed(1);
            document.getElementById('span-k-value').textContent = kMax.toFixed(2);

            const showHertz = document.getElementById('checkbox-hertz').checked;
            const showOverlap = document.getElementById('checkbox-overlap').checked;
            const showGaussian = document.getElementById('checkbox-gaussian').checked;

            const rValues = Array.from({length: 200}, (_, i) => i * rMax / 199);
            const kValues = Array.from({length: 200}, (_, i) => i * kMax / 199);

            const traces_left = [];
            const traces_right = [];

            if (showHertz) {
                traces_left.push({
                    x: rValues,
                    y: rValues.map(r => calculateHertz(r)),
                    name: 'Hertz',
                    line: {color: '#ff0000'},
                    hovertemplate: 'Hertz<br>f(r) = (1 - r^2)^{5/2}, r \\le 1$<extra></extra>'
                });
                traces_right.push({
                    x: kValues,
                    y: kValues.map(k => calculateHertzFT(k)),
                    name: 'Hertz',
                    line: {color: '#ff0000'},
                    hovertemplate: 'Hertz<br>F(k) = 15\\pi \\frac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}$<extra></extra>'
                });
            }

            if (showOverlap) {
                traces_left.push({
                    x: rValues,
                    y: rValues.map(r => calculateOverlap(r)),
                    name: 'Overlap',
                    line: {color: '#000000'},
                    hovertemplate: 'Overlap<br>f(r) = (1 - r)^2$<extra></extra>'
                });
                traces_right.push({
                    x: kValues,
                    y: kValues.map(k => calculateOverlapFT(k)),
                    name: 'Overlap',
                    line: {color: '#000000'},
                    hovertemplate: 'Overlap<br>F(k) = 8\\pi \\frac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}$<extra></extra>'
                });
            }

            if (showGaussian) {
                traces_left.push({
                    x: rValues,
                    y: rValues.map(r => calculateGaussian(r)),
                    name: 'Gaussian',
                    line: {color: '#0000ff'},
                    hovertemplate: 'Gaussian<br>f(r) = e^{-\\pi r^2}$<extra></extra>'
                });
                traces_right.push({
                    x: kValues,
                    y: kValues.map(k => calculateGaussianFT(k)),
                    name: 'Gaussian',
                    line: {color: '#0000ff'},
                    hovertemplate: 'Gaussian<br>F(k) = \\frac{1}{\\sqrt{\\pi}} e^{-k^2 / (4\\pi)}$<extra></extra>'
                });
            }

            const yAxisType = document.getElementById('btn-log-log').classList.contains('active') ? 'log' :
                            document.getElementById('btn-log-linear').classList.contains('active') ? 'log' : 'linear';

            Plotly.react('plot-left', traces_left, {
                title: {text: 'function', font: {color: '#666'}},
                xaxis: {title: 'r', range: [0, rMax]},
                yaxis: {type: 'linear'},
                showlegend: true
            });

            Plotly.react('plot-right', traces_right, {
                title: {text: '3D Fourier transform (real part)', font: {color: '#666'}},
                xaxis: {title: 'k', range: [0, kMax]},
                yaxis: {type: yAxisType},
                showlegend: true
            });

            if (window.MathJax) MathJax.typeset();
        }

        // Event listeners
        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', updatePlots);
        });

        document.querySelectorAll('#scale-selector button').forEach(button => {
            button.addEventListener('click', (e) => {
                document.querySelectorAll('#scale-selector button').forEach(b => 
                    b.classList.remove('active'));
                e.target.classList.add('active');
                updatePlots();
            });
        });

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', updatePlots);
        });

        // Initialize plots
        window.addEventListener('load', updatePlots);
    </script>
</body>
</html>
