            width: 300px;
        }

        .slider-row span {
            width: 50px;
            text-align: right;
        }

        #visualization-container {
            display: flex;
            justify-content: center;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>relative volatility</h3>
            <button id="btn-reset">+</button>
            <div class="slider-row">
                <label for="slider-ac">between A and C</label>
                <input type="range" id="slider-ac" min="0.1" max="5.0" step="0.01" value="0.6">
                <span id="value-ac">0.60</span>
            </div>
            <div class="slider-row">
                <label for="slider-bc">between B and C</label>
                <input type="range" id="slider-bc" min="0.1" max="5.0" step="0.01" value="3.93">
                <span id="value-bc">3.93</span>
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="ternary-plot-canvas"></canvas>
        </div>
    </div>

    <script>
        let sketch = function(p) {
            const width = 600;
            const height = 550;
            const padding = 50;
            const N = 10; // Grid divisions

            let alpha13 = 0.6;
            let alpha23 = 3.93;

            function baryToCart(x1, x2, x3) {
                const h = Math.sqrt(3) / 2;
                const px = width/2 + (x2 - x1) * (width-2*padding)/2;
                const py = height - padding - (x1 + x2) * h * (height-2*padding)/2;
                return {x: px, y: py};
            }

            function calculateVaporFractions(x1, x2, x3) {
                const D = alpha13*x1 + alpha23*x2 + x3;
                return {
                    y1: alpha13*x1/D,
                    y2: alpha23*x2/D,
                    y3: x3/D
                };
            }

            function interpolateColor(v) {
                const colors = [
                    {pos: 0.0, color: [44, 123, 182]},
                    {pos: 0.25, color: [171, 217, 233]},
                    {pos: 0.5, color: [255, 255, 191]},
                    {pos: 0.75, color: [253, 174, 97]},
                    {pos: 1.0, color: [215, 25, 28]}
                ];

                for(let i = 0; i < colors.length-1; i++) {
                    if(v >= colors[i].pos && v <= colors[i+1].pos) {
                        const t = (v - colors[i].pos)/(colors[i+1].pos - colors[i].pos);
                        const r = p.lerp(colors[i].color[0], colors[i+1].color[0], t);
                        const g = p.lerp(colors[i].color[1], colors[i+1].color[1], t);
                        const b = p.lerp(colors[i].color[2], colors[i+1].color[2], t);
                        return p.color(r, g, b);
                    }
                }
                return p.color(0);
            }

            p.setup = function() {
                p.createCanvas(width, height);
                redraw();
            };

            function redraw() {
                p.background(255);

                // Draw grid
                for(let i = 0; i <= N; i++) {
                    for(let j = 0; j <= N-i; j++) {
                        const x1 = i/N;
                        const x2 = j/N;
                        const x3 = 1 - x1 - x2;

                        if(x3 < 0) continue;

                        // Calculate vapor fractions
                        const vapor = calculateVaporFractions(x1, x2, x3);

                        // Calculate difference function
                        const F = Math.pow(x1-vapor.y1, 2) + 
                                Math.pow(x2-vapor.y2, 2) + 
                                Math.pow(x3-vapor.y3, 2);

                        const v = F/2.0; // Normalize
                        p.fill(interpolateColor(v));
                        p.stroke(0);
                        p.strokeWeight(0.5);

                        // Draw triangle
                        const p1 = baryToCart(x1, x2, x3);
                        const p2 = baryToCart(x1+1/N, x2, x3-1/N);
                        const p3 = baryToCart(x1, x2+1/N, x3-1/N);

                        p.triangle(p1.x, p1.y, p2.x, p2.y, p3.x, p3.y);
                    }
                }

                // Draw labels
                p.textSize(16);
                p.noStroke();
                p.fill(0);
                const A = baryToCart(1, 0, 0);
                const B = baryToCart(0, 1, 0);
                const C = baryToCart(0, 0, 1);
                p.text('A', A.x, A.y-10);
                p.text('B', B.x+10, B.y);
                p.text('C', C.x, C.y+20);
            }

            p.draw = function() {
                // p5.js requires draw function but we only redraw on events
            };

            window.updatePlot = function(newAlpha13, newAlpha23) {
                alpha13 = newAlpha13;
                alpha23 = newAlpha23;
                redraw();
            };
        };

        new p5(sketch);

        // Event handlers
        document.getElementById('slider-ac').addEventListener('input', function(e) {
            const value = parseFloat(e.target.value);
            document.getElementById('value-ac').textContent = value.toFixed(2);
            window.updatePlot(value, alpha23);
        });

        document.getElementById('slider-bc').addEventListener('input', function(e) {
            const value = parseFloat(e.target.value);
            document.getElementById('value-bc').textContent = value.toFixed(2);
            window.updatePlot(alpha13, value);
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            const sliderAC = document.getElementById('slider-ac');
            const sliderBC = document.getElementById('slider-bc');
            sliderAC.value = 0.6;
            sliderBC.value = 3.93;
            document.getElementById('value-ac').textContent = "0.60";
            document.getElementById('value-bc').textContent = "3.93";
            window.updatePlot(0.6, 3.93);
        });
    </script>
</body>
</html>
