<!DOCTYPE html>
<html>
<head>
    <title>Band Structure Demo</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        #main-container {
            display: flex;
            padding: 20px;
            gap: 30px;
        }
        #control-panel {
            flex: 0 0 300px;
            padding: 20px;
            border: 1px solid #ccc;
        }
        #plot-container {
            flex: 1;
            min-width: 600px;
            border: 1px solid #ccc;
        }
        label {
            display: block;
            margin-top: 15px;
        }
        input[type="range"] {
            width: 100%;
            margin: 10px 0;
        }
        .value-display {
            color: #666;
            font-family: monospace;
        }
        hr {
            margin: 20px 0;
        }
    </style>
</head>
<body>
    <h1>Band Structure of a Quantum Wire with Rashba and Zeeman Interactions</h1>

    <div id="main-container">
        <div id="control-panel">
            <label for="slider-wso">Rashba spin-orbit interaction strength (wso)</label>
            <input type="range" id="slider-wso" min="0" max="2" step="0.1" value="0">
            <span id="value-wso" class="value-display">0.0</span>

            <hr>

            <h2>Magnetic Field</h2>
            <label for="slider-n">Subband (n)</label>
            <input type="range" id="slider-n" min="0" max="10" step="1" value="0">
            <span id="value-n" class="value-display">0</span>

            <h3>Strengths</h3>
            <label for="slider-bx">x direction (BX)</label>
            <input type="range" id="slider-bx" min="0" max="2" step="0.1" value="0">
            <span id="value-bx" class="value-display">0.0</span>

            <label for="slider-by">y direction (BY)</label>
            <input type="range" id="slider-by" min="0" max="2" step="0.1" value="0">
            <span id="value-by" class="value-display">0.0</span>

            <label for="slider-bz">z direction (BZ)</label>
            <input type="range" id="slider-bz" min="0" max="2" step="0.1" value="0">
            <span id="value-bz" class="value-display">0.0</span>
        </div>

        <div id="plot-container"></div>
    </div>

    <script>
        function updateSliderValue(sliderId, valueId) {
            const slider = document.getElementById(sliderId);
            const valueDisplay = document.getElementById(valueId);
            const value = parseFloat(slider.value);
            valueDisplay.textContent = Number.isInteger(value) ? value : value.toFixed(1);
        }

        function calculateEnergies(k, n, wso, BX, BY, BZ) {
            const baseEnergy = 2 * n + 1 + k * k;
            const term = Math.sqrt((BZ + wso * k) ** 2 + BX ** 2 + BY ** 2);
            return [baseEnergy + term, baseEnergy - term];
        }

        function generateTitle() {
            const n = parseFloat(document.getElementById('slider-n').value);
            const wso = parseFloat(document.getElementById('slider-wso').value);
            const BX = parseFloat(document.getElementById('slider-bx').value);
            const BY = parseFloat(document.getElementById('slider-by').value);
            const BZ = parseFloat(document.getElementById('slider-bz').value);

            let title = `n = ${n}`;
            if (wso > 0) title += `, wso = ${wso.toFixed(1)}`;
            if (BX > 0) title += `, BX = ${BX.toFixed(1)}`;
            if (BY > 0) title += `, BY = ${BY.toFixed(1)}`;
            if (BZ > 0) title += `, BZ = ${BZ.toFixed(1)}`;
            return title;
        }

        function updatePlot() {
            const n = parseFloat(document.getElementById('slider-n').value);
            const wso = parseFloat(document.getElementById('slider-wso').value);
            const BX = parseFloat(document.getElementById('slider-bx').value);
            const BY = parseFloat(document.getElementById('slider-by').value);
            const BZ = parseFloat(document.getElementById('slider-bz').value);

            const k = Array.from({length: 101}, (_, i) => -5 + i * 0.1);
            const energies = k.map(k_val => calculateEnergies(k_val, n, wso, BX, BY, BZ));

            const E_plus = energies.map(e => e[0]);
            const E_minus = energies.map(e => e[1]);

            const traces = [];

            if (wso === 0 && BX === 0 && BY === 0 && BZ === 0) {
                traces.push({
                    x: k,
                    y: E_plus,
                    mode: 'lines',
                    line: {color: 'red', width: 2}
                });
            } else {
                traces.push({
                    x: k,
                    y: E_plus,
                    mode: 'lines',
                    line: {color: 'red', width: 2}
                }, {
                    x: k,
                    y: E_minus,
                    mode: 'lines',
                    line: {color: 'red', width: 2, dash: 'dash'}
                });
            }

            const layout = {
                title: generateTitle(),
                xaxis: {
                    title: 'normalized k length (lwky)',
                    range: [-5, 5],
                    zeroline: true
                },
                yaxis: {
                    title: '2 E / &#x210F;ω',
                    range: [0, 40],
                    zeroline: true
                }
            };

            Plotly.react('plot-container', traces, layout);
        }

        // Add event listeners to all sliders
        const sliders = ['wso', 'n', 'bx', 'by', 'bz'];
        sliders.forEach(slider => {
            document.getElementById(`slider-${slider}`).addEventListener('input', () => {
                updateSliderValue(`slider-${slider}`, `value-${slider}`);
                updatePlot();
            });
        });

        // Initialize plot
        updatePlot();
    </script>
</body>
</html>
