                    <label>Hour 1:</label>
                    <input type="range" id="slider-precip-1" min="0" max="10" step="1" value="1">
                    <span id="value-precip-1">1</span>
                </div>
                <div class="slider-row">
                    <label>Hour 2:</label>
                    <input type="range" id="slider-precip-2" min="0" max="10" step="1" value="0">
                    <span id="value-precip-2">0</span>
                </div>
                <div class="slider-row">
                    <label>Hour 3:</label>
                    <input type="range" id="slider-precip-3" min="0" max="10" step="1" value="0">
                    <span id="value-precip-3">0</span>
                </div>

                <h4>UH Streamflow Rates</h4>
                <div class="slider-row">
                    <label>Hour 1:</label>
                    <input type="range" id="slider-uh-1" min="0" max="10" step="1" value="1">
                    <span id="value-uh-1">1</span>
                </div>
                <div class="slider-row">
                    <label>Hour 2:</label>
                    <input type="range" id="slider-uh-2" min="0" max="10" step="1" value="5">
                    <span id="value-uh-2">5</span>
                </div>
                <div class="slider-row">
                    <label>Hour 3:</label>
                    <input type="range" id="slider-uh-3" min="0" max="10" step="1" value="3">
                    <span id="value-uh-3">3</span>
                </div>
                <div class="slider-row">
                    <label>Hour 4:</label>
                    <input type="range" id="slider-uh-4" min="0" max="10" step="1" value="1">
                    <span id="value-uh-4">1</span>
                </div>
            </div>
        </div>

        <div id="plots-container">
            <div id="plot-canvas-1" class="plot-canvas"></div>
            <div id="plot-canvas-2" class="plot-canvas"></div>
            <div id="plot-canvas-3" class="plot-canvas"></div>
            <div id="plot-canvas-4" class="plot-canvas"></div>
            <div id="plot-canvas-5" class="plot-canvas"></div>
            <div id="plot-canvas-6" class="plot-canvas"></div>
        </div>
    </div>

    <script>
        let sketches = [];
        let P = [1, 0, 0];
        let U = [1, 5, 3, 1];

        function calculateHydrographs() {
            // Calculate component hydrographs
            const C1 = U.map(u => P[0] * u);
            const C2 = U.map(u => P[1] * u);
            const C3 = U.map(u => P[2] * u);

            // Calculate composite hydrograph
            const Q = new Array(6).fill(0);
            Q[0] = P[0] * U[0];
            Q[1] = P[0] * U[1] + P[1] * U[0];
            Q[2] = P[0] * U[2] + P[1] * U[1] + P[2] * U[0];
            Q[3] = P[0] * U[3] + P[1] * U[2] + P[2] * U[1];
            Q[4] = P[1] * U[3] + P[2] * U[2];
            Q[5] = P[2] * U[3];

            return { C1, C2, C3, Q };
        }

        function createDrawPlot(container, data, xMax, yMax, xLabel, yLabel, timeOffset = 0) {
            return function(p) {
                p.setup = function() {
                    const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                    canvas.parent(container);
                };

                p.draw = function() {
                    p.background(255);
                    p.stroke(0);
                    p.strokeWeight(1);

                    const margin = 40;
                    const plotWidth = p.width - 2 * margin;
                    const plotHeight = p.height - 2 * margin;

                    // Draw axes
                    p.line(margin, p.height - margin, p.width - margin, p.height - margin);
                    p.line(margin, margin, margin, p.height - margin);

                    // Draw data
                    p.stroke('#2e86c1');
                    p.fill('#d4e6f1');
                    p.beginShape();
                    p.vertex(margin, p.height - margin);

                    for(let i = 0; i < data.length; i++) {
                        const x = margin + ((i + timeOffset) / xMax) * plotWidth;
                        const y = p.height - margin - (data[i] / yMax) * plotHeight;

                        if(i > 0) {
                            const prevX = margin + ((i - 1 + timeOffset) / xMax) * plotWidth;
                            p.vertex(x, p.height - margin - (data[i-1] / yMax) * plotHeight);
                        }
                        p.vertex(x, y);
                    }
                    p.vertex(p.width - margin, p.height - margin);
                    p.endShape(p.CLOSE);

                    // Draw labels
                    p.textAlign(p.CENTER);
                    p.text(xLabel, p.width/2, p.height - 10);
                    p.push();
                    p.translate(15, p.height/2);
                    p.rotate(-p.HALF_PI);
                    p.text(yLabel, 0, 0);
                    p.pop();
                };
            };
        }

        function update() {
            P = [
                Number(document.getElementById('slider-precip-1').value),
                Number(document.getElementById('slider-precip-2').value),
                Number(document.getElementById('slider-precip-3').value)
            ];

            U = [
                Number(document.getElementById('slider-uh-1').value),
                Number(document.getElementById('slider-uh-2').value),
                Number(document.getElementById('slider-uh-3').value),
                Number(document.getElementById('slider-uh-4').value)
            ];

            const { C1, C2, C3, Q } = calculateHydrographs();

            // Remove existing sketches
            sketches.forEach(s => s.remove());
            sketches = [];

            // Create new sketches
            sketches.push(new p5(createDrawPlot(document.getElementById('plot-canvas-1'), P, 5, 10, 't (hr)', 'P (in)')));
            sketches.push(new p5(createDrawPlot(document.getElementById('plot-canvas-2'), U, 5, 10, 't (hr)', 'Q (cfs/in)')));
            sketches.push(new p5(createDrawPlot(document.getElementById('plot-canvas-3'), C1, 8, 100, 't (hr)', 'Q (cfs)')));
            sketches.push(new p5(createDrawPlot(document.getElementById('plot-canvas-4'), C2, 8, 100, 't (hr)', 'Q (cfs)', 1)));
            sketches.push(new p5(createDrawPlot(document.getElementById('plot-canvas-5'), C3, 8, 100, 't (hr)', 'Q (cfs)', 2)));
            sketches.push(new p5(createDrawPlot(document.getElementById('plot-canvas-6'), Q, 8, 100, 't (hr)', 'Q (cfs)')));
        }

        // Set up event listeners
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', function() {
                document.getElementById(this.id.replace('slider-', 'value-')).textContent = this.value;
                update();
            });
        });

        document.getElementById('toggle-controls').addEventListener('click', function() {
            const slidersArea = document.getElementById('sliders-area');
            slidersArea.style.display = slidersArea.style.display === 'none' ? 'block' : 'none';
            this.textContent = slidersArea.style.display === 'none' ? '+' : '-';
        });

        // Initial update
        update();
    </script>
</body>
</html>
