    <script>
        let gameState = {
            a11: -5, a12: -3, a21: -1, a22: -4,
            b11: -5, b12: -3, b21: -1, b22: -4
        };

        function setup() {
            const canvas = createCanvas(400, 400);
            canvas.parent('canvas-container');
            setupControls();
            updateAll();
        }

        function draw() {
            background(255);

            // Transform coordinates
            translate(40, height - 40);
            scale(320, -320);

            // Draw axes
            stroke(0);
            strokeWeight(1/320);
            line(0, 0, 1.1, 0);
            line(0, 0, 0, 1.1);

            // Draw best response curves
            drawPlayer1Response();
            drawPlayer2Response();
            drawEquilibria();
        }

        function drawPlayer1Response() {
            const den1 = gameState.a11 - gameState.a12 - gameState.a21 + gameState.a22;
            const y1_indiff = (gameState.a22 - gameState.a12) / den1;

            stroke(0, 0, 255);
            strokeWeight(2/320);

            if (Math.abs(den1) < 1e-6) {
                // Dominant strategy case
                if (gameState.a12 - gameState.a22 > 0) {
                    line(1, 0, 1, 1);
                } else {
                    line(0, 0, 0, 1);
                }
            } else if (y1_indiff >= 0 && y1_indiff <= 1) {
                line(0, y1_indiff, 1, y1_indiff);
                if (den1 > 0) {
                    line(0, 0, 0, y1_indiff);
                    line(1, y1_indiff, 1, 1);
                } else {
                    line(0, y1_indiff, 0, 1);
                    line(1, 0, 1, y1_indiff);
                }
            } else {
                // Dominant strategy in mixed extension
                const test = gameState.a11 * 0.5 + gameState.a12 * 0.5 >
                           gameState.a21 * 0.5 + gameState.a22 * 0.5;
                line(test ? 1 : 0, 0, test ? 1 : 0, 1);
            }
        }

        function drawPlayer2Response() {
            const den2 = gameState.b11 - gameState.b12 - gameState.b21 + gameState.b22;
            const x1_indiff = (gameState.b22 - gameState.b21) / den2;

            stroke(0, 255, 0);
            strokeWeight(2/320);

            if (Math.abs(den2) < 1e-6) {
                if (gameState.b21 - gameState.b22 > 0) {
                    line(0, 1, 1, 1);
                } else {
                    line(0, 0, 1, 0);
                }
            } else if (x1_indiff >= 0 && x1_indiff <= 1) {
                line(x1_indiff, 0, x1_indiff, 1);
                if (den2 > 0) {
                    line(0, 0, x1_indiff, 0);
                    line(x1_indiff, 1, 1, 1);
                } else {
                    line(x1_indiff, 0, 1, 0);
                    line(0, 1, x1_indiff, 1);
                }
            } else {
                const test = gameState.b11 * 0.5 + gameState.b21 * 0.5 >
                           gameState.b12 * 0.5 + gameState.b22 * 0.5;
                line(0, test ? 1 : 0, 1, test ? 1 : 0);
            }
        }

        function drawEquilibria() {
            // Simplified equilibria visualization
            stroke(255, 0, 0);
            strokeWeight(4/320);
            fill(255, 0, 0);

            const den1 = gameState.a11 - gameState.a12 - gameState.a21 + gameState.a22;
            const den2 = gameState.b11 - gameState.b12 - gameState.b21 + gameState.b22;
            const y1_indiff = (gameState.a22 - gameState.a12) / den1;
            const x1_indiff = (gameState.b22 - gameState.b21) / den2;

            if (isFinite(x1_indiff) && isFinite(y1_indiff) &&
                x1_indiff >= 0 && x1_indiff <= 1 &&
                y1_indiff >= 0 && y1_indiff <= 1) {
                circle(x1_indiff, y1_indiff, 8/320);
            }
        }

        function setupControls() {
            // Setup all slider controls
            const sliders = ['a11', 'a12', 'a21', 'a22', 'b11', 'b12', 'b21', 'b22'];
            sliders.forEach(id => {
                const slider = document.getElementById(`slider-${id}`);
                const value = document.getElementById(`value-${id}`);
                const btnMinus = document.getElementById(`btn-minus-${id}`);
                const btnPlus = document.getElementById(`btn-plus-${id}`);

                slider.oninput = () => {
                    gameState[id] = parseInt(slider.value);
                    value.textContent = slider.value;
                    updateAll();
                };

                btnMinus.onclick = () => {
                    if (slider.value > slider.min) {
                        slider.value = parseInt(slider.value) - 1;
                        gameState[id] = parseInt(slider.value);
                        value.textContent = slider.value;
                        updateAll();
                    }
                };

                btnPlus.onclick = () => {
                    if (slider.value < slider.max) {
                        slider.value = parseInt(slider.value) + 1;
                        gameState[id] = parseInt(slider.value);
                        value.textContent = slider.value;
                        updateAll();
                    }
                };
            });
        }

        function updateAll() {
            updatePayoffTable();
            updateEquilibriaDisplay();
            redraw();
        }

        function updatePayoffTable() {
            document.getElementById('cell-11').textContent = `${gameState.a11}, ${gameState.b11}`;
            document.getElementById('cell-12').textContent = `${gameState.a12}, ${gameState.b12}`;
            document.getElementById('cell-21').textContent = `${gameState.a21}, ${gameState.b21}`;
            document.getElementById('cell-22').textContent = `${gameState.a22}, ${gameState.b22}`;
        }

        function updateEquilibriaDisplay() {
            const den1 = gameState.a11 - gameState.a12 - gameState.a21 + gameState.a22;
            const den2 = gameState.b11 - gameState.b12 - gameState.b21 + gameState.b22;
            const y1_indiff = (gameState.a22 - gameState.a12) / den1;
            const x1_indiff = (gameState.b22 - gameState.b21) / den2;

            let eqText = '\\( \\{ ';
            if (isFinite(x1_indiff) && isFinite(y1_indiff) &&
                x1_indiff >= 0 && x1_indiff <= 1 &&
                y1_indiff >= 0 && y1_indiff <= 1) {
                eqText += `(${x1_indiff.toFixed(3)}, ${y1_indiff.toFixed(3)})`;
            } else {
                eqText += '\\text{No pure strategy Nash equilibrium}';
            }
            eqText += ' \\} \\)';

            document.getElementById('equilibria-display').innerHTML = eqText;
            MathJax.typeset();
        }
    </script>
</body>
</html>
