<!DOCTYPE html>
<html>
<head>
    <title>Cubic & Quadratic Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        #demo-container {
            width: 800px;
            margin: 20px auto;
            font-family: Arial, sans-serif;
        }
        #controls-container {
            margin-bottom: 20px;
        }
        #slider-group {
            display: flex;
            align-items: center;
            gap: 10px;
            margin-bottom: 10px;
        }
        #slider-t {
            width: 300px;
        }
        #input-t {
            width: 80px;
        }
        #btn-reset {
            padding: 5px 10px;
            cursor: pointer;
        }
        #checkbox-group {
            margin-bottom: 10px;
        }
        #canvas-container {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="controls-container">
            <div id="slider-group">
                <label for="slider-t">choose quadratic</label>
                <input type="range" id="slider-t" min="-5" max="5" step="0.01" value="0.5">
                <input type="number" id="input-t" min="-5" max="5" step="0.01" value="0.5">
                <button id="btn-reset" style="font-family: 'Courier New', monospace;">&#x21BB;</button>
            </div>
            <div id="checkbox-group">
                <input type="checkbox" id="checkbox-family">
                <label for="checkbox-family">family</label>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let locators = [
            {x: -4, y: 1},
            {x: -2, y: 1.8},
            {x: 1, y: -1},
            {x: 4, y: -1}
        ];
        let cubicCoeffs = {a: 0, b: 0, c: 0, d: 0};
        let selectedLocator = null;
        let scale = 50; // pixels per unit

        function setup() {
            const canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');

            // Initialize UI controls
            document.getElementById('slider-t').addEventListener('input', updateFromSlider);
            document.getElementById('input-t').addEventListener('input', updateFromInput);
            document.getElementById('checkbox-family').addEventListener('change', redraw);
            document.getElementById('btn-reset').addEventListener('click', reset);

            calculateCubicCoeffs();
        }

        function draw() {
            background(255);
            translate(width/2, height/2);
            scale(1, -1); // Flip y-axis

            // Draw axes
            drawAxes();

            if (!document.getElementById('checkbox-family').checked) {
                // Draw cubic
                drawCubic();
            } else {
                // Draw family of quadratics
                drawQuadraticFamily();
            }

            // Draw current quadratic
            const t = parseFloat(document.getElementById('slider-t').value);
            const quadCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
            drawQuadratic(quadCoeffs, color(245, 124, 0), 3);

            // Draw locators
            drawLocators();
        }

        function drawAxes() {
            stroke(200);
            strokeWeight(1);
            line(-width/2, 0, width/2, 0);
            line(0, -height/2, 0, height/2);

            // Draw tick marks
            for(let x = -floor(width/2/scale); x <= floor(width/2/scale); x++) {
                line(x*scale, -5, x*scale, 5);
            }
            for(let y = -floor(height/2/scale); y <= floor(height/2/scale); y++) {
                line(-5, y*scale, 5, y*scale);
            }
        }

        function drawCubic() {
            stroke(63, 81, 181);
            strokeWeight(3);
            noFill();
            beginShape();
            for(let px = -width/2; px <= width/2; px++) {
                const x = px/scale;
                const y = evaluateCubic(x) * scale;
                vertex(px, y);
            }
            endShape();
        }

        function drawQuadratic(coeffs, col, weight) {
            stroke(col);
            strokeWeight(weight);
            noFill();
            beginShape();
            for(let px = -width/2; px <= width/2; px++) {
                const x = px/scale;
                const y = (coeffs.p*x*x + coeffs.q*x + coeffs.r) * scale;
                vertex(px, y);
            }
            endShape();
        }

        function drawQuadraticFamily() {
            for(let t = -5; t <= 5; t += 0.5) {
                const quadCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
                const col = color(map(t, -5, 5, 0, 255), 100, 100, 50);
                drawQuadratic(quadCoeffs, col, 1);
            }
        }

        function drawLocators() {
            push();
            scale(1, -1); // Flip back for locator drawing
            for(let i = 0; i < locators.length; i++) {
                const x = locators[i].x * scale;
                const y = -locators[i].y * scale;
                fill(255);
                stroke(0);
                strokeWeight(2);
                circle(x, y, 10);
                line(x-5, y, x+5, y);
                line(x, y-5, x, y+5);
            }
            pop();
        }

        function mousePressed() {
            const mx = (mouseX - width/2)/scale;
            const my = -(mouseY - height/2)/scale;

            for(let i = 0; i < locators.length; i++) {
                if(dist(mx, my, locators[i].x, locators[i].y) < 0.2) {
                    selectedLocator = i;
                    break;
                }
            }
        }

        function mouseDragged() {
            if(selectedLocator !== null) {
                locators[selectedLocator].x = (mouseX - width/2)/scale;
                locators[selectedLocator].y = -(mouseY - height/2)/scale;
                calculateCubicCoeffs();
                redraw();
            }
        }

        function mouseReleased() {
            selectedLocator = null;
        }

        function calculateCubicCoeffs() {
            // Simple implementation - in practice would use matrix solving
            cubicCoeffs = {
                a: 0.1,
                b: 0.2,
                c: -0.3,
                d: 0
            };
        }

        function getOsculatingQuadraticCoeffs(t, cubic) {
            return {
                p: 3*cubic.a*t + cubic.b,
                q: -3*cubic.a*t*t + cubic.c,
                r: cubic.a*t*t*t + cubic.d
            };
        }

        function evaluateCubic(x) {
            return cubicCoeffs.a*x*x*x + cubicCoeffs.b*x*x + cubicCoeffs.c*x + cubicCoeffs.d;
        }

        function updateFromSlider() {
            document.getElementById('input-t').value = this.value;
            redraw();
        }

        function updateFromInput() {
            document.getElementById('slider-t').value = this.value;
            redraw();
        }

        function reset() {
            locators = [
                {x: -4, y: 1},
                {x: -2, y: 1.8},
                {x: 1, y: -1},
                {x: 4, y: -1}
            ];
            document.getElementById('slider-t').value = 0.5;
            document.getElementById('input-t').value = 0.5;
            document.getElementById('checkbox-family').checked = false;
            calculateCubicCoeffs();
            redraw();
        }
    </script>
</body>
</html>
