    <script src="https://cdn.plot.ly/plotly-2.24.1.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.6.0/p5.js"></script>
    <style>
        #demo-container {
            max-width: 1000px;
            margin: 20px auto;
            font-family: Arial, sans-serif;
        }

        #control-panel {
            background: #f5f5f5;
            padding: 20px;
            margin-bottom: 20px;
            border-radius: 8px;
        }

        .slider-container {
            margin: 10px 0;
        }

