            display: inline-block;
            width: 150px;
        }

        input[type="range"] {
            width: 300px;
        }

        #visualization-area {
            display: flex;
            gap: 20px;
        }

        #plot-container, #piston-container {
            flex: 1;
            height: 400px;
        }

        .temp-units {
            margin-top: 15px;
        }

        .temp-units label {
            margin-right: 15px;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <div class="slider-container">
                <label for="slider-v0">initial volume v₀</label>
                <input type="range" id="slider-v0" min="100" max="500" value="211" step="1">
                <span id="v0-value">211</span>
            </div>

            <div class="slider-container">
                <label for="slider-T">temperature T</label>
                <input type="range" id="slider-T" min="-273" max="727" value="270" step="1">
                <span id="t-value">270°C</span>
            </div>

            <div class="temp-units">
                <label>temperature units</label>
                <input type="radio" id="radio-kelvin" name="temp-units" value="K">
                <label for="radio-kelvin">Kelvin</label>
                <input type="radio" id="radio-celsius" name="temp-units" value="C" checked>
                <label for="radio-celsius">Celsius</label>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-container"></div>
            <div id="piston-container"></div>
        </div>
    </div>

    <script>
        const T0 = 273.15; // Reference temperature in Kelvin
        let v0 = 211; // Initial volume
        let currentTemp = 270; // Initial temperature in Celsius
        let k = v0 / T0; // Proportionality constant

        // Initialize plot
        const plotData = {
            traces: [{
                x: [],
                y: [],
                mode: 'lines',
                name: 'V-T relationship',
                line: {color: '#1f77b4'}
            }, {
                x: [currentTemp],
                y: [getVolume(currentTemp + T0)],
                mode: 'markers',
                marker: {size: 12, color: 'red'},
                name: 'Current state'
            }]
        };

        Plotly.newPlot('plot-container', plotData.traces, {
            title: 'Charles\'s Law: V-T Relationship',
            xaxis: {title: 'Temperature (°C)'},
            yaxis: {title: 'Volume', range: [0, 2000]}
        });

        function getVolume(tempK) {
            return k * tempK;
        }

        function updatePlot() {
            const temps = [];
            const vols = [];
            const isCelsius = document.getElementById('radio-celsius').checked;
            const minTemp = isCelsius ? -273 : 0;
            const maxTemp = isCelsius ? 727 : 1000;

            for(let t = minTemp; t <= maxTemp; t++) {
                temps.push(t);
                vols.push(getVolume(isCelsius ? t + T0 : t));
            }

            Plotly.update('plot-container', {
                x: [temps, [isCelsius ? currentTemp : currentTemp + T0]],
                y: [vols, [getVolume(currentTemp + T0)]]
            });

            Plotly.relayout('plot-container', {
                'xaxis.title': `Temperature (${isCelsius ? '°C' : 'K'})`
            });
        }

        // P5.js sketch
        new p5(function(p) {
            p.setup = function() {
                p.createCanvas(400, 400);
            };

            p.draw = function() {
                p.background(255);
                const volume = getVolume(currentTemp + T0);
                const height = p.map(volume, 100, 2000, 350, 50);

                // Draw cylinder
                p.fill(200);
                p.rect(100, height, 200, 350-height); // Gas
                p.stroke(0);
                p.line(100, 50, 100, 350); // Left wall
                p.line(300, 50, 300, 350); // Right wall
                p.line(100, 350, 300, 350); // Base

                // Draw piston
                p.fill(150);
                p.rect(90, height-10, 220, 10); // Piston head
            };
        }, 'piston-container');

        // Event listeners
        document.getElementById('slider-v0').addEventListener('input', function(e) {
            v0 = parseInt(e.target.value);
            document.getElementById('v0-value').textContent = v0;
            k = v0 / T0;
            updatePlot();
        });

        document.getElementById('slider-T').addEventListener('input', function(e) {
            currentTemp = parseInt(e.target.value);
            const isCelsius = document.getElementById('radio-celsius').checked;
            document.getElementById('t-value').textContent = 
                currentTemp + (isCelsius ? '°C' : 'K');
            updatePlot();
        });

        document.getElementsByName('temp-units').forEach(radio => {
            radio.addEventListener('change', function() {
                const slider = document.getElementById('slider-T');
                if(this.value === 'K') {
                    currentTemp += T0;
                    slider.min = '0';
                    slider.max = '1000';
                } else {
                    currentTemp -= T0;
                    slider.min = '-273';
                    slider.max = '727';
                }
                slider.value = currentTemp;
                document.getElementById('t-value').textContent = 
                    currentTemp + (this.value === 'C' ? '°C' : 'K');
                updatePlot();
            });
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
