<!DOCTYPE html>
<html>
<head>
    <title>PDE Visualization</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        .control-group {
            margin: 20px 0;
        }
        button {
            width: 30px;
            margin: 0 5px;
        }
        input[type="range"] {
            width: 150px;
            vertical-align: middle;
        }
        .value-display {
            display: inline-block;
            width: 60px;
            text-align: right;
        }
    </style>
</head>
<body>
    <div id="main-container" style="display: flex; font-family: sans-serif;">
        <div id="control-panel" style="width: 250px; padding: 20px;">
            <div class="control-group">
                <label>τ:</label>
                <button id="btn-dec-tau">-</button>
                <input type="range" id="slider-tau" min="0.01" max="5" step="0.01" value="1.0">
                <button id="btn-inc-tau">+</button>
                <span id="value-tau" class="value-display">1.00</span>
            </div>

            <div class="control-group">
                <label>φ:</label>
                <button id="btn-dec-phi">-</button>
                <input type="range" id="slider-phi" min="0.1" max="10" step="0.1" value="2.0">
                <button id="btn-inc-phi">+</button>
                <span id="value-phi" class="value-display">2.0</span>
            </div>

            <div class="control-group">
                <label>β:</label>
                <button id="btn-dec-beta">-</button>
                <input type="range" id="slider-beta" min="-0.5" max="0.5" step="0.01" value="0.2">
                <button id="btn-inc-beta">+</button>
                <span id="value-beta" class="value-display">0.20</span>
            </div>

            <div class="control-group">
                <label>γ:</label>
                <button id="btn-dec-gamma">-</button>
                <input type="range" id="slider-gamma" min="0" max="40" step="1" value="20">
                <button id="btn-inc-gamma">+</button>
                <span id="value-gamma" class="value-display">20</span>
            </div>

            <div class="control-group">
                <label>ξ:</label>
                <button id="btn-dec-xi">-</button>
                <input type="range" id="slider-xi" min="0.001" max="1.0" step="0.001" value="0.4">
                <button id="btn-inc-xi">+</button>
                <span id="value-xi" class="value-display">0.400</span>
            </div>
        </div>

        <div id="visualization-panel" style="flex-grow: 1;">
            <div id="plot-concentration" style="height: 400px;"></div>
            <div id="plot-temperature" style="height: 400px;"></div>
        </div>
    </div>

    <script>
        // Initialize plots
        const N = 100; // Number of spatial grid points
        const dx = 1.0 / (N-1);
        const x = Array.from({length: N}, (_, i) => i * dx);

        // Initialize simulation state
        let c = new Array(N).fill(0);
        let theta = new Array(N).fill(1);

        function runSimulation() {
            const tau = parseFloat(document.getElementById('slider-tau').value);
            const phi = parseFloat(document.getElementById('slider-phi').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            const gamma = parseFloat(document.getElementById('slider-gamma').value);

            // Simple placeholder simulation (actual PDE solver would go here)
            for(let i = 0; i < N; i++) {
                const xi = i * dx;
                c[i] = Math.exp(-phi * xi) * (1 - Math.exp(-tau));
                theta[i] = 1 + beta * (1 - Math.exp(-phi * xi)) * (1 - Math.exp(-tau));
            }

            updatePlots();
        }

        function updatePlots() {
            const xi = parseFloat(document.getElementById('slider-xi').value);
            const tau = parseFloat(document.getElementById('slider-tau').value);

            // Interpolate values at selected xi
            const idx = Math.floor(xi * (N-1));
            const c_xi = c[idx];
            const theta_xi = theta[idx];

            // Create concentration plot
            const concentrationData = [{
                x: x,
                y: c,
                type: 'scatter',
                mode: 'lines',
                name: 'Profile'
            }, {
                x: [xi, xi, 0],
                y: [0, c_xi, c_xi],
                type: 'scatter',
                mode: 'lines',
                line: {color: '#333333'},
                showlegend: false
            }];

            const concentrationLayout = {
                xaxis: {title: 'ξ', range: [0, 1]},
                yaxis: {title: 'c', range: [0, 1.1]},
                margin: {t: 30, r: 50, b: 50, l: 50},
                annotations: [{
                    x: 0.95,
                    y: 0.5,
                    text: `τ = ${tau.toFixed(2)}<br>ξ = ${xi.toFixed(3)}<br>c = ${c_xi.toFixed(5)}`,
                    showarrow: false
                }]
            };

            // Create temperature plot
            const temperatureData = [{
                x: x,
                y: theta,
                type: 'scatter',
                mode: 'lines',
                name: 'Profile'
            }, {
                x: [xi, xi, 0],
                y: [Math.min(...theta), theta_xi, theta_xi],
                type: 'scatter',
                mode: 'lines',
                line: {color: '#333333'},
                showlegend: false
            }];

            const temperatureLayout = {
                xaxis: {title: 'ξ', range: [0, 1]},
                yaxis: {title: 'θ', autorange: true},
                margin: {t: 30, r: 50, b: 50, l: 50},
                annotations: [{
                    x: 0.95,
                    y: 0.5,
                    text: `τ = ${tau.toFixed(2)}<br>ξ = ${xi.toFixed(3)}<br>θ = ${theta_xi.toFixed(5)}`,
                    showarrow: false
                }]
            };

            Plotly.react('plot-concentration', concentrationData, concentrationLayout);
            Plotly.react('plot-temperature', temperatureData, temperatureLayout);
        }

        // Set up event listeners
        const sliders = ['tau', 'phi', 'beta', 'gamma', 'xi'];
        sliders.forEach(param => {
            const slider = document.getElementById(`slider-${param}`);
            const value = document.getElementById(`value-${param}`);
            const btnDec = document.getElementById(`btn-dec-${param}`);
            const btnInc = document.getElementById(`btn-inc-${param}`);

            slider.addEventListener('input', () => {
                value.textContent = parseFloat(slider.value).toFixed(param === 'gamma' ? 0 : 3);
                if(param === 'xi') {
                    updatePlots();
                } else {
                    runSimulation();
                }
            });

            btnDec.addEventListener('click', () => {
                slider.value = parseFloat(slider.value) - parseFloat(slider.step);
                slider.dispatchEvent(new Event('input'));
            });

            btnInc.addEventListener('click', () => {
                slider.value = parseFloat(slider.value) + parseFloat(slider.step);
                slider.dispatchEvent(new Event('input'));
            });
        });

        // Initial simulation and plot
        runSimulation();
    </script>
</body>
</html>
