        <div class="slider-container">
            <label>intrinsic growth rate \(r\)</label>
            <input type="range" id="slider-r" min="0.01" max="0.2" step="0.001" value="0.113">
            <span>+</span>
            <span id="value-r">0.113</span>
        </div>
        <div class="slider-container">
            <label>shape exponent \(\beta\)</label>
            <input type="range" id="slider-beta" min="0.1" max="1.5" step="0.001" value="0.477">
            <span>+</span>
            <span id="value-beta">0.477</span>
        </div>
    </div>

    <div id="visualization-area">
        <div class="flex-container">
            <div id="plot-container"></div>
            <div id="table-container">
                <table>
                    <thead>
                        <tr><th>Age</th><th>Volume</th></tr>
                    </thead>
                    <tbody>
                        <tr><td>30</td><td>289</td></tr>
                        <tr><td>35</td><td>445</td></tr>
                        <tr><td>40</td><td>565</td></tr>
                        <tr><td>45</td><td>704</td></tr>
                        <tr><td>50</td><td>833</td></tr>
                        <tr><td>55</td><td>924</td></tr>
                    </tbody>
                </table>
            </div>
        </div>
        <div id="results-display">
            <p>rejuvenation bias u = 0.0000</p>
            <p>site productivity p = 0.0000</p>
        </div>
    </div>

    <script>
        // Data points
        const empiricalData = {
            age: [30, 35, 40, 45, 50, 55],
            volume: [289, 445, 565, 704, 833, 924]
        };

        function richardsFunction(t, bMax, b0, r, beta) {
            const M = Math.pow(bMax/b0, beta) - 1;
            return bMax / Math.pow(1 + M * Math.exp(-r * t), 1/beta);
        }

        function calculateInflectionPoint(bMax, b0, r, beta) {
            const M = Math.pow(bMax/b0, beta) - 1;
            const t_i = Math.log(M * beta) / r;
            const V_i = bMax * Math.pow((beta + 1)/(2 * beta), 1/beta);
            const V_prime_i = (r/beta) * V_i * (1 - Math.pow(V_i/bMax, beta));
            return { t_i, V_i, V_prime_i };
        }

        function fourFifthsLaw(t, u, p) {
            return t > u ? p * Math.pow(t - u, 0.8) : null;
        }

        function update() {
            const bMax = parseFloat(document.getElementById('slider-bmax').value);
            const b0 = parseFloat(document.getElementById('slider-b0').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            // Update displayed values
            document.getElementById('value-bmax').textContent = bMax.toFixed(0);
            document.getElementById('value-b0').textContent = b0.toFixed(1);
            document.getElementById('value-r').textContent = r.toFixed(3);
            document.getElementById('value-beta').textContent = beta.toFixed(3);

            // Generate x values for curves
            const t = Array.from({length: 651}, (_, i) => i/10 + 15);

            // Calculate Richards curve
            const richardsY = t.map(x => richardsFunction(x, bMax, b0, r, beta));

            // Calculate 4/5 law parameters
            const { t_i, V_i, V_prime_i } = calculateInflectionPoint(bMax, b0, r, beta);
            const u = t_i - 0.8 * V_i / V_prime_i;
            const p = V_i / Math.pow(t_i - u, 0.8);

            // Calculate 4/5 law curve
            const lawY = t.map(x => fourFifthsLaw(x, u, p));

            // Update results display
            document.getElementById('results-display').innerHTML = `
                <p>rejuvenation bias u = ${u.toFixed(4)}</p>
                <p>site productivity p = ${p.toFixed(4)}</p>
            `;

            // Update plot
            const data = [
                {
                    x: empiricalData.age,
                    y: empiricalData.volume,
                    mode: 'markers',
                    type: 'scatter',
                    marker: { color: 'blue', size: 8 },
                    showlegend: false
                },
                {
                    x: t,
                    y: richardsY,
                    mode: 'lines',
                    type: 'scatter',
                    line: { color: 'orange', width: 2 },
                    showlegend: false
                },
                {
                    x: t,
                    y: lawY,
                    mode: 'lines',
                    type: 'scatter',
                    line: { color: 'blue', width: 2 },
                    showlegend: false
                }
            ];

            const layout = {
                title: 'forest growth curve',
                plot_bgcolor: '#FFFFF0',
                xaxis: {
                    title: 'stand age (years)',
                    range: [15, 80],
                    gridcolor: 'lightgrey',
                    gridwidth: 1,
                    zeroline: false
                },
                yaxis: {
                    title: {
                        text: 'wood volume, m³ ha⁻¹<br>Richards curve vs 4/5 law',
                        standoff: 40
                    },
                    range: [0, Math.max(bMax * 1.1, 1600)],
                    gridcolor: 'lightgrey',
                    gridwidth: 1,
                    zeroline: false
                },
                margin: { t: 40, r: 40, b: 60, l: 80 }
            };

            Plotly.react('plot-container', data, layout);
        }

        // Add event listeners to sliders
        document.getElementById('slider-bmax').addEventListener('input', update);
        document.getElementById('slider-b0').addEventListener('input', update);
        document.getElementById('slider-r').addEventListener('input', update);
        document.getElementById('slider-beta').addEventListener('input', update);

        // Initial update
        update();
    </script>
</body>
</html>
