<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="lattice-controls">
                <label>Lattice:</label>
                <button id="btn-sc" class="active">SC</button>
                <button id="btn-bcc">BCC</button>
                <button id="btn-fcc">FCC</button>
            </div>
            <div id="miller-controls">
                <label>Miller indices:</label>
                <label for="input-h">h</label>
                <input type="number" id="input-h" value="1" min="-10" max="10" step="1">
                <label for="input-k">k</label>
                <input type="number" id="input-k" value="1" min="-10" max="10" step="1">
                <label for="input-l">l</label>
                <input type="number" id="input-l" value="1" min="-10" max="10" step="1">
            </div>
            <div id="opacity-controls">
                <label for="slider-opacity">Plane opacity:</label>
                <input type="range" id="slider-opacity" min="0" max="1" step="0.01" value="0.8">
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script type="module">
        import * as THREE from 'https://cdn.jsdelivr.net/npm/three@0.150.0/build/three.module.js';
        import { OrbitControls } from 'https://cdn.jsdelivr.net/npm/three@0.150.0/examples/jsm/controls/OrbitControls.js';

        let scene, camera, renderer, controls;
        let atomsGroup, planesGroup;
        let currentLattice = 'SC';

        // Scene setup
        function init() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            camera = new THREE.PerspectiveCamera(75, window.innerWidth/window.innerHeight, 0.1, 1000);
            camera.position.set(2, 2, 2);

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(800, 500);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            controls = new OrbitControls(camera, renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(1, 2, 3);
            scene.add(directionalLight);

            // Groups
            atomsGroup = new THREE.Group();
            planesGroup = new THREE.Group();
            scene.add(atomsGroup);
            scene.add(planesGroup);

            // Unit cell wireframe
            const edges = new THREE.EdgesGeometry(new THREE.BoxGeometry(1, 1, 1));
            const line = new THREE.LineSegments(edges, new THREE.LineBasicMaterial({ color: 0x000000 }));
            scene.add(line);

            // Coordinate axes
            const axesHelper = new THREE.AxesHelper(1);
            scene.add(axesHelper);

            animate();
            updateScene();
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function updateScene() {
            // Clear existing objects
            while(atomsGroup.children.length > 0) {
                atomsGroup.remove(atomsGroup.children[0]);
            }
            while(planesGroup.children.length > 0) {
                planesGroup.remove(planesGroup.children[0]);
            }

            // Add atoms based on lattice type
            const atomGeometry = new THREE.SphereGeometry(0.05);
            const atomMaterial = new THREE.MeshPhongMaterial({
                color: 0x156289,
                specular: 0x00ffff
            });

            const addAtom = (x, y, z) => {
                const atom = new THREE.Mesh(atomGeometry, atomMaterial);
                atom.position.set(x, y, z);
                atomsGroup.add(atom);
            };

            // Corner atoms for all lattices
            const corners = [
                [-0.5, -0.5, -0.5], [-0.5, -0.5, 0.5],
                [-0.5, 0.5, -0.5], [-0.5, 0.5, 0.5],
                [0.5, -0.5, -0.5], [0.5, -0.5, 0.5],
                [0.5, 0.5, -0.5], [0.5, 0.5, 0.5]
            ];
            corners.forEach(pos => addAtom(...pos));

            if(currentLattice === 'BCC') {
                addAtom(0, 0, 0);
            }
            else if(currentLattice === 'FCC') {
                addAtom(0, 0, 0.5); addAtom(0, 0, -0.5);
                addAtom(0, 0.5, 0); addAtom(0, -0.5, 0);
                addAtom(0.5, 0, 0); addAtom(-0.5, 0, 0);
            }

            // Add Miller planes
            const h = parseInt(document.getElementById('input-h').value);
            const k = parseInt(document.getElementById('input-k').value);
            const l = parseInt(document.getElementById('input-l').value);

            if(!(h === 0 && k === 0 && l === 0)) {
                drawMillerPlanes(h, k, l);
            }
        }

        function drawMillerPlanes(h, k, l) {
            const opacity = parseFloat(document.getElementById('slider-opacity').value);
            const planeMaterial = new THREE.MeshBasicMaterial({
                color: 0x0066ff,
                transparent: true,
                opacity: opacity,
                side: THREE.DoubleSide
            });

            // Simplified plane generation - just for demonstration
            const planeGeometry = new THREE.PlaneGeometry(1, 1);
            const plane = new THREE.Mesh(planeGeometry, planeMaterial);

            // Rotate plane based on Miller indices
            plane.lookAt(new THREE.Vector3(h, k, l));

            planesGroup.add(plane);
        }

        // Event listeners
        document.querySelectorAll('#lattice-controls button').forEach(button => {
            button.addEventListener('click', (e) => {
                document.querySelectorAll('#lattice-controls button').forEach(b => 
                    b.classList.remove('active'));
                e.target.classList.add('active');
                currentLattice = e.target.id.split('-')[1].toUpperCase();
                updateScene();
            });
        });

        document.querySelectorAll('#miller-controls input').forEach(input => {
            input.addEventListener('change', updateScene);
        });

        document.getElementById('slider-opacity').addEventListener('input', (e) => {
            planesGroup.children.forEach(plane => {
                plane.material.opacity = e.target.value;
            });
        });

        // Initialize
        init();
    </script>
</body>
</html>
